"use strict";(self.webpackChunkdevyuseon_github_io=self.webpackChunkdevyuseon_github_io||[]).push([[6880],{8256:n=>{n.exports=JSON.parse('{"blogPosts":[{"id":"/2024/05/25/springcamp-2024","metadata":{"permalink":"/blog/2024/05/25/springcamp-2024","source":"@site/blog/2024-05-25-springcamp-2024.md","title":"SpringCamp 2024 \ud6c4\uae30 \ud83c\udf31","description":"\uc2a4\ud504\ub9c1\ucea0\ud504\uc5d0 \ub2e4\ub140\uc654\ub2e4 \ud83c\udf31","date":"2024-05-25T00:00:00.000Z","formattedDate":"May 25, 2024","tags":[],"readingTime":23.775,"hasTruncateMarker":true,"authors":[],"frontMatter":{"title":"SpringCamp 2024 \ud6c4\uae30 \ud83c\udf31","description":"\uc2a4\ud504\ub9c1\ucea0\ud504\uc5d0 \ub2e4\ub140\uc654\ub2e4 \ud83c\udf31","hide_table_of_contents":false,"toc_max_heading_level":4},"unlisted":false,"nextItem":{"title":"Java\uc5d0\uc11c new String()\uc73c\ub85c \uc778\ucf54\ub529\uc744 \ubcc0\ud658 \ud560 \uc218\ub294 \uc5c6\ub2e4.","permalink":"/blog/2024/05/22/java-new-string-encoding"}},"content":"[2024 \uc2a4\ud504\ub9c1\ucea0\ud504](https://springcamp.ksug.org/2024/)\uc5d0 \ub2e4\ub140\uc654\ub2e4!\\n\\n\x3c!-- truncate --\x3e\\n\\n## \uce58\uc5f4\ud588\ub358 \ud2f0\ucf13\ud305\\n\\n<div style={{width: \'60%\'}}>\\n\\n![\uacb0\uc81c\ucc3d](../static/img/post-img/IMG_5213.jpg)\\n\\n</div>\\n\\n\ud578\ub4dc\ud3f0\uc73c\ub85c \uc2dc\ub3c4\ud588\ub294\ub370 \ub2e4\ud589\ud788 \uc131\uacf5\ud588\ub2e4. 1\ubd84\ub3c4 \uc548\ub418\uc5b4 \ub9e4\uc9c4\ub418\uc5c8\ub2e4\uace0 \ud55c\ub2e4.\\n\\n## \ud604\uc7a5 \uc0ac\uc9c4 \ubc0f \uad7f\uc988 \uc218\ub839\\n\\n<div style={{width: \'60%\'}}>\\n\\n![\ud604\uc7a5](../static/img/post-img/IMG_5202.jpg)\\n\\n</div>\\n\\n![\uad7f\uc988](../static/img/post-img/IMG_5212.jpg)\\n\\n\ud604\ub300\uc790\ub3d9\ucc28, \uce74\uce74\uc624\ubc45\ud06c \ubd80\uc2a4\uac00 \uc788\uc5b4 \ube68\ub300&\uce6b\uc194 \uc138\ud2b8\uc640 \uc218\uac74\uc744 \ubc1b\uc558\ub2e4. \uc2a4\ud504\ub9c1\ucea0\ud504 \uc8fc\ucd5c\uce21\uc5d0\uc120 \uc190\ud48d\uae30\ub97c \uc8fc\uc168\ub2e4. \uc774\ubc88 \uc5ec\ub984\uc5d0 \uc548\uadf8\ub798\ub3c4 \uc0ac\ub824\uace0 \ud588\ub358 \ud130\ub77c \ub9c8\uc74c\uc5d0 \uc3d9 \ub4e4\uc5c8\uace0, \ubb34\uc5c7\ubcf4\ub2e4 \uac00\uc7a5 \uc88b\uc558\ub358\uac74 \ud0a4\ucea1..!!! \ud0a4\ubcf4\ub4dc \ub355\ud6c4\ub85c\uc11c \ud0a4\ucea1 \uad7f\uc988\ub77c\ub2c8 \uc774\uac74 \ucd5c\uace0\uc57c.. \uc2a4\ud2f0\ucee4\ub3c4 \uc57c\ubb34\uc9c0\uac8c \ucc59\uaca8\uc654\ub2e4 \u314e\u314e\\n\\n\ub2e4\ub4e4 \uc11c\ub85c \uc778\uc0ac\ud558\uace0 \ud558\uc2dc\ub358\ub370 \ub098\ub9cc \uce5c\uad6c\uac00 \uc5c6\uc5b4\uc11c \uc57d\uac04 \uc2ac\ud390\ub2e4.. 2024 \ud558\ubc18\uae30\uc5d0\ub294 \ucee4\ubba4\ub2c8\ud2f0 \ud65c\ub3d9\uc744 \ud560 \uc218 \uc788\ub294 \uae30\ud68c\uac00 \uc788\uc73c\uba74 \uc88b\uaca0\ub2e4. \uac1c\ubc1c\ubc14\ub2e5\uc774\ub098 \uc720\ucf8c\ud55c \uc2a4\ud504\ub9c1 \ubc29 \uac19\uc740 \uc624\ud508\ucc44\ud305\ub3c4 \ub4e4\uc5b4\uac00 \uc788\ub294\ub370, \uc778\uc2f8\ub825\uc744 \uc880 \ub354 \ud0a4\uc6b8 \ud544\uc694\uac00 \uc788\uaca0\ub2e4.\\n\\n## \ud0c0\uc784 \ud14c\uc774\ube14\\n\\n\\n| Track 1       |                                                                   |        |\\n| ------------- | ----------------------------------------------------------------- | ------ |\\n| 12:00 ~ 13:00 | \uc811\uc218 / \ub4f1\ub85d                                                       |        |\\n| 13:00 ~ 13:45 | Tidy First                                                        | \uc548\uc601\ud68c |\\n| 14:00 ~ 14:45 | Spring AI : LLM\uc5d0\ub3c4 \ubd04\uc774 \ucc3e\uc544\uc624\ub2e4.                                | \ud669\ubbfc\ud638 |\\n| 14:45 ~ 15:15 | Coffee Break                                                      |        |\\n| 15:15 ~ 16:00 | \uc65c \ub098\ub294 \ud14c\uc2a4\ud2b8\ub97c \uc791\uc131\ud558\uae30 \uc2eb\uc744\uae4c?                                 | \uc870\uc131\uc544 |\\n| 16:15 ~ 17:00 | \uc2e4\uc804! MSA \uac1c\ubc1c \uac00\uc774\ub4dc                                             | \uae40\uc6a9\uc6b1 |\\n| 17:15 ~ 18:00 | \uad6c\ud574\uc918 \ud648\uc988! \uc740\ud589\uc5d0\uc11c 3\ucc9c\ub9cc \ud2b8\ub798\ud53d\uc758 \ud648 \uc11c\ube44\uc2a4 \uc0c8\ub85c \ub9cc\ub4e4\uae30 | \uc774\uc601\uaddc |\\n\\n\\n| Track 2       |                                                                   |        |\\n| ------------- | ----------------------------------------------------------------- | ------ |\\n| 12:00 ~ 13:00 | \uc811\uc218 / \ub4f1\ub85d                                                       |        |\\n| 13:00 ~ 13:45 | \ub3d9\uc2dc\uc131\uc758 \ubbf8\ub798 - \ucf54\ub8e8\ud2f4\uc758 \ubc84\uce04\uc5bc \uc2a4\ub808\ub4dc                            | \uc774\uc0c1\ud6c8 |\\n| 14:00 ~ 14:45 | Spring Coroutine In Action                                        | \ucd5c\uc9c4\uc601 |\\n| 14:45 ~ 15:15 | Coffee Break                                                      |        |\\n| 15:15 ~ 16:00 | \uc2a4\ud504\ub9c1 R2DBC \uc5f0\ub3d9 \uae30\ub2a5\uc744 \ud65c\uc6a9\ud55c \uc791\uc740 \ucf54\ud2c0\ub9b0 SQL DSL \uac1c\ubc1c\uae30 | \uc624\ud604\uc11d |\\n| 16:15 ~ 17:00 | \ub370\uc774\ud130\ub97c \uc2a4\ucf00\uce58 \ud558\uae30                                              | \ud669\uc601   |\\n| 17:15 ~ 18:00 | AutoParams\ub97c \uc0ac\uc6a9\ud55c Spring Boot \uc751\uc6a9\ud504\ub85c\uadf8\ub7a8 \ud14c\uc2a4\ud2b8        | \uc774\uaddc\uc6d0 |\\n\\n\ub450 \ud2b8\ub799\uc73c\ub85c \ub098\ub258\uc5b4\uc11c \uc9c4\ud589\ub418\uc5c8\ub2e4. \ud574\ub2f9 \uc2dc\uac04\uc5d0 \uc6d0\ud558\ub294 \uc138\uc158\uc744 \uc120\ud0dd\ud574\uc11c \ub4e4\uc744 \uc218 \uc788\uc5c8\ub294\ub370, \uc544\uc9c1 \ucf54\ud2c0\ub9b0\uc73c\ub85c \uc2a4\ud504\ub9c1\uc744 \ub2e4\ub8e8\uc5b4 \ubcf8 \uc801\uc740 \uc5c6\uc5b4\uc11c Track1 \uc138\uc158\uc73c\ub85c \ucb49 \ub4e4\uc5c8\ub2e4.\\n\\n## \ud83c\udf31 Tidy First?\\n\\n![](https://m.media-amazon.com/images/I/414Lja0sVML._SY445_SX342_.jpg)\\n\\nTDD, \ub9ac\ud329\ud1a0\ub9c1\uc758 \uc800\uc790\ub85c \uc720\uba85\ud55c \ucf04\ud2b8 \ubc31\uc758 \uc2e0\uac04\uc778 _Tidy First?_ \uc758 \uc5ed\uc790\uc774\uc2e0 \uc548\uc601\ud68c\ub2d8\uc758 \uc138\uc158\uc774\uc600\ub2e4.\\n\\nTidy\ub294 \ud55c\uad6d\ub9d0\ub85c \ud558\uba74 \'\ucf54\ub4dc \uc815\ub9ac\' \ucbe4\uc73c\ub85c \ubc88\uc5ed\ud560 \uc218 \uc788\ub2e4. \uc774 \ucc45\uc740 \ucd1d 3\uad8c \uc2dc\ub9ac\uc988\ub85c \uad6c\uc131\ub418\uc5b4 \uc788\uace0, Tidy First?\ub294 \uc774\uc911 \uccab\ubc88\uc9f8 \ucc45\uc774\ub2e4. \ub450\ubc88\uc9f8 \ucc45\uc740 _Tidy Together_ \ub85c \ub098\uc62c \uac00\ub2a5\uc131\uc774 \ud06c\ub2e4\uace0 \ud55c\ub2e4.\\n\\n\uc0ac\uc2e4 \uc18c\ud504\ud2b8\uc6e8\uc5b4 \uac1c\ubc1c\uc774\ub780 \uc694\uad6c\uc0ac\ud56d\uc5d0 \ub9de\ub294 \uacb0\uacfc\ubb3c\uc774 \uc62c\ubc14\ub974\uac8c \ub098\uc624\uae30\ub9cc \ud558\uba74 \ub418\uc9c0\ub9cc, \ud55c\ubc88 \ub9cc\ub4e4\uace0 \ub05d\uc774 \uc544\ub2c8\ub77c, \uc720\uc9c0\ubcf4\uc218\ub97c \uacc4\uc18d \ud574\uc57c\ud55c\ub2e4. \ub530\ub77c\uc11c \ubcc0\uacbd\uc5d0 \uc6a9\uc774\ud55c \ucf54\ub4dc\ub97c \uc9dc\ub294 \uac83\uc774 \uc911\uc694\ud558\ub2e4. \uc774 \uacfc\uc815\uc5d0\uc11c \ud544\uc694\ud55c\uac8c **Tidy**\uc774\uace0, \uc774\uac83\uc744 **\uc65c** \ud574\uc57c \ud558\ub294\uc9c0, **\uc5b8\uc81c** \ud574\uc57c \ud558\ub294\uc9c0, **\uc5b4\ub5bb\uac8c** \ud574\uc57c \ud558\ub294\uc9c0\uc5d0 \ub300\ud574 \ub2e4\ub8e8\ub294 \ucc45\uc774\ub2e4.\\n\\n\uc774 \ucc45\uc758 \uc124\uba85\uc5d0 \uc548\uc601\ud68c\ub2d8\uc774 \uc18c\ud504\ud2b8\uc6e8\uc5b4 \uac1c\ubc1c\uc5d0 \ub300\ud574 \uc0dd\uac01\ud558\ub294 \ubc14\ub97c \uc5ff\ubcfc \uc218 \uc788\uc5c8\ub294\ub370\\n\\n> \uc18c\ud504\ud2b8\uc6e8\uc5b4 \uac1c\ubc1c\uc774\ub780 \'\uc720\uae30\uccb4\'\uc640 \uc720\uc0ac\ud558\ub2e4. \\"\uc694\uc18c\ub97c \uc720\uc775\ud558\uac8c \uad00\uacc4\ub9fa\ub294 \uc77c\\" \uc774\ub2e4.\\n\\n\uc774 \ubb38\uc7a5\uc774 \uc778\uc0c1\uae4a\uc5c8\ub358 \uac83 \uac19\ub2e4. \uc0dd\ubb3c\uacfc \uc720\uc0ac\ud558\uac8c \uc18c\ud504\ud2b8\uc6e8\uc5b4\ub3c4 \ucabc\uac1c\uba74 \uc791\uc740 \ub2e8\uc704\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\uace0, \uadf8\uac83\ub4e4\uc774 \uc0c1\ud638\uc791\uc6a9 \ud574 \ud558\ub098\uc758 \uc2dc\uc2a4\ud15c\uc744 \ub9cc\ub4e4\uc5b4 \ub0b4\uae30 \ub54c\ubb38\uc774\ub2e4.\\n\\n\uc9e7\uc740 \uacbd\ub825\uc744 \uac16\uace0 \uc788\ub294 \ub098\uc9c0\ub9cc,, \uac15\uc5f0 \ub0b4\uc6a9\uc5d0\ub294 \uacf5\uac10\ud560 \uc218 \uc788\ub294 \ub0b4\uc6a9\uc774 \uc544\uc8fc \ub9ce\uc558\ub2e4. \ucf54\ub4dc\ub294 \uc644\ubcbd\ud560 \uc218 \uc5c6\uace0, \ub54c\ub860 \uae09\ud558\uac8c \uc791\uc131\ud558\uae30\ub3c4 \ud558\ub294\ub370 \uc774\uac78 \uc5b8\uc820\uac04 \uc815\ub9ac\ud574\uc57c \ud55c\ub2e4. \ubbf8\ub8e8\uba74 \ubbf8\ub8f0\uc218\ub85d \ud558\uae30 \ud798\ub4e4\uc5b4\uc9c0\uace0, \ucc98\uc74c\ubd80\ud130 \uc798 \ud574\ub193\ub294\uac8c \uac00\uc7a5 \ubca0\uc2a4\ud2b8\uc774\ub2e4. \ucf54\ub4dc\ub97c \uc815\ub9ac\ud558\ub294 \ubc29\ubc95\ub860\uc744 \uacf5\ubd80\ud574\uc11c \uc801\uc6a9\ud574\ubcf4\uba74 \ub354 \uc88b\uc744 \uac83 \uac19\ub2e4.\\n\\n\ucc45\uc774 \ucf54\ub4dc\ub97c \ud0c0\uc774\ud551\ud558\uba74\uc11c \ub530\ub77c\ud560 \uc218 \uc788\uac8c\ub054 \uad6c\uc131\ub418\uc5b4 \uc788\ub2e4\uace0 \ud558\ub294\ub370, \uc2a4\ud130\ub514\ub97c \uad6c\uc131\ud574\uc11c \uc644\uc8fc\ub97c \ud55c\ubc88 \ud574\ubcf4\uace0 \uc2f6\ub2e4.\\n\\n## \ud83c\udf31 Spring AI : LLM\uc5d0\ub3c4 \ubd04\uc774 \ucc3e\uc544\uc624\ub2e4.\\n\\n![](../static/img/post-img/IMG_5206.jpg)\\n\\n\uce74\uce74\uc624\uc758 \ud669\ubbfc\ud638\ub2d8 \uc138\uc158\uc774\uc600\ub2e4. LLM\uc758 \ubc1c\uc804 \uacfc\uc815\uacfc Spring AI\ub97c \ud65c\uc6a9\ud558\ub294 \ubc29\ubc95\uc5d0 \ub300\ud574 \uac15\uc5f0\ud574\uc8fc\uc168\ub2e4. SpringAI\uc758 \ud2b9\uc9d5\uc744 \uac04\ub7b5\ud558\uac8c \uc815\ub9ac\ud558\uba74\\n\\n- \uc8fc\uc694 AI Provider \ud1b5\ud569\\n  - OpenAI, \ub9c8\uc774\ud06c\ub85c\uc18c\ud504\ud2b8 Asure, \uc544\ub9c8\uc874, \uad6c\uae00 \ub4f1 \uc9c0\uc6d0\\n  - \ucc44\ud305, \ud14d\uc2a4\ud2b8-\uc774\ubbf8\uc9c0 \ubcc0\ud658 \ubaa8\ub378 \uc720\ud615 \uc9c0\uc6d0\\n  - \ub3d9\uae30\uc2dd \ubc0f \uc2a4\ud2b8\ub9bc \uc9c0\uc6d0\\n- \uc8fc\uc694 \ubca1\ud130DB \uc9c0\uc6d0\\n- AI \uac1c\ubc1c \ud3b8\uc758\\n  - AI \ubaa8\ub378 \ucd9c\ub825\uc744 POJO\ub85c \ub9e4\ud551\\n  - Function Calling \uc9c0\uc6d0\\n  - AI\uc6a9 SpringBoot Starter \uc9c0\uc6d0\\n\\n\uc815\ub3c4\uac00 \uc788\uaca0\ub2e4. \uc9c0\uae08 v.0\uc810\ub300 \uac1c\ubc1c\uc774 \uc9c4\ud589\uc911\uc774\uace0 \uc62c\ud574 \ud558\ubc18\uae30\ucbe4 v1.0\uc774 \ub098\uc628\ub2e4\uace0 \ud55c\ub2e4. \ub098 \ub610\ud55c ChatGPT\uc640 \ucf54\ud30c\uc77c\ub7ff\uc744 \uc815\ub9d0 \uc720\uc6a9\ud558\uac8c \uc4f0\uace0 \uc788\ub2e4. AI\uac00 \uc815\ub9d0 \ube60\ub974\uac8c \ubc1c\uc804\ud558\uace0 \uc788\ub2e4\uace0 \ub290\uaef4\uc9c4\ub2e4. \uac1c\ubc1c\uc790\ub85c\uc11c\ub3c4 \ubb34\uc2dc\ud560 \uc218 \uc5c6\ub294 \ud750\ub984\uc778 \uac83 \uac19\ub2e4. \uc774\ub7ec\ud55c \ud504\ub808\uc784\uc6cc\ud06c\ub97c \uc774\uc6a9\ud574 AI\ub97c \uc0ac\uc6a9 \ud560 \uc218 \uc788\ub294 \ub2a5\ub825\uc744 \uac16\ucd98 \uac1c\ubc1c\uc790\ub3c4 \uace7 \uc218\uc694\uac00 \uc810\uc810 \ub298\uc5b4\ub0a0 \uac83 \uac19\ub2e4. \uc2e4\uc81c\ub85c \uadf8\ub7f0 \uac83 \uac19\uae30\ub3c4 \ud558\ub2e4.\\n\\n\ubcf4\uc548\uc801\uc73c\ub85c \uad6d\ub0b4 \ucd5c\uace0 \uc608\ubbfc\ud55c \uc6b0\ub9ac\ud68c\uc0ac\ub3c4 \ucd5c\uadfc \ub9c8\uc774\ud06c\ub85c\uc18c\ud504\ud2b8 \ucf54\ud30c\uc77c\ub7ff\uc744 \uc774\uc6a9\ud560 \uc218 \uc788\uac8c \ud574\uc8fc\uc5c8\ub2e4. IT\ubd84\uc57c\uac00 AI\uc2dc\ub300\ub97c \ub9de\uc73c\uba70 \ube60\ub974\uac8c \ubcc0\ud654\ud558\uace0 \uc788\ub2e4.\\n\\n\ub098\ub3c4 \ub2e4\uc74c \uc0ac\uc774\ub4dc \ud504\ub85c\uc81d\ud2b8\ub294 AI\ub97c \ud65c\uc6a9\ud55c \ud504\ub85c\uc81d\ud2b8\ub97c \ud574\ubcf4\uc544\uc57c \uaca0\ub2e4. ~~\ub3c8\ub9cc \ubb38\uc81c\uac00 \uc548\ub41c\ub2e4\uba74~~\\n\\n## \u2615\ufe0f Coffee Break\\n\\n![alt text](../static/img/post-img/IMG_5207.jpg)\\n\\n\ub2ec\ub2e4\uad6c\ub9ac\uc640 \ucee4\ud53c(\ub610\ub294 \uc624\ub80c\uc9c0\uc8fc\uc2a4) \ub098\ub220\uc8fc\uc154\uc11c \uae30\ub825 \ucda9\uc804\ud588\ub2e4 \ud83d\udc4d\\n\\n## \uc65c \ub098\ub294 \ud14c\uc2a4\ud2b8\ub97c \uc791\uc131\ud558\uae30 \uc2eb\uc744\uae4c?\\n\\n![alt text](../static/img/post-img/IMG_5209.jpg)\\n\\n\ub124\uc774\ubc84 \ud50c\ub77c\uc988\ub9c8\ud300\uc758 \uc870\uc131\uc544\ub2d8 \uc138\uc158\uc774\uc600\ub2e4. \ud14c\uc2a4\ud2b8\ub97c \uc791\uc131\ud558\ub294 \uc790\uc138\uc5d0 \ub300\ud55c \uc774\uc57c\uae30\uc600\uace0, \uc624\ud508\uc18c\uc2a4\uc778 [\ud83d\udc35 FixtureMonkey](https://naver.github.io/fixture-monkey/v1-0-0/)\uac1c\ubc1c\uae30\uc640 \uc0ac\uc6a9 \ud301\ub3c4 \uc804\ub2ec\ud574\uc8fc\uc168\ub2e4.\\n\\n\ud53d\uc2a4\uccd0\ubabd\ud0a4\ub294 \ub4e4\uc5b4\ubcf8 \uc801\uc774 \uc788\uae34 \ud55c\ub370, \ud14c\uc2a4\ud2b8 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub77c\uace0\ub9cc \uc54c\uace0\uc788\uace0 \uc6a9\ub3c4\ub294 \ubab0\ub790\ub2e4. \ud14c\uc2a4\ud2b8\uc5d0 \ud544\uc694\ud55c \uac1d\uccb4\ub4e4\uc744 \uc27d\uac8c \uc0dd\uc131\ud574\uc8fc\ub294 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub77c\uace0 \ud55c\ub2e4. \ud14c\uc2a4\ud2b8\ub97c \uc791\uc131\ud55c \uacbd\ud5d8\uc774 \ub9ce\uc9c0\ub294 \uc54a\uc9c0\ub9cc, REST Docs\ub85c \ud14c\uc2a4\ud2b8 \uc791\uc131\ud560\ub54c, \ud544\uc694\ud55c \uc624\ube0c\uc81d\ud2b8\ub4e4\uc744 \uc0dd\uc131\uc790\ub098 \ube4c\ub354\ub85c \uc0dd\uc131\ud574\uc8fc\ub294 \uacfc\uc815\uc740 \uaf64.. \uadc0\ucc2e\uae34 \ud588\ub2e4. \uc774\ub7ec\ud55c \uacfc\uc815\uc744 \uc904\uc5ec\uc904 \uc218 \uc788\ub2e4\ub2c8 \uc801\uadf9 \uac80\ud1a0\ud574\ubcf4\uc544\uc57c \uaca0\ub2e4\ub294 \uc0dd\uac01\uc744 \ud588\ub2e4.\\n\\n\ud14c\uc2a4\ud2b8\ub97c \uc791\uc131\ud558\uba74 \uc548\uc804\uc131\uc744 \ub192\uc5ec \uc2e0\ub8b0\uc131 \uc788\ub294 \ud504\ub85c\uadf8\ub7a8\uc744 \ub9cc\ub4e4 \uc218 \uc788\ub2e4. \ud558\uc9c0\ub9cc \ud14c\uc2a4\ud2b8\ub97c \uc791\uc131\ud558\ub294\ub370\uc5d0\ub3c4 \ube44\uc6a9\uc774 \ub4e0\ub2e4. \uac00\uc7a5 \ud070 \ubb38\uc81c\uc810\uc740 \uc694\uad6c\uc0ac\ud56d\uc774 \ucd94\uac00\ub418\uace0 \ubcc0\uacbd\ub420\ub54c \ud14c\uc2a4\ud2b8\ub3c4 \uac19\uc774 \uc218\uc815\uc774 \ud544\uc694\ud558\ub2e4\ub294 \uac83\uc774\ub2e4. \uae30\ub2a5 \ud558\ub098 \ucd94\uac00\ud558\uace0 \ud544\ub4dc \ud558\ub098 \ucd94\uac00\ud588\ub294\ub370 \uba87\ubc31\uac1c\uc758 \ud14c\uc2a4\ud2b8\ub97c \uc218\uc815\ud574\uc57c \ud55c\ub2e4\uba74...? \ub054\ucc0d\ud558\ub2e4.\\n\\n\uc774\ub7ec\ud55c \ud2b8\ub808\uc774\ub4dc\uc624\ud504\ub97c \uace0\ubbfc\ud55c \uacbd\ud5d8\uc5d0 \ub300\ud574 \ud480\uc5b4\uc8fc\uc168\ub2e4. \uc774\ub7f0 \uc800\ub7f0 \uc2e4\ud328\ub97c \uacbd\ud5d8\ud558\uace0 \uc2dc\ud589\ucc29\uc624\ub97c \uacaa\uc73c\uba70 \ud14c\uc2a4\ud2b8\uc5d0 \ub300\ud574 \ub098\ub984\uc758 \uc815\uc758\ub97c \ud55c \uacfc\uc815\uc744 \uacf5\uc720\ud574\uc8fc\uc168\ub2e4.\\n\\n> \ubaa8\ub4e0 \ud14c\uc2a4\ud2b8 \ucf00\uc774\uc2a4, \uc5e3\uc9c0 \ucf00\uc774\uc2a4\ub97c \ubaa8\ub450 \ud14c\uc2a4\ud2b8 \ud558\ub824\uace0 \ud558\uae30\ubcf4\ub2e8 \ub3d9\uc791 \uc5ec\ubd80 \uac80\uc99d\uc5d0 \uc9d1\uc911\ud558\uc790.\\n\\n\ud14c\uc2a4\ud2b8\ub97c \uc791\uc131\ud558\ub294 \uac83\uc740 \uc911\uc694\ud558\uc9c0\ub9cc, \uc9c0\uc18d \uac00\ub2a5\ud55c \ud14c\uc2a4\ud2b8\ub97c \uc791\uc131\ud558\ub294\uac83\uc774 \uac00\uc7a5 \uc911\uc694\ud558\ub2e4! \ub294 \uba54\uc2dc\uc9c0\uc600\ub2e4.\\n\\n\ud14c\uc2a4\ud2b8\uc758 \ud544\uc694\uc131\uc740 \ubf08\uc800\ub9ac\uac8c \ub290\ub07c\uace0 \uc788\ub2e4. \ud68c\uc0ac\uc5d0\uc11c SpringBoot\ub85c \ub41c \ubd80\ubd84\uc740 \ud55c\ubc88 \uc2dc\uc791\ud558\uba74 \ud0c0\uace0\ud0c0\uace0 \ub4e4\uc5b4\uac00 \ub3d9\uc791\ud558\ub294.. \ub9c8\uce58 \uc808\ucc28\uc9c0\ud5a5\ucc98\ub7fc \ub418\uc5b4\uc788\ub2e4. \uc5ec\uae30\uc11c \ud558\ub098 \uc218\uc815\ud558\ub824\uace0 \ud558\uba74 \uc790\uc2e0\uc774 \uc77c\ub2e8 \uc5c6\ub2e4. \uc218\uc815\uc744 \ud574\uc11c \uc6d0\ub798 \ub3d9\uc791\ud588\ub358 \uae30\ub2a5\uc774 \uc798 \ub420\uc9c0 \uc54c \uc218\uac00 \uc5c6\ub2e4. \uc2ec\uc9c0\uc5b4 \ub9ac\ub205\uc2a4\uc5d0\uc11c\ub9cc \ub3d9\uc791\ud558\uace0, \uc815\ud574\uc9c4 \ud3f4\ub354 \uad6c\uc870\uc5d0\uc11c\ub9cc \ub3d9\uc791\ud558\uae30 \ub54c\ubb38\uc5d0 \uc708\ub3c4\uc6b0 IDE\uc5d0\uc11c\uc870\ucc28 \ub3cc\ub9b4 \uc218 \uc5c6\ub2e4. \uae30\ub2a5 \ud558\ub098 \uc218\uc815\ud558\uba74 \uae38\uac8c\ub294 40\ubd84.. \ub3d9\uc548 \ucf54\ub4dc\ub97c \uc2e4\ud589\ud574\ubcf4\uace0 \ub85c\uadf8\ub97c \ud1b5\ud574\uc11c \ub514\ubc84\uae45 \ud574\uc57c \ud558\ub294 \uc0c1\ud669\uc774\ub2e4. \uc800\ubc88\uc5d0 \ub9ac\ud329\ud1a0\ub9c1 \ud55c\ub2e4\uace0 \ucf54\ub4dc\ub97c \ub300\uac70 \uc218\uc815\ud55c \uc801\uc774 \uc788\uc5c8\ub294\ub370, \ud558\ub8e8\uc5d0 9/10 \uc815\ub3c4\ub97c \uc2e4\ud589\ud574\uc11c \uacb0\uacfc\ubcf4\ub294 \uc2dc\uac04\uc5d0 \uc368\uc57c \ud588\ub2e4. \ud14c\uc2a4\ud2b8\uac00 \uc788\uc5c8\ub2e4\uba74, \ud14c\uc2a4\ud2b8\uac00 \uac00\ub2a5\ud55c \ud658\uacbd\uc744 \uc5fc\ub450\uc5d0 \ub450\uace0 \uc791\uc131\ub418\uc5c8\ub2e4\uba74 \uc774\ub7f0 \uc77c\uc774 \uc5c6\uc5c8\uc744\uac70\ub2e4. \uc9c4\uc9dc \ub108\ubb34 \ud798\ub4e4\ub2e4..\\n\\n\ud558\uc9c0\ub9cc \uc774\ubbf8 \uc644\uc131\ub41c \uc2dc\uc2a4\ud15c\uc5d0\uc11c \ud14c\uc2a4\ud2b8\ub97c \ucd94\uac00\ud558\uae30\ub780 \uc27d\uc9c0 \uc54a\ub2e4. \ud14c\uc2a4\ud2b8\ub97c \uace0\ub824\ud558\uc9c0 \uc54a\uace0 \uc9dc\uc5ec\uc84c\uae30 \ub54c\ubb38\uc5d0, \uc720\ub2db\ud14c\uc2a4\ud2b8\uc640 \uac19\uc740 \uc791\uc740 \ub2e8\uc704\uc758 \ud14c\uc2a4\ud2b8\ub294 \ub9e4\uc6b0 \ud798\ub4e4\ub2e4. Q&A\uc5d0\uc11c \uc774\uac83\uc5d0 \ub300\ud55c \ud78c\ud2b8\ub97c \uc5bb\uc744 \uc218 \uc788\uc5c8\ub294\ub370, \ubd84\ub9ac \uac00\ub2a5\ud55c\uac83\uc744 \ubd84\ub9ac\ud558\uace0 \uc774\uac83\ubd80\ud130 \ud14c\uc2a4\ud2b8 \ud558\ub294 \ubc29\ubc95\uc774\ub2e4. \ud14c\uc2a4\ud2b8 \ucf54\ub4dc\uac00 \uc5c6\uc9c0\ub9cc, \ub098\ubd80\ud130 \uc77c\ub2e8 \uc2e4\ucc9c\ud558\uace0 \ud14c\uc2a4\ud2b8\uc758 \uc7a5\uc810\uc744 \ud589\ub3d9\uc73c\ub85c \uc785\uc99d\ud574\uc57c\uaca0\ub2e4\ub294 \uc0dd\uac01\uc744 \ud588\ub2e4. \uc774\ubc88\uc5d0 \ucd94\uac00\ub418\ub294 \uae30\ub2a5\uc740 \uaf2d. \ud14c\uc2a4\ud2b8 \uc791\uc131\ud558\uba74\uc11c \ud574\ubcf4\ub824\uace0 \ud55c\ub2e4.\\n\\n\uc77c\ub2e8 \ud14c\uc2a4\ud2b8 \uc791\uc131\ubc95\ubd80\ud130 \uacf5\ubd80\ud574\uc57c \ud560 \uac83 \uac19\ub2e4. \ud14c\uc2a4\ud2b8\ub97c \uc798 \ud558\ub294 \uc0ac\ub78c\uc774 \ube44\uc9c0\ub2c8\uc2a4 \ucf54\ub4dc\ub3c4 \uc798 \uc9dc\uac8c \ub420 \uc218 \uc788\uc744\uac83 \uac19\ub2e4\ub294 \uc0dd\uac01\uc774 \ub4e0\ub2e4. \uc791\uc740 \ub2e8\uc704\uc758 \ud14c\uc2a4\ud2b8\uac00 \uac00\ub2a5\ud558\ub2e4\ub294 \uac83\uc740 \uacb0\ud569\ub3c4\uac00 \ub0ae\ub2e4\ub294 \ub73b\uc774\uae30 \ub54c\ubb38\uc774\ub2e4. \uc5ec\ub7ec\ubaa8\ub85c \ub290\ub07c\ub294 \uac83\uc774 \ub9ce\uc558\ub358 \uc138\uc158\uc774\ub2e4.\\n\\n\ucd94\uac00\ub85c \ubd88\ud3b8\ud55c \uc810\uc744 \uac1c\uc120\ud558\ub824\uace0 \uc624\ud508\uc18c\uc2a4\uae4c\uc9c0 \uac1c\ubc1c\ud558\ub294 \ubaa8\uc2b5\uc774 \uc815\ub9d0 \uba4b\uc788\uc5c8\ub2e4. \uc624\ud508\uc18c\uc2a4 \uba54\uc778\ud14c\uc774\ub108\ub77c\ub2c8 \ub0b4 \uc6cc\ub108\ube44\ub2e4.\\n\\n## \uc2e4\uc804! MSA \uac1c\ubc1c \uac00\uc774\ub4dc\\n\\n\uc0bc\uc131 SDS\uc758 \uae40\uc6a9\uc6b1\ub2d8\uc758 \uac15\uc5f0\uc774\uc600\ub2e4. \uc0ac\ub0b4\uc5d0\uc11c MSA \ucee8\uc124\ud134\ud2b8\uc640 \uac1c\ubc1c\uc744 \ud558\uace0 \uacc4\uc2e0\ub2e4\uace0 \ud55c\ub2e4. \uc774\ubc88 \uc2a4\ud504\ub9c1\ucea0\ud504 \uac15\uc5f0\uc911\uc5d0\uc11c\ub3c4 \uc815\ub9d0 \uc778\uc0c1\uae4a\uc5c8\ub358 \uc2dc\uac04\uc774\uc600\ub2e4.\\n\\n\uac04\ub2e8\ud558\uac8c \uc815\ub9ac\ud574\ubcf4\uba74 \ub2e4\uc74c\uacfc \uac19\ub2e4.\\n\\n+++\\n\\nMSA\ub97c \ub3c4\uc785\ud558\ub824\uba74 \ub2e4\uc74c \ub450\uac00\uc9c0\uac00 \uac71\uc815\uc774 \ub41c\ub2e4.\\n- API\ub85c \uc18d\ub3c4\uac00 \uad1c\ucc2e\uc744\uae4c?\\n- \ud2b8\ub79c\uc7ad\uc158 \ubcf4\uc7a5 \uc5c6\uc774 \uc815\ud569\uc131\uc774 \ub9de\uc744\uae4c?\\n\\n**\ud83d\udccc API\ub85c \uc18d\ub3c4\uac00 \uad1c\ucc2e\uc744\uae4c?**\\n\\nMSA\ub85c \uc2dc\uc2a4\ud15c\uc744 \uad6c\uc131\ud558\uac8c \ub418\uba74 \uac01 \uc11c\ube44\uc2a4\ub294 API\ub97c \ud1b5\ud574 \ud1b5\uc2e0\ud558\uac8c \ub41c\ub2e4. \ub124\ud2b8\uc6cc\ud06c\ub97c \ud0c0\uae30 \ub54c\ubb38\uc5d0 \uc18d\ub3c4\uc5d0 \ub300\ud55c \uc6b0\ub824\uac00 \uc788\uae30 \ub9c8\ub828\uc774\ub2e4. \uc774\ub97c \ud574\uacb0\ud558\ub294 \ubc29\ubc95\uc740 \uc774\ub7f0 \uac83\ub4e4\uc774 \uc788\ub2e4.\\n\\n1. \ub370\uc774\ud130 \ubcf5\uc81c\\n    - \ud544\uc694\ud55c \uc18d\uc131\uc744 \ubbf8\ub9ac \ubcf5\uc0ac\ud574\ub450\uace0, \ubc30\uce58 \ub4f1\uc73c\ub85c sync\ub97c \ub9de\ucd98\ub2e4.\\n    - \uc774\ub54c \uc8fc\uc758\ud560 \uac83\uc740 \ubabd\ub545 \ubcf5\uc0ac\ud558\uba74 \uc548\ub41c\ub2e4\ub294 \uac83\uc774\ub2e4. DB Table\ub85c \uce58\uba74, \uba87\uac1c \ud544\uc694\ud55c \uc18d\uc131\ub9cc \ubcf5\uc0ac\ud574\ub454\ub2e4.\\n2. \ubaa8\ub378\ub9c1 \ubcc0\uacbd\\n    - \uc124\uacc4\ud560\ub54c \uc608\ub97c\ub4e4\uc5b4 \'\uace0\uac1d\'\uacfc \'\uc0c1\ub2f4\'\ud14c\uc774\ube14\uc774 \uc788\ub2e4\uba74, \ud574\ub2f9 \ub370\uc774\ud130\uac00 \'\uace0\uac1d\'\uacfc \uad00\ub828\ub41c \ub370\uc774\ud130\uc774\uace0, \uc124\uacc4\uc0c1 \uace0\uac1d\uc11c\ube44\uc2a4\uc640 \uac19\uc774\uc788\ub294 \uac83\uc774 \uc6d0\ub798\uc758 \uadf8\ub9bc\uc77c\ub54c \ub9cc\uc57d \'\uc0c1\ub2f4\' \uc11c\ube44\uc2a4\uc5d0\uc11c \uc790\uc8fc \ucc38\uc870\ud558\ub294 \uac83\uc774\ub77c\uba74 \uace0\uac1d\uc815\ubcf4\uc9c0\ub9cc \uc0c1\ub2f4 \uc11c\ube44\uc2a4\ucabd\uc5d0 \ub370\uc774\ud130\ub97c \ub450\ub294\uac83\uc774\ub2e4. \uc774\ub807\uac8c \ub418\uba74 \uc11c\ube44\uc2a4\uac04 \ud1b5\uc2e0\uc744 \ud558\uc9c0 \uc54a\uace0\ub3c4 \ud574\uacb0\ud560 \uc218 \uc788\ub2e4.\\n3. \uc77c\uad04 \uc870\ud68c\\n    - 200\uac1c\ub97c \uc870\ud68c\ud55c\ub2e4\uace0 \uce58\uba74, REST API 200\uac1c\uac00 \ub098\uac00\ub294 \uac83\uc774 \uc544\ub2c8\ub77c, \ud55c\ubc88\uc5d0 \uc870\ud68c\ud560 \uc218 \uc788\uac8c \ud574\uc57c \ud55c\ub2e4.\\n    - REST API\uc758 N+1 Problem \ubb38\uc81c\uac00 \uc788\ub2e4.\\n4. \ubcd1\ub82c \uc870\ud68c\\n    - \uc21c\uac04\uc801\uc73c\ub85c \ud070 \ubd80\ud558\uac00 \uc0dd\uae38 \uc218 \uc788\uc5b4 \ub300\ubd80\ubd84\uc758 \uacbd\uc6b0 \uc548\ud2f0\ud328\ud134\uc774\ub2e4.\\n5. \ub85c\uceec \uce90\uc2dc\\n    - \uacf5\ud1b5\ucf54\ub4dc\uc640 \uac19\uc774 \ubcc0\uacbd\uc774 \uc801\uc9c0\ub9cc \uc790\uc8fc \uc870\ud68c\ud574\uc57c \ud558\ub294 \uacbd\uc6b0, \ub85c\uceec\uce90\uc2dc\uc5d0 \uc62c\ub824\ub450\uace0 \uc0ac\uc6a9\ud55c\ub2e4.\\n    - Redis\ub294 \ub290\ub9ac\ub2e4.\\n    - \ub85c\uceec\uce90\uc2dc\ub294 \ub3d9\uae30\ud654 \ud558\uc9c0 \uc54a\ub294 \uac83\uc744 \uc804\uc81c\ub85c \ud55c\ub2e4.\\n      - \uc11c\ube44\uc2a4\uac00 \uc5ec\ub7ec\uac1c\uc77c\ub54c \uc774\ub97c \ub3d9\uae30\ud654 \ud560 \uc218 \uc5c6\ub2e4.\\n      - \ub3d9\uae30\ud654 \ud558\ub294 \uac83\uc740 \uc548\ud2f0\ud328\ud134\uc774\ub2e4.\\n\\n**\ud83d\udccc \ud2b8\ub79c\uc7ad\uc158 \ubcf4\uc7a5 \uc5c6\uc774 \uc815\ud569\uc131\uc774 \ub9de\uc744\uae4c?**\\n\\n\uc11c\ube44\uc2a4\uac00 \ub098\ub258\uc5b4\uc838 \uc788\uae30 \ub54c\ubb38\uc5d0 \ud558\ub098\uc758 \uae30\ub2a5\uc5d0\uc11c \ud55c\ubc88\uc758 \ud2b8\ub79c\uc7ad\uc158\uc73c\ub85c \ub3d9\uc791\uc744 \ubaa8\ub450 \uc218\ud589\ud560 \uc218 \uc5c6\ub2e4.\\n\\n- MSA \uad6c\uc870\uc758 \uacbd\uc6b0 \uc804\uccb4 \uacfc\uc815\uc5d0 \ub300\ud55c \ub864\ubc31\uc774 \ubd88\uac00\ub2a5\ud558\ub2e4.\\n- \ub864\ubc31 \ub300\uc2e0 \uc2e4\ud328\uc2dc \uc0ad\uc81c\ud558\ub294 API\uac00 \ud544\uc694\ud558\ub2e4.\\n- \uadf8\ub7fc \uc0ad\uc81c\ud558\ub2e4\uac00 \uc2e4\ud328\ud558\uba74 \uc5b4\uca4c\ub0d0?\\n    - \ub300\uc0ac\uc791\uc5c5\uc774 \ud544\uc694\ud560 \uc218 \uc788\ub2e4\ub294\uac83\uc744 \uc778\uc9c0\ud574\ub454\ub2e4. (ex. \ubc30\uce58)\\n- API\uac00 \uc2e4\ud328\ud588\uc744\ub54c \ubc14\ub85c \uc7ac\uc2dc\ub3c4 \ud558\ub294\uac83\uc740 \ub300\ubd80\ubd84 \uc548\ud2f0\ud328\ud134\uc774\ub2e4.\\n    - \ud574\ub2f9 \uc11c\ube44\uc2a4\uac00 \ud68c\ubcf5\ud560 \uc2dc\uac04\uc744 \uc8fc\uc5b4\uc57c \ud55c\ub2e4.\\n    - \uadf8\ub7f0\ub370 \uc774\ubca4\ud2b8 \ubc29\uc2dd\uc740 \uad1c\ucc2e\ub2e4!\\n        - \uc774\ubca4\ud2b8\ub85c \uc7ac\uc2dc\ub3c4 \ud558\ub294 \uac83\uc740 \ud574\ub2f9 \uc11c\ube44\uc2a4\uac00 \uac00\ub2a5\ud560\ub54c \ud050 \ub4f1\uc5d0\uc11c \ub04c\uc5b4\ub2e4 \uc4f0\ub294 \uac83\uc774\ub77c \uad1c\ucc2e\ub2e4.\\n    - \uadf8\ub9ac\uace0 \uc2e4\ud328\ub77c\uace0 \uc751\ub2f5\ubc1b\uc558\ub294\ub370, \uc0ac\uc2e4 \uc131\uacf5\ud588\uc744 \uc218\ub3c4 \uc788\ub2e4\ub294 \uac83\uc744 \uc54c\uc544\uc57c \ud55c\ub2e4.\\n        - \uc774\ub807\uac8c \ub418\uba74 \ub450\ubc88 \uc218\ud589\ud558\uac8c \ub41c\ub2e4.\\n        - \ub530\ub77c\uc11c \uba71\ub4f1\uc131\uc774 \ubcf4\uc7a5\ub418\uc5b4\uc57c \ud55c\ub2e4.\\n- \uc5b8\uc81c \uc774\ubca4\ud2b8\ub85c \ubd84\ub9ac\ud558\ub294\uac00?\\n    - \uc2e4\ud328\ud574\ub3c4 \uc804\uccb4 \ucde8\uc18c\uac00 \uc5c6\ub2e4\uba74 \uc774\ubca4\ud2b8\ub85c \ubd84\ub9ac\ud55c\ub2e4.\\n\\n+++\\n\\n\ud558\ub098\ud558\ub098 \ub2e4 \ub3c4\uc6c0\ub418\uace0 \uacf5\ubd80\ud560 \uc218 \uc788\ub294 \ub0b4\uc6a9\uc774\ub77c \uc5f4\uc2ec\ud788 \uc801\uc5b4\ubcf8 \uc138\uc158\uc774\uc600\ub2e4. \ud68c\uc0ac \ud504\ub85c\uc81d\ud2b8\uc758 \uacbd\uc6b0 MSA\ub97c \ub3c4\uc785\ud558\uae30\uc5d4 \uc0ac\ub78c\ub3c4 \uc5c6\uace0, \uc791\uc544\uc11c \ub2f9\ubd84\uac04\uc740 \uc2e4\ubb34\uc5d0\uc11c \uacbd\ud5d8 \ud574\ubcfc \uc218 \uc5c6\ub294 \uc810\uc774 \uc544\uc27d\ub2e4.. \uc5b8\uc820\uac00 \uac1c\ubc1c\ud560 \ub54c\ub97c \ub300\ube44\ud574 \ubbf8\ub9ac\ubbf8\ub9ac \uacf5\ubd80\ud574 \ub450\uc5b4\uc57c \uaca0\ub2e4. MSA\ub77c\ub294 \uac1c\ub150\uc774 \uba40\uac8c \ub290\uaef4\uc84c\ub294\ub370, \uc774\ubc88 \uc138\uc158\uc744 \ub4e4\uc73c\uba74\uc11c \uc724\uacfd\uc774 \uc880 \ubcf4\uc600\ub358 \uac83 \uac19\ub2e4. MSA\ub97c \uc801\uc6a9\ud55c\ub2e4\ub294 \uac83\uc740 \uc5b4\ub5a4 \uac83\uc774\uace0, \uc8fc\uc758\ud560 \uc810\uc740 \ubb34\uc5c7\uc778\uc9c0 \uc870\uae08\uc740 \uc54c \uac83 \uac19\ub2e4. \ub9e4\uc6b0\ub9e4\uc6b0 \uc88b\uc558\ub358 \uac15\uc5f0\uc774\uc600\ub2e4!\\n\\n## \uad6c\ud574\uc918 \ud648\uc988! \uc740\ud589\uc5d0\uc11c 3\ucc9c\ub9cc \ud2b8\ub798\ud53d\uc758 \ud648 \uc11c\ube44\uc2a4 \uc0c8\ub85c \ub9cc\ub4e4\uae30\\n\\n\uce74\uce74\uc624\ubc45\ud06c \ud648\uc11c\ube44\uc2a4\uac1c\ubc1c\ud300\uc758 \uc774\uc601\uaddc\ub2d8\uc758 \ubc1c\ud45c\uc600\ub2e4. \uc740\ud589\uc5d0\uc11c \ud648 \uc11c\ube44\uc2a4\uc758 \ubd84\ub9ac \uc5ec\uc815\uc744 \uc18c\uac1c\ud558\ub294 \ub0b4\uc6a9\uc774\uc600\ub2e4.\\n\\n\uae30\uc220 \ubd80\ucc44\ub85c \ud574\uacb0\ud558\ub294 \ubb38\uc81c\ub294 \ub450\uac00\uc9c0 \uc600\ub2e4\uace0 \ud55c\ub2e4.\\n\\n- \uad6c\uc870\uc801 \ubb38\uc81c\\n- \uc131\ub2a5\uc801 \ubb38\uc81c\\n\\n**\ud83d\udccc \uad6c\uc870\uc801 \ubb38\uc81c - \ud575\uc0ac\uace0\ub0a0 \uc544\ud0a4\ud14d\ucc98**\\n\\n\uad6c\uc870\uc801 \ubb38\uc81c\ub294 \ud575\uc0ac\uace0\ub0a0 \uc544\ud0a4\ud14d\ucc98\ub97c \uc801\uc6a9\ud574 \ud480\uc5c8\ub2e4\uace0 \ud55c\ub2e4. \uc694\uc998 \uc815\ub9d0 \uc790\uc8fc \ub4e4\ub9ac\ub294 \ub2e8\uc5b4\uc778\ub370, \uc544\uc9c1 \uc5ec\uae30\uae4c\uc9c0\ub294 \uacf5\ubd80\ud558\uc9c0 \ubabb\ud588\ub2e4.\ud83e\udd72 \ub3c4\uba54\uc778\uacc4\uce35\uc744 \ubcf4\ud638\ud560 \uc218 \uc788\ub294 \uc544\ud0a4\ud14d\ucc98\ub77c\uace0 \ud55c\ub2e4. \uac1c\ub150\uc744 \uc798 \uc54c\uc9c0\ub294 \ubabb\ud574 \uc544\uc8fc \uc640\ub2ff\uc9c4 \uc54a\uc558\uc9c0\ub9cc, \uc5b4\ub5a4 \uac83\uc774 \uc7a5\uc810\uc778\uc9c0\ub97c \uc54c\uac8c\ub418\uc5b4\uc11c \uad81\uae08\ud558\ub2e4\ub294 \uc0dd\uac01\uc774 \ucee4\uc84c\ub2e4. \uacf5\ubd80\ud560\uac70 \ube68\ub9ac \ub2e4 \ub05d\ub0b4\uace0 \uc544\ud0a4\ud14d\ucc98\ucabd\ub3c4 \ud30c\ubcf4\uace0 \uc2f6\ub2e4..\\n\\n**\ud83d\udccc \uc131\ub2a5\uc801 \ubb38\uc81c - \ucf54\ub8e8\ud2f4 \uc801\uc6a9**\\n\\n\uc138\uac00\uc9c0\uac00 \uace0\ub824 \ub300\uc0c1\uc774\uc600\ub2e4\uace0 \ud55c\ub2e4.\\n\\n- Spring @Async\\n- Spring Webflux\\n- \ucf54\ub8e8\ud2f4\\n\\n\ucf54\ub8e8\ud2f4\uc744 \ucc44\ud0dd\ud558\uba74\uc11c Java\uc5d0\uc11c \ucf54\ud2c0\ub9b0\uc73c\ub85c\uc758 \ud3ec\ud305\uc791\uc5c5\ub3c4 \uc774\ub8e8\uc5b4\uc84c\ub2e4\uace0 \ud55c\ub2e4. \ucf54\ub8e8\ud2f4\uc740 \ub098\ub3c4 \uc548\ub4dc\ub85c\uc774\ub4dc \uacf5\ubd80\ud560\ub54c \uc811\ud55c \uc801\uc774 \uc788\ub294\ub370,, \uc81c\ub300\ub85c \uc0ac\uc6a9\ud574\ubcf8 \uc801\uc740 \uc5c6\ub2e4. \uba40\ud2f0\uc2a4\ub808\ub4dc\ub97c \ub2e4\ub8e8\uae30 \uc88b\ub2e4(?) \uc815\ub3c4\ub85c\ub9cc \uc54c\uace0 \uc788\uc5c8\ub2e4. \uc694\uc998 \ucf54\ud504\ub9c1\uc73c\ub85c \uac1c\ubc1c\ud558\ub294\uc0ac\ub78c\uc774 \ubd80\uca4d \ub9ce\uc544\uc9c4 \uac83 \uac19\uace0, \ud604\uc5c5\uc5d0\uc11c\ub3c4 \ub9ce\uc774 \uc4f0\uc774\ub294 \ub4ef \ud574\ubcf4\uc778\ub2e4. \uc77c\ub2e8 \uc790\ubc14\ub97c \uc815\ubcf5\ud558\uace0,, \ucf54\ud2c0\ub9b0\uc73c\ub85c\ub3c4 \ud574\ubcf4\uace0 \uc2f6\ub2e4.\\n\\n**\ud83d\udccc \uc774\uad00\uc2dc \uc548\uc815\uc131 \ud655\ubcf4**\\n\\n\uc880 \ub9ce\uc774 \uc0c8\ub85c\uc6e0\ub358 \ubd80\ubd84\uc774 \uc774\ubd80\ubd84\uc774\uc600\ub294\ub370, \uac70\uc758 \uc0c8\ub85c\uc6b4 \ud504\ub85c\uc81d\ud2b8\ub85c \uc62e\uae30\uba74\uc11c, \uadf8 \uc804\uc5d0 \uc218\ud589\ud588\ub358 \uae30\ub2a5\uc774 \uc798 \ub3d9\uc791\ud558\ub294\uac83\uc744 \uac80\uc99d\ud558\ub294\uac83\uc774 \uac00\uc7a5 \uc911\uc694\ud588\ub2e4\uace0 \ud55c\ub2e4. \uadf8\ub7ec\uba74\uc11c \uc801\uc6a9\ud55c \ubc29\ubc95\uc744 \uba87\uac00\uc9c0 \uc18c\uac1c\ud574\uc8fc\uc168\ub294\ub370, \uc544\uc8fc \uc0c8\ub85c\uc6e0\ub2e4.\\n\\n- \uc751\ub2f5 \ube44\uad50 \uc11c\ube44\uc2a4\\n    - \uae30\uc874 \uc11c\ube44\uc2a4\uc640 \uc2e0\uaddc \uc11c\ube44\uc2a4\uc5d0 \ub3d9\uc77c\ud55c \uc694\uccad\uc5d0\uc11c, \ub3d9\uc77c\ud55c \uc751\ub2f5\uc774 \uc624\ub294\uc9c0 \uac80\uc99d\ud558\ub294 \uc11c\ube44\uc2a4\ub97c \ub530\ub85c \ub9cc\ub4e4\uc5c8\ub2e4\uace0 \ud55c\ub2e4.\\n    - \uc870\ud68c \uc704\uc8fc\uc758 \uc11c\ube44\uc2a4\ub77c \uac00\ub2a5\ud55c \ub4ef \ud588\ub2e4.\\n    - \uae30\uc874 \uc11c\ube44\uc2a4\uc640 \uc2e0\uaddc \uc11c\ube44\uc2a4\uc758 \uc751\ub2f5\uc774 \uc77c\uce58\ud558\uc9c0 \uc54a\uc744 \ub54c, alert\ub97c \uc8fc\uba74 \ub85c\uadf8\ub97c \ud655\uc778\ud574\ubcf4\ub294 \ubc29\uc2dd\uc73c\ub85c \uc9c4\ud589\ud588\ub2e4\uace0 \ud55c\ub2e4.\\n- \ud45c\ubcf8 \uac80\uc0ac\\n    - \ubaa8\ub4e0 \uc11c\ube44\uc2a4\ub97c \ube44\uad50\ud558\uace0 \uac80\uc99d\ud558\uae30 \ud798\ub4e4\uc5b4\uc11c \uc791\uc740 \ube44\uc728\ubd80\ud130 \uc810\uc810 \ub298\ub824\ub098\uac14\ub2e4\uace0 \ud55c\ub2e4.\\n- A/B \ud2b8\ub798\ud53d \ubd84\uc0b0\\n    - A/B \ud14c\uc2a4\ud2b8\ub97c \ud558\ub4ef\uc774 \uc2e0\uaddc \uc11c\ube44\uc2a4 / \uae30\uc874 \uc11c\ube44\uc2a4\ub85c \ud2b8\ub798\ud53d\uc744 \ubd84\uc0b0\uc2dc\ucf30\ub2e4\uace0 \ud55c\ub2e4.\\n    - \ube44\uc728\uc740 \uc124\uc815\ub4f1\uc73c\ub85c \uc720\ub3d9\uc801\uc73c\ub85c \ubcc0\uacbd\ud560 \uc218 \uc788\uac8c \uad6c\uc131\ud574\uc11c, \ubb38\uc81c\uc0c1\ud669\uc5d0\uc11c\ub3c4 \ub300\ucc98 \uac00\ub2a5\ud558\uac8c\ub054 \uad6c\uc131\ud588\ub2e4\uace0 \ud55c\ub2e4.\\n\\n\uc774 \uc138\uc158\uc5d0\uc11c \uc774\uad00\uc5d0 \ub300\ud55c \ub0b4\uc6a9\uc774 \uac00\uc7a5 \uc0c8\ub85c\uc6e0\ub2e4. \uc774\ub7f0 \ubc29\ubc95\uc744 \uc0dd\uac01\ud574\ubcfc \uc218\ub3c4 \uc788\uad6c\ub098. \ub180\ub790\uc5c8\ub2e4.\\n\\n## \ub9c8\ubb34\ub9ac\\n\\n\uc0dd\uac01\ubcf4\ub2e4 \uc5c4\uccad \ud0c0\uc774\ud2b8\ud558\uac8c \uc9c4\ud589\ub418\uc5c8\ub2e4. \ub0b4\ub144\uc5d0 \ub610 \uac08 \uc218 \uc788\uac8c \ub418\uba74 \ubc25 \ub4e0\ub4e0\ud788 \uba39\uace0,, \ucee4\ud53c\ub3c4 \ucda9\ubd84\ud788 \ub9c8\uc2dc\uace0 \ucc38\uc5ec\ud574\uc57c\uaca0\ub2e4\uace0 \uc0dd\uac01\ud588\ub2e4 \u314b\u314b\\n\\n\ucc38\uac00\ud558\uae38 \uc815\ub9d0\uc815\ub9d0 \uc798\ud588\ub2e4\uace0 \uc0dd\uac01\uc774 \ub4e4\uc5c8\ub2e4. \uc9c0\uae08 \ub4e4\uc740 \ub0b4\uc6a9\uc744 \ubc14\ub85c \ud761\uc218\ud574\uc11c \uc2e4\ucc9c\ud558\uae34 \uc5b4\ub835\uaca0\uc9c0\ub9cc, \uac1c\ubc1c\uc5d0 \ub300\ud55c \ub098\uc758 \uc778\uc0ac\uc774\ud2b8\ub97c \uc815\ub9d0 \ub113\uac8c \ud655\uc7a5\uc2dc\ucf1c \uc904 \uc218 \uc788\ub294 \uc88b\uc740 \uae30\ud68c\uc600\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \uc628\ub77c\uc778\uc73c\ub85c\ub3c4 \uc774\ub7f0 \uc790\ub8cc\ub4e4\uc744 \ucda9\ubd84\ud788 \ub9ce\uc774 \uc811\ud560 \uc218 \uc788\uc9c0\ub9cc \ud604\uc7a5\uc5d0\uc11c \ub4e3\ub294 \uac83\uc740 \uc640\ub2ff\ub294 \uc815\ub3c4\uac00 \ub2e4\ub974\ub2e4\uace0 \ub290\uaf08\ub2e4.\\n\\n\uc624\ud504\ub77c\uc778 \ud589\uc0ac \ucc38\uc5ec\ub294 \ucc98\uc74c\uc774\uc600\ub294\ub370 \ub108\ubb34 \uc88b\uc558\ub358 \uae30\uc5b5\ubc16\uc5d0 \uc5c6\uc5b4\uc11c, \ub2e4\ub978 \uc88b\uc740 \ucc38\uc5ec \uae30\ud68c\uac00 \ub610 \uc788\ub2e4\uba74 \uc801\uadf9\uc801\uc73c\ub85c \ucc38\uc5ec\ud560 \uc0dd\uac01\uc774\ub2e4.\\n\\n\uc624\ub298\uc5d0 \ub300\ud55c \uac10\uc0c1\uc774 \ub2e4 \ub0a0\uc544\uac00\uae30\uc804\uc5d0 \uae09\ud558\uac8c \ud6c4\ub2e4\ub2e5 \uc801\uc5b4\ubcf4\uc558\ub2e4. 2025 \uc2a4\ud504\ub9c1 \ucea0\ud504\ub97c \uace0\ubbfc\ud558\uc2e0\ub2e4\uba74 \uaf2d \ucc38\uac00\ud574\ubcf4\uc2dc\ub77c\uace0 \uad8c\ud558\uace0 \uc2f6\ub2e4\ud83d\ude1a\\n\\n<br/>"},{"id":"/2024/05/22/java-new-string-encoding","metadata":{"permalink":"/blog/2024/05/22/java-new-string-encoding","source":"@site/blog/2024-05-22-java-new-string-encoding.md","title":"Java\uc5d0\uc11c new String()\uc73c\ub85c \uc778\ucf54\ub529\uc744 \ubcc0\ud658 \ud560 \uc218\ub294 \uc5c6\ub2e4.","description":"Java\uc5d0\uc11c new String()\uc73c\ub85c \uc778\ucf54\ub529\uc744 \ubcc0\ud658 \ud560 \uc218\ub294 \uc5c6\ub2e4.","date":"2024-05-22T00:00:00.000Z","formattedDate":"May 22, 2024","tags":[],"readingTime":6.215,"hasTruncateMarker":true,"authors":[],"frontMatter":{"title":"Java\uc5d0\uc11c new String()\uc73c\ub85c \uc778\ucf54\ub529\uc744 \ubcc0\ud658 \ud560 \uc218\ub294 \uc5c6\ub2e4.","description":"Java\uc5d0\uc11c new String()\uc73c\ub85c \uc778\ucf54\ub529\uc744 \ubcc0\ud658 \ud560 \uc218\ub294 \uc5c6\ub2e4.","hide_table_of_contents":false,"toc_max_heading_level":4},"unlisted":false,"prevItem":{"title":"SpringCamp 2024 \ud6c4\uae30 \ud83c\udf31","permalink":"/blog/2024/05/25/springcamp-2024"},"nextItem":{"title":"json\uc744 \ub2e4\ud615\uc131 \uc801\uc6a9\ub41c \ud074\ub798\uc2a4\ub85c \uc5ed\uc9c1\ub82c\ud654 \ud558\uae30","permalink":"/blog/2024/05/05/deserialize-polymorphism-subclasses"}},"content":"\ubb38\uc790\uc5f4 \uc778\ucf54\ub529\uc774 \uae68\uc9c0\ub294 \ubb38\uc81c\ub97c \ud574\uacb0\ud558\uba74\uc11c,\\n\uc790\ubc14\uc5d0\uc11c \ubb38\uc790\uc5f4 \uc778\ucf54\ub529 \ubcc0\ud658 \uc2dc \ud558\uae30 \uc26c\uc6b4 \uc2e4\uc218\uc5d0 \ub300\ud574 \uc815\ub9ac\ud574 \ubcf4\uc558\ub2e4.\\n\\n\x3c!-- truncate --\x3e\\n\\n## \ud83d\udccc \ubb38\uc81c \uc0c1\ud669 \uc815\ub9ac\\n\\n\uc5b4\ub5a4 \ud30c\uc77c\uc744 \uc77d\uc5b4, UTF-8\ub85c \ubcc0\ud658\ud55c \ud30c\uc77c\uc744 \uc0dd\uc131\ud558\ub294 \ubd80\ubd84\uc5d0\uc11c \uc544\ub798\uc640 \uac19\uc774 \ubb38\uc790\uc5f4\uc774 \uae68\uc9c0\ub294 \ud604\uc0c1\uc774 \ubc1c\uc0dd\ud588\ub2e4.\\n\\n```\\n\ufffd\u05fd\ufffd\u01ae \ufffd\u063a\ufffd\ufffd\ufffd! \ufffd\ufffd\ufffd\ufffd\ufffd\ufffd\\n```\\n\\n\ubb38\uc81c\uac00 \ub41c \ubd80\ubd84\uc740 \uc774\ubd80\ubd84 \uc774\uc600\ub2e4.\\n\\n```java\\nBufferedReader br = new BufferedReader(new InputStreamReader(new FileInputStream(filePath), \\"EUC-KR\\"));\\nBufferedWriter bw = Files.newBufferedWriter(Paths.get(newFilePath), StandardOpenOption.APPEND);\\nString line = \\"\\";\\nwhile((line = br.readLine()) != null)\\n{\\n    // \uc0dd\ub7b5\\n    bw.write(new String(line.getBytes(\\"EUC-KR\\"),\\"UTF-8\\"));\\n    bw.newLine();\\n}\\n```\\n\\n\uc608\ub97c\ub4e4\uc5b4 \ud30c\uc77c \uc778\ucf54\ub529\uc774 `EUC-KR`\uc774\ub77c\uace0 \ud588\uc744\ub54c \ub2e4\uc74c\uacfc \uac19\uc740 \uc77c\uc774 \uc77c\uc5b4\ub09c\ub2e4.\\n\\n1. EUC-KR\ub85c \ud30c\uc77c\uc744 \uc77d\ub294\ub2e4.\\n2. `br.readLine()` \uc73c\ub85c \uc77d\uc740 \ubb38\uc790\uc5f4(String)\uc5d0\uc11c EUC-KR \ubc14\uc774\ud2b8 \ubc30\uc5f4\uc744 \uc5bb\uace0, \uc774\ub97c `new String()` \uc73c\ub85c UTF-8 \uce90\ub9ad\ud130\uc14b\uc744 \uc9c0\uc815\ud574 String\uc744 \uc0dd\uc131\ud55c\ub2e4.\\n3. \ud30c\uc77c\uc5d0 \uc4f4\ub2e4.\\n\\n\uc774 \uacfc\uc815\uc5d0\uc11c \ubb38\uc790\uc5f4\uc740 100% \uae68\uc9c0\uac8c \ub41c\ub2e4.\\n\\n## \ud83d\udccc Java String \uc0dd\uc131\uc790\ub294 \uc778\ucf54\ub529\uc744 \'\ubcc0\ud658\'\ud558\uc9c0 \uc54a\ub294\ub2e4.\\n\\n\uc790\ubc14 \uc778\ucf54\ub529\uc744 \ubcc0\ud658\ud558\ub294 \ubc29\ubc95\uc744 \uad6c\uae00\uc5d0\uc11c \uac80\uc0c9\ud558\uba74\\n\\n```java\\nnew String(line.getBytes(\\"EUC-KR\\"),\\"UTF-8\\");\\n```\\n\\n\uc774\ub7f0 \ucf54\ub4dc\ub97c \uc885\uc885 \ubcfc \uc218 \uc788\ub2e4. String\uc758 \uc0dd\uc131\uc790\ub85c \ubc14\uc774\ud2b8 \ubc30\uc5f4\uacfc \uce90\ub9ad\ud130\uc14b\uc744 \ubc1b\ub294 \uc0dd\uc131\uc790\uac00 \uc788\uc5b4 \ub9c8\uce58 \ubcc0\ud658\uc774 \ub420\uac83\ub9cc \uac19\ub2e4.\\n\\n\ud558\uc9c0\ub9cc \uc774\ub294 \ud2c0\ub838\ub2e4.\\n\\n\uc815\ud655\ud558\uac8c \uc54c\ub824\uba74 \uc790\ubc14\uc758 String \ud074\ub798\uc2a4\ub97c \uc0b4\ud3b4\ubcf4\uc544\uc57c \ud55c\ub2e4.\\n\\n### \u2705 String \uc0dd\uc131\uc790 \uc0b4\ud3b4\ubcf4\uae30\\n\\n**String.java**\ub97c \ub72f\uc5b4\ubcf4\uba74, `byte[]`\ub97c \uc804\ub2ec\ud558\ub294 \uc0dd\uc131\uc790\ub4e4\uc740 \uacb0\uad6d\\n\\n```java\\nprivate String(Charset charset, byte[] bytes, int offset, int length){//}\\n```\\n\\n\ud568\uc218\ub97c \uac70\uce58\uac8c \ub428\uc744 \uc54c \uc218 \uc788\ub2e4.\\n\\n\uce90\ub9ad\ud130\uc14b\uc774 UTF_8, ISO_8859_1, US_ASCII\uc774 \uc544\ub2d0\uacbd\uc6b0 CharsetDecoder\ub85c \ub514\ucf54\ub529 \ud558\ub294\ub370,\\n\uc774\ub54c \uc8fc\uc5b4\uc9c4 \uce90\ub9ad\ud130\uc14b\uc73c\ub85c \ub514\ucf54\ub354\ub97c \uc0dd\uc131\ud558\uace0, \ub514\ucf54\ub529\ud55c\ub2e4. \ub2e4\ub978 \uce90\ub9ad\ud130\uc14b\uc73c\ub85c \uc778\ucf54\ub529\ub41c \ubc14\uc774\ud2b8\ubc30\uc5f4\uc744 \uc77d\uc73c\ub824\uace0 \ud558\uba74 \ub2f9\uc5f0\ud788 \uc815\uc0c1\uc801\uc73c\ub85c \uc218\ud589\ub418\uc9c0 \uc54a\ub294\ub2e4.\\n\\n\ub2e8\uc21c\ud788 \ud574\ub2f9 \uce90\ub9ad\ud130\uc14b\uc73c\ub85c \ub514\ucf54\ub529 \ud560 \ubfd0, \ubcc0\ud658\ud558\ub294 \uae30\ub2a5\uae4c\uc9c0 \uc218\ud589\ud558\uc9c0 \uc54a\ub294\ub2e4.\\n\\n\ub530\ub77c\uc11c \uce90\ub9ad\ud130\uc14b A\ub85c \uc778\ucf54\ub529\ub41c \ubc14\uc774\ud2b8 \ubc30\uc5f4\ub85c \ub9cc\ub4e4\uace0, \uc774\ub97c \uce90\ub9ad\ud130\uc14b B\ub85c \uc9c0\uc815\ud574 `new String(line.getBytes(A), B)` \ud558\uac8c \ub420 \uacbd\uc6b0, \uc790\ubc14\ub294 \ub4e4\uc5b4\uc628 \ubc14\uc774\ud2b8 \ubc30\uc5f4\uc744 \uce90\ub9ad\ud130\uc14b B\ub85c \uc77d\uc744 \uc218 \uc5c6\uc5b4, \uae68\uc9c4 \ubb38\uc790\uc5f4\uc774 \ub418\uc5b4\ubc84\ub9b0\ub2e4.\\n\\n### \u2705 \ud14c\uc2a4\ud2b8\\n\\n\ub208\uc73c\ub85c \ud655\uc778\ud574\ubcf4\uae30 \uc704\ud574\\n\\n- \uac01\uae30 \ub2e4\ub978 \uce90\ub9ad\ud130\uc14b\uc73c\ub85c \ubc14\uc774\ud2b8 \ubc30\uc5f4\uc744 \uc0dd\uc131\ud558\uace0\\n- \uc774\ub97c \ucd9c\ub825\ud558\uace0\\n- new String\uc73c\ub85c \ub2e4\ub978 \uce90\ub9ad\ud130\uc14b\uc744 \uc9c0\uc815\ud574 \ucd9c\ub825\ud574 \ubcf4\uc558\ub2e4.\\n\\n```java\\n String original = \\"\ud14c\uc2a4\ud2b8 \ubb38\uc790\uc5f4\\";\\n\\n // \uac01 \uc778\ucf54\ub529 \ubc29\uc2dd\uc73c\ub85c \ubc14\uc774\ud2b8 \ubc30\uc5f4 \uc0dd\uc131\\n byte[] ms949Bytes = original.getBytes(Charset.forName(\\"MS949\\"));\\n byte[] utf8Bytes = original.getBytes(StandardCharsets.UTF_8);\\n byte[] utf16Bytes = original.getBytes(StandardCharsets.UTF_16);\\n byte[] eucKrBytes = original.getBytes(Charset.forName(\\"EUC-KR\\"));\\n\\n // \ubc14\uc774\ud2b8 \ubc30\uc5f4 \ucd9c\ub825\\n System.out.println(\\"Original: \\" + original);\\n System.out.println(\\"MS949 Bytes: \\" + Arrays.toString(ms949Bytes));\\n System.out.println(\\"UTF-8 Bytes: \\" + Arrays.toString(utf8Bytes));\\n System.out.println(\\"UTF-16 Bytes: \\" + Arrays.toString(utf16Bytes));\\n System.out.println(\\"EUC-KR Bytes: \\" + Arrays.toString(eucKrBytes));\\n\\n // \uac01 \ubc14\uc774\ud2b8 \ubc30\uc5f4\uc744 \ub2e4\ub978 \uc778\ucf54\ub529\uc73c\ub85c \ub514\ucf54\ub529\\n String ms949ToUtf8 = new String(ms949Bytes, StandardCharsets.UTF_8);\\n String utf8ToMs949 = new String(utf8Bytes, Charset.forName(\\"MS949\\"));\\n String utf16ToEucKr = new String(utf16Bytes, Charset.forName(\\"EUC-KR\\"));\\n String eucKrToUtf16 = new String(eucKrBytes, StandardCharsets.UTF_16);\\n\\n // \uacb0\uacfc \ucd9c\ub825\\n System.out.println(\\"MS949 to UTF-8: \\" + ms949ToUtf8);\\n System.out.println(\\"UTF-8 to MS949: \\" + utf8ToMs949);\\n System.out.println(\\"UTF-16 to EUC-KR: \\" + utf16ToEucKr);\\n System.out.println(\\"EUC-KR to UTF-16: \\" + eucKrToUtf16);\\n```\\n\\n\uacb0\uacfc\ub294 \ub2e4\uc74c\uacfc \uac19\ub2e4.\\n\\n```\\nOriginal: \ud14c\uc2a4\ud2b8 \ubb38\uc790\uc5f4\\nMS949 Bytes: [-59, -41, -67, -70, -58, -82, 32, -71, -82, -64, -38, -65, -83]\\nUTF-8 Bytes: [-19, -123, -116, -20, -118, -92, -19, -118, -72, 32, -21, -84, -72, -20, -98, -112, -20, -105, -76]\\nUTF-16 Bytes: [-2, -1, -47, 76, -62, -92, -46, -72, 0, 32, -69, 56, -57, -112, -59, -12]\\nEUC-KR Bytes: [-59, -41, -67, -70, -58, -82, 32, -71, -82, -64, -38, -65, -83]\\nMS949 to UTF-8: \ufffd\u05fd\ufffd\u01ae \ufffd\ufffd\ufffd\u06bf\ufffd\\nUTF-8 to MS949: \ufffd\ub012\ufffd\ub4aa\ufffd\ub4c3 \u81fe\uba84\uc604\ufffd\ubff4\\nUTF-16 to EUC-KR: \ufffd\ufffd\ufffdL\uc9da\u606c  \ufffd8\ufffd\ud210\\nEUC-KR to UTF-16: \uc5d7\ubdba\uc6ae\u20b9\uaec0\ufffd\\n```\\n\\n\uc774\ub85c\uc368 `new String()` \uc0dd\uc131\uc790\ub85c \ubb38\uc790\uc5f4 \uc778\ucf54\ub529\uc744 \ubcc0\ud658\ud560 \uc218 \uc5c6\ub2e4\ub294 \uc0ac\uc2e4\uc744 \ud655\uc778\ud588\ub2e4.\\n\\n## \ud83d\udccc \ud55c\uae00\uc744 \uc778\ucf54\ub529/\ub514\ucf54\ub529 \ud560\ub550 \uc8fc\uc758\ud558\uc790.\\n\\nnew String()\uc73c\ub85c \uc798\ubabb\uc744 \ud574\ub3c4 \uc601\uc5b4\ub294 \ub300\ubd80\ubd84\uc758 \uce90\ub9ad\ud130\uc14b\uc774 1\ubc14\uc774\ud2b8 \uc774\uae30 \ub54c\ubb38\uc5d0 \ubb38\uc81c\uac00 \ub420 \uc77c\uc774 \uac70\uc758 \uc5c6\ub2e4.\\n\\n\ud558\uc9c0\ub9cc \ud55c\uae00\uc758 \uacbd\uc6b0 1\uc5d0\uc11c 4\ubc14\uc774\ud2b8\uae4c\uc9c0 \uac00\ubcc0\ud615\uc73c\ub85c \uc800\uc7a5\ub418\uae30 \ub54c\ubb38\uc5d0 \ub9de\ub294 \uce90\ub9ad\ud130\uc14b\uc744 \uc9c0\uc815\ud574\uc57c \ud55c\ub2e4.\\n\\n## \ud83d\udccc \uc218\uc815\ud55c \ucf54\ub4dc\\n\\n\ubb38\uc81c\uac00 \ub41c \ucf54\ub4dc\ub97c \uc544\ub798\uc640 \uac19\uc774 \uc218\uc815\ud558\uba74 \ub41c\ub2e4.\\n\\n```java\\nBufferedReader br = new BufferedReader(new InputStreamReader(new FileInputStream(filePath), \\"EUC-KR\\"));\\nBufferedWriter bw = Files.newBufferedWriter(Paths.get(filePath),StandardCharsets.UTF_8, StandardOpenOption.APPEND);\\nString line = \\"\\";\\nwhile((line = br.readLine()) != null)\\n{\\n    // \uc0dd\ub7b5\\n    bw.write(line);\\n    bw.newLine();\\n}\\n```\\n\\n- BufferedWriter \uc790\uccb4\ub97c utf-8\ub85c \uc9c0\uc815\ud558\uc5ec \uc0dd\uc131\ud55c\ub2e4.\\n- \ub530\ub85c \uc77d\uc740 String\uc744 byte array\ub85c \ubcc0\ud658\ud558\uc5ec \uc778\ucf54\ub529\uc744 \ubcc0\ud658\ud558\ub294 \uc791\uc5c5 \uc5c6\uc774, \ubc14\ub85c \uc4f4\ub2e4.\\n\\nnew String\uc73c\ub85c \uc778\ucf54\ub529\uc744 \ubcc0\ud658\ud558\ub824\uba74 \uc774\ub807\uac8c \ud558\uba74 \ub41c\ub2e4.\\n\\n```java\\nbyte[] ms949Bytes = original.getBytes(Charset.forName(\\"MS949\\"));\\nString ms949String = new String(ms949Bytes, \\"MS949\\");\\nbyte[] utf8Bytes = ms949String.getBytes(StandardCharsets.UTF_8);\\nString utf9String = new String(utf8Bytes, StandardCharsets.UTF_8);\\n```\\n\\n## \ud83d\udccc \uc815\ub9ac\\n\\n- \uc790\ubc14\uc758 String \uc0dd\uc131\uc790\ub294 \uc778\ucf54\ub529\uc744 \ubcc0\ud658\ud558\ub294 \uc5ed\ud560\uc744 \ud558\uc9c0 \uc54a\ub294\ub2e4.\\n\\n## \ud83d\udccc \ucc38\uace0\uc790\ub8cc\\n\\n- [Java Character Set\uc758 \uc774\ud574](https://kin.naver.com/knowhow/detail.nhn?docId=527939)"},{"id":"/2024/05/05/deserialize-polymorphism-subclasses","metadata":{"permalink":"/blog/2024/05/05/deserialize-polymorphism-subclasses","source":"@site/blog/2024-05-05-deserialize-polymorphism-subclasses.md","title":"json\uc744 \ub2e4\ud615\uc131 \uc801\uc6a9\ub41c \ud074\ub798\uc2a4\ub85c \uc5ed\uc9c1\ub82c\ud654 \ud558\uae30","description":"jackson\uc73c\ub85c json \uc5ed\uc9c1\ub82c\ud654\uc2dc \ud544\ub4dc \uac12\uc73c\ub85c \ub9e4\ud551 \ud0c0\uc785\uc744 \uacb0\uc815\ud558\ub294 \ubc29\ubc95\uc744 \uc54c\uc544\ubcf8\ub2e4.","date":"2024-05-05T00:00:00.000Z","formattedDate":"May 5, 2024","tags":[],"readingTime":5.47,"hasTruncateMarker":true,"authors":[],"frontMatter":{"title":"json\uc744 \ub2e4\ud615\uc131 \uc801\uc6a9\ub41c \ud074\ub798\uc2a4\ub85c \uc5ed\uc9c1\ub82c\ud654 \ud558\uae30","description":"jackson\uc73c\ub85c json \uc5ed\uc9c1\ub82c\ud654\uc2dc \ud544\ub4dc \uac12\uc73c\ub85c \ub9e4\ud551 \ud0c0\uc785\uc744 \uacb0\uc815\ud558\ub294 \ubc29\ubc95\uc744 \uc54c\uc544\ubcf8\ub2e4.","hide_table_of_contents":false,"toc_max_heading_level":4},"unlisted":false,"prevItem":{"title":"Java\uc5d0\uc11c new String()\uc73c\ub85c \uc778\ucf54\ub529\uc744 \ubcc0\ud658 \ud560 \uc218\ub294 \uc5c6\ub2e4.","permalink":"/blog/2024/05/22/java-new-string-encoding"},"nextItem":{"title":"Process.waitFor() \uc774 \uc601\uc6d0\ud788 \ub05d\ub098\uc9c0 \uc54a\ub294\ub2e4.","permalink":"/blog/2024/04/08/Process.waitfor-dealock-occur"}},"content":"\ub2e4\uc591\ud55c json \uad6c\uc870\ub97c \ub2e4\ud615\uc131\uc744 \uc801\uc6a9\ud55c (\uc778\ud130\ud398\uc774\uc2a4, \uc0c1\uc18d ...) \uc790\ubc14 \uac1d\uccb4\ub85c \uc5ed\uc9c1\ub82c\ud654 \ud558\uae30!\\n\\n\x3c!-- truncate --\x3e\\n\\n:::info\\n\\n\uc774\ud574\ub97c \ub3d5\uae30 \uc704\ud574 \uc800\uc758 \uc0c1\ud669\uc744 \uc124\uba85\ud574 \ub193\uc558\uc73c\ub098, \uc774 \uacbd\uc6b0\uac00 \uc544\ub2c8\ub354\ub77c\ub3c4 **Jackson**\uc744 \uc0ac\uc6a9\ud558\ub294 \ubaa8\ub4e0 \uacf3\uc5d0 \uc801\uc6a9 \uac00\ub2a5\ud55c \ubc29\ubc95\uc785\ub2c8\ub2e4. :)\\n\\n:::\\n\\n## \ud074\ub798\uc2a4 \uad6c\uc870\\n\\nopensearch(\u2252elasticsearch) \uc778\ub371\uc2a4 \ub9e4\ud551\uc774 dynamic\uc73c\ub85c \ub418\uc5b4 \uc788\uace0, type\ubcc4\ub85c json \uad6c\uc870\uac00 \ub2ec\ub790\uc2b5\ub2c8\ub2e4. \uae30\uc874 \ucf54\ub4dc\ub294 HashMap\uc73c\ub85c \uc77d\uc740 \ub2e4\uc74c `.get().get()...` \ud558\ub294 \uad6c\uc870\ub85c \ub418\uc5b4 \uc788\uc5c8\uc2b5\ub2c8\ub2e4\ud83e\udd72.\\n\\n\ud574\uacb0\ubc95\uc774 \uc788\uc744 \uac70\ub77c \uad73\uac8c \ubbff\uace0, draw.io\ub97c \ucf1c \uc57c\uc2ec\ucc28\uac8c \uc778\ud130\ud398\uc774\uc2a4\uc640 \ucd94\uc0c1\ud074\ub798\uc2a4\ub85c \uc544\ub798 \uad6c\uc870\ub85c \ucd94\uc0c1\ud654\ub97c \ud574\ubcf4\uc558\uc2b5\ub2c8\ub2e4. (\ud68c\uc0ac \ucf54\ub4dc\ub97c \uac00\uc838\uc62c \uc218 \uc5c6\uc5b4 \ud074\ub798\uc2a4 \uc774\ub984\uc740 \uc784\uc758\ub85c \uc815\ud588\uc2b5\ub2c8\ub2e4.)\\n\\n![](../static/img/post-img/20240505112503.png)\\n\\n`opensearch-java` \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \uc0ac\uc6a9\ud574\uc11c \ub2e4\uc74c\uacfc \uac19\uc774 response\ub97c \uc9c0\uc815\ud55c \ud074\ub798\uc2a4\ub85c \uc5ed\uc9c1\ub82c\ud654 \ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n```java\\nSearchResponse<IndexData> searchResponse = client.search(s -> s.index(index), IndexData.class);\\n```\\n\\nopensearch-java\ub294 \ub0b4\ubd80\uc801\uc73c\ub85c **jackson** \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \uc0ac\uc6a9\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n\ud574\uacb0\ubc95\uc740 \uc758\uc678\ub85c \uac04\ub2e8\ud588\ub294\ub370\uc694, Jackson\uc5d0\uc11c \uc81c\uacf5\ud558\ub294 \uc5b4\ub178\ud14c\uc774\uc158\uc73c\ub85c \uc11c\ube0c \ud0c0\uc785\uc744 \ub9e4\ud551\ud558\uba74 \ub429\ub2c8\ub2e4.\\n\\n\\n## \ub9e4\ud551 \ud0c0\uc785\uc744 \uc9c0\uc815\ud558\uc9c0 \uc54a\uc558\uc744 \ub54c\\n\\n\uc77c\ub2e8 \uc544\ubb34\ub7f0 \uc870\uce58\ub97c \ud558\uc9c0 \uc54a\uc558\uc744 \ub54c, \uc5b4\ub5a4 \uc77c\uc774 \uc77c\uc5b4\ub098\ub294\uc9c0 \uc0b4\ud3b4\ubd05\uc2dc\ub2e4. \uc544\ub798\uc640 \uac19\uc740 json\uc744 Document.class\ub85c \uc5ed\uc9c1\ub82c\ud654 \ud574\ubcf4\uaca0\uc2b5\ub2c8\ub2e4.\\n\\n```json\\n{\\n  \\"policies\\": [\\n    {\\n      \\"type\\": \\"a\\"\\n    },\\n    {\\n      \\"type\\": \\"b\\"\\n    },\\n    {\\n      \\"type\\": \\"c\\"\\n    }\\n  ]\\n}\\n```\\n\\n```java\\nObjectMapper objectMapper = new ObjectMapper();\\nString json = \\"{\\\\\\"policies\\\\\\":[{\\\\\\"type\\\\\\":\\\\\\"a\\\\\\"},{\\\\\\"type\\\\\\":\\\\\\"b\\\\\\"},{\\\\\\"type\\\\\\":\\\\\\"c\\\\\\"}]}\\";\\nDocument document = objectMapper.readValue(json, Document.class);\\n```\\n\\n```java\\ncom.fasterxml.jackson.databind.exc.InvalidDefinitionException: Cannot construct instance of `hello.jsontest.classes.Policy` (no Creators, like default constructor, exist): abstract types either need to be mapped to concrete types, have custom deserializer, or contain additional type information\\n```\\n\\n`InvalidDefinitionException` \uc774 \ud130\uc9d1\ub2c8\ub2e4. \uc815\ud655\ud55c \ud0c0\uc785\uc744 \uc9c0\uc815\ud574\uc8fc\uac70\ub098 \ucd94\uac00 \uc815\ubcf4\ub97c \uc81c\uacf5\ud574\uc8fc\ub77c\uace0 \ud558\ub124\uc694.\\n\\nJackson\uc774 \uc81c\uacf5\ud558\ub294 \uc5b4\ub178\ud14c\uc774\uc158\uc73c\ub85c \ud0c0\uc785\uc744 \uc9c0\uc815\ud558\ub294 \ubc29\ubc95\uc744 \uc54c\uc544\ubd05\uc2dc\ub2e4.\\n\\n## @JsonTypeInfo\uacfc @JsonSubTypes\\n\\n`@JsonTypeInfo`\ub294 \uc11c\ube0c \ud0c0\uc785\uc744 \uc5b4\ub5a4 \uc815\ubcf4\ub97c \uc0ac\uc6a9\ud574 \uc9c0\uc815\ud560 \uac83\uc778\uc9c0 \uc815\ud574\uc8fc\ub294 \uc5b4\ub178\ud14c\uc774\uc158,<br/>\\n`@JsonSubTypes`\ub294 \uc2e4\uc81c \uc11c\ube0c\ud0c0\uc785\uc744 \uc5b4\ub5a4 \ud074\ub798\uc2a4\ub85c \uc9c0\uc815\ud560 \uac83\uc778\uc9c0 \uc815\ud574\uc8fc\ub294 \uc5b4\ub178\ud14c\uc774\uc158 \uc785\ub2c8\ub2e4.\\n\\n\uc704 \ud074\ub798\uc2a4 \uad6c\uc870\uc5d0\uc11c, Policy \ud074\ub798\uc2a4\uc5d0 \ub9e4\ud551 \uc815\ubcf4\ub97c \uc815\uc758\ud558\ub824\uba74 \uc774\ub807\uac8c \ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n```java\\n@JsonTypeInfo(use = Id.NAME, include = As.EXISTING_PROPERTY, property = \\"type\\")\\n@JsonSubTypes({\\n        @JsonSubTypes.Type(value = APolicy.class, name = \\"a\\"),\\n        @JsonSubTypes.Type(value = BPolicy.class, name = \\"b\\"),\\n        @JsonSubTypes.Type(value = CPolicy.class, name = \\"c\\")\\n})\\npublic interface Policy {\\n}\\n```\\n\\n```java\\nObjectMapper objectMapper = new ObjectMapper();\\nString json = \\"{\\\\\\"policies\\\\\\":[{\\\\\\"type\\\\\\":\\\\\\"a\\\\\\"},{\\\\\\"type\\\\\\":\\\\\\"b\\\\\\"},{\\\\\\"type\\\\\\":\\\\\\"c\\\\\\"}]}\\";\\nDocument document = objectMapper.readValue(json, Document.class);\\nSystem.out.println(\\"document.getPolicies().get(0).getClass() = \\" + document.getPolicies().get(0).getClass());\\nSystem.out.println(\\"document.getPolicies().get(1).getClass() = \\" + document.getPolicies().get(1).getClass());\\nSystem.out.println(\\"document.getPolicies().get(2).getClass() = \\" + document.getPolicies().get(2).getClass());\\n```\\n\\n\uc704 \ucf54\ub4dc\ub97c \uc2e4\ud589\ud558\uba74 \ub2e4\uc74c\uacfc \uac19\uc740 \uacb0\uacfc\ub97c \uc5bb\uc744 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n```java\\ndocument.getPolicies().get(0).getClass() = class hello.jsontest.classes.APolicy\\ndocument.getPolicies().get(1).getClass() = class hello.jsontest.classes.BPolicy\\ndocument.getPolicies().get(2).getClass() = class hello.jsontest.classes.CPolicy\\n```\\n\\n\uc758\ub3c4\ub41c \ub300\ub85c \uc5ed\uc9c1\ub82c\ud654\uac00 \uc798 \ub41c\uac83\uc744 \ubcfc \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ud83d\udc4d\\n\\n\uc704\uc5d0 \uc801\uc6a9\ub41c \uc635\uc158\uc5d0 \ub300\ud55c \uc124\uba85\uc785\ub2c8\ub2e4.\\n\\n- **@JsonTypeInfo**\\n  - name\uc774\ub77c\ub294 \ud2b9\uc815 property\ub97c \uc9c0\uc815\ud574 \ud0c0\uc785 \uad6c\ubd84\\n  - \uc774\ubbf8 \uc874\uc7ac\ud558\ub294 \\"type\\"\uc774\ub77c\ub294 \ud544\ub4dc\ub85c \ud0c0\uc785\uc744 \uad6c\ubd84\ud569\ub2c8\ub2e4.\\n- **@JsonSubTypes**\\n  - \\"a\\"\ub77c\ub294 name\uc740 APolicy.class\uc5d0, \\"b\\"\ub77c\ub294 name\uc740 BPolicy.class\uc5d0\\n  - \uc640 \uac19\uc740 \uad6c\ud604 \uc815\ubcf4\ub97c \ub9e4\ud551\ud574\uc90d\ub2c8\ub2e4.\\n\\n\ub530\ub77c\uc11c Jackson\uc740 \\"type\\"\ud544\ub4dc \uac12\uc774 a\ub77c\uba74 APolicy, b\ub77c\uba74 BPolicy\ub97c \ub9e4\ud551\ud560 \uc218 \uc788\uac8c \ub41c \uac83\uc785\ub2c8\ub2e4.\\n\\n`use`\uc5d0\ub294 \uc138 \uc635\uc158\uc744 \uc0ac\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n- **Id.CLASS**: \ud074\ub798\uc2a4 \uba85\uc73c\ub85c \uc11c\ube0c\ud0c0\uc785\uc744 \uad6c\ubd84\ud558\uaca0\ub2e4\ub294 \uc758\ubbf8\uc785\ub2c8\ub2e4.\\n  - \uc9c1\ub82c\ud654\uc2dc class \uba85\uc774 json\uc5d0 \ub0a8\uac8c \ub429\ub2c8\ub2e4.\\n- **Id.NAME**: \uc704 \uc608\uc2dc\ucc98\ub7fc, \ud2b9\uc815 \ud504\ub85c\ud37c\ud2f0\ub97c name\uc73c\ub85c \uc9c0\uc815\ud574 \uad6c\ubd84\ud569\ub2c8\ub2e4.\\n  - include = As.PROPERTY\\n    - \uc9c1\ub82c\ud654 \uc2dc type \uc815\ubcf4\uac00 \uc0dd\uc131\ub429\ub2c8\ub2e4.\\n  - include = As.EXISTING_PROPERTY\\n    - \uc9c1\ub82c\ud654 \uc2dc \uae30\uc874 \ud544\ub4dc\ub97c \uc0ac\uc6a9\ud569\ub2c8\ub2e4. (\uc0c8\ub85c\uc6b4 \ud544\ub4dc\uac00 \uc0dd\uc131\ub418\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4)\\n- **Id.DEDUCTION**: \uac01\uac01\uc758 \uc11c\ube0c \ud0c0\uc785\uc5d0\uc11c \uad6c\ubd84\ud560\ub9cc\ud55c \ud544\ub4dc\uac00 \uc788\ub2e4\uba74, jackson\uc5d0\uc11c \ucd94\ub860\ud574 \ubcc0\ud658\ud574\uc8fc\ub294 \uc124\uc815\uc785\ub2c8\ub2e4.\\n\\n\uac01\uac01\uc758 \uc0c1\ud669\uc5d0 \ub9de\uac8c \uc801\uc6a9\ud574 \uc8fc\uba74 \ub418\uaca0\uc2b5\ub2c8\ub2e4 :)\\n\\n## \ub9c8\uce58\uba70\\n\\n\uae30\uc874\uc758 HashMap\uc744 \uc774\uc6a9\ud55c \ucf54\ub4dc\uc5d0\uc11c, \uc790\ubc14\ub2f5\uac8c \ub2e4\ud615\uc131\uc744 \uc720\uc9c0\ud558\uba70 \uc5ed\uc9c1\ub82c\ud654\ub97c \ud560 \uc218 \uc788\uac8c \ub418\uc5b4\uc11c, \ub354\uc6b1 \uae54\ub054\ud558\uace0 \uc720\uc5f0\ud55c \ucf54\ub4dc\ub97c \uc791\uc131\ud560 \uc218 \uc788\uac8c \ub418\uc5c8\uc2b5\ub2c8\ub2e4. \uc5ed\uc2dc \uc548\ub418\ub294 \uac83\uc740 \uc5c6\ub124\uc694..\ud83d\ude1a"},{"id":"/2024/04/08/Process.waitfor-dealock-occur","metadata":{"permalink":"/blog/2024/04/08/Process.waitfor-dealock-occur","source":"@site/blog/2024-04-08-Process.waitfor-dealock-occur.md","title":"Process.waitFor() \uc774 \uc601\uc6d0\ud788 \ub05d\ub098\uc9c0 \uc54a\ub294\ub2e4.","description":"\uc790\ubc14\uc5d0\uc11c \uc678\ubd80 \ud504\ub85c\uc138\uc2a4\uc5d0 exec() \uba85\ub839\uc744 \uc804\ub2ec\ud558\uace0, waitFor() \uba54\uc11c\ub4dc \uc0ac\uc6a9\uc2dc \ud589\uc774 \uac78\ub9ac\ub294 \uc774\uc288","date":"2024-04-08T00:00:00.000Z","formattedDate":"April 8, 2024","tags":[],"readingTime":7.33,"hasTruncateMarker":true,"authors":[],"frontMatter":{"title":"Process.waitFor() \uc774 \uc601\uc6d0\ud788 \ub05d\ub098\uc9c0 \uc54a\ub294\ub2e4.","description":"\uc790\ubc14\uc5d0\uc11c \uc678\ubd80 \ud504\ub85c\uc138\uc2a4\uc5d0 exec() \uba85\ub839\uc744 \uc804\ub2ec\ud558\uace0, waitFor() \uba54\uc11c\ub4dc \uc0ac\uc6a9\uc2dc \ud589\uc774 \uac78\ub9ac\ub294 \uc774\uc288","hide_table_of_contents":false,"toc_max_heading_level":4},"unlisted":false,"prevItem":{"title":"json\uc744 \ub2e4\ud615\uc131 \uc801\uc6a9\ub41c \ud074\ub798\uc2a4\ub85c \uc5ed\uc9c1\ub82c\ud654 \ud558\uae30","permalink":"/blog/2024/05/05/deserialize-polymorphism-subclasses"},"nextItem":{"title":"Spring REST Docs + OAS \uc801\uc6a9\uae30","permalink":"/blog/2024/03/30/springrestdocs-openapi3"}},"content":"Java\uc5d0\uc11c command\ub97c \uc2e4\ud589\ud558\ub294 \ubd80\ubd84\uc5d0\uc11c, \ud589\uc774 \uac78\ub9ac\ub294 \uc774\uc288\uac00 \ubc1c\uc0dd\ud588\ub2e4. \uc544\ubb34 \uc624\ub958\ub3c4 \ub098\ud0c0\ub098\uc9c0 \uc54a\uace0 \uc2dc\uc2a4\ud15c\uc774 \ud589\uc774 \uac78\ub824\ubc84\ub838\ub2e4.\\n\\n\x3c!-- truncate --\x3e\\n\\n## \ubb38\uc81c \uc0c1\ud669\\n\\n```java\\nProcess process = Runtime.getRuntime().exec(command);\\nprocess.waitFor(); // \uc5ec\uae30\uac00 \ubb38\uc81c\\n```\\n\\n`waitFor()` \uac00 \ub05d\ub098\uc9c0 \uc54a\ub294 \ubb38\uc81c\uac00 \uc788\uc5c8\ub2e4.\\n\\n## \uc6d0\uc778\\n\\n### \ubb38\uc81c\uac00 \ub41c \ubd80\ubd84\\n\\n\uc6d0\uc778\uc740 \uc0dd\uac01\ubcf4\ub2e4 \uae08\ubc29 \ucc3e\uc744 \uc218 \uc788\uc5c8\ub2e4. \uc720\uba85\ud55c \uc774\uc288\uc600\ub2e4..! [JavaDoc](https://docs.oracle.com/javase/8/docs/api/java/lang/Process.html)\uc5d0\ub3c4 \uc774\ub7f0 \ub0b4\uc6a9\uc774 \uc788\ub2e4.\\n\\n:::info\\n\\n> Because some native platforms only provide limited buffer size for standard input and output streams, failure to promptly write the input stream or read the output stream of the subprocess may cause the subprocess to block, or even deadlock.\\n\\n\uc77c\ubd80 \ub124\uc774\ud2f0\ube0c \ud50c\ub7ab\ud3fc\uc740 \ud45c\uc900 \uc785\ub825 \ubc0f \ucd9c\ub825 \uc2a4\ud2b8\ub9bc\uc5d0 \ub300\ud574 \uc81c\ud55c\ub41c \ubc84\ud37c \ud06c\uae30\ub9cc\uc744 \uc81c\uacf5\ud558\uae30 \ub54c\ubb38\uc5d0, \uc785\ub825 \uc2a4\ud2b8\ub9bc\uc744 \uc989\uc2dc \uc791\uc131\ud558\uac70\ub098 \ud558\uc704 \ud504\ub85c\uc138\uc2a4\uc758 \ucd9c\ub825 \uc2a4\ud2b8\ub9bc\uc744 \uc77d\uc9c0 \uc54a\uc73c\uba74 \ud558\uc704 \ud504\ub85c\uc138\uc2a4\uac00 \ucc28\ub2e8\ub418\uac70\ub098 \uc2ec\uc9c0\uc5b4 \uad50\ucc29 \uc0c1\ud0dc\uc5d0 \ube60\uc9c8 \uc218 \uc788\ub2e4.\\n:::\\n\\nProcess \ud074\ub798\uc2a4\ub97c \ud1b5\ud574 \uc11c\ube0c \ud504\ub85c\uc138\uc2a4\ub97c \uc0dd\uc131\ud558\uace0, `exec()` \uc73c\ub85c \uc2e4\ud589\ud55c\ub2e4. \ud558\uc704\ud504\ub85c\uc138\uc2a4\ub294 Process \ud074\ub798\uc2a4\uc758 `getInputStream()`\uacfc `getErrorStream()` \uba54\uc11c\ub4dc\ub97c \ud1b5\ud574 \ucd9c\ub825 \uba54\uc2dc\uc9c0\ub97c \ubd80\ubaa8 \ud504\ub85c\uc138\uc2a4\uc5d0 \ubcf4\ub0b8\ub2e4. \uadf8\ub9ac\uace0 \ubb38\uc81c\uac00 \ub41c \ucf54\ub4dc\ub294 \uc544\ub798 \ubc29\uc2dd\uc73c\ub85c \ucd9c\ub825\uacfc \uc624\ub958\ub97c **\uc21c\ucc28\uc801**\uc73c\ub85c \uc77d\uace0 \ucc98\ub9ac\ud55c\ub2e4.\\n\\n```java\\nnew BufferedReader(new InputStreamReader(process.getInputStream(), \\"UTF-8\\"));\\n// \ub85c\uadf8\uc5d0 \uc4f0\ub294 \uc791\uc5c5 ..\\nnew BufferedReader(new InputStreamReader(process.getErrorStream(), \\"UTF-8\\"));\\n// \ub85c\uadf8\uc5d0 \uc4f0\ub294 \uc791\uc5c5 ..\\n```\\n\\n\uadf8\ub7f0\ub370 \uc774\ub54c \ub9cc\uc57d \uc2e4\ud589\ub418\ub294 \ud504\ub85c\uc138\uc2a4\uac00 \ub9ce\uc740 \uc591\uc758 \ucd9c\ub825\uc744 \uc0dd\uc131\ud558\uace0, \uc774\ub97c \ube60\ub974\uac8c \uc77d\uc5b4\ub4e4\uc774\uc9c0 \uc54a\uc73c\uba74 \ubc84\ud37c\uac00 \uac00\ub4dd \ucc28\uc11c \ud504\ub85c\uc138\uc2a4\uac00 \ube14\ub85d\ub420 \uc218 \uc788\ub2e4.\\n\\n\ub370\ub4dc\ub77d\uc774 \ubc1c\uc0dd\ud558\ub294 \uc0c1\ud669\uc740 \ub2e4\uc74c\uacfc \uac19\ub2e4.\\n\\n### \ub370\ub4dc\ub77d\uc774 \ubc1c\uc0dd\ud558\ub294 \uc0c1\ud669\\n\\n1. **\uc678\ubd80 \ud504\ub85c\uc138\uc2a4 \uc2e4\ud589**: \uc790\ubc14\uc5d0\uc11c `Runtime.exec()` \ub610\ub294 `ProcessBuilder.start()`\ub97c \uc0ac\uc6a9\ud558\uc5ec \uc678\ubd80 \ud504\ub85c\uc138\uc2a4\ub97c \uc2e4\ud589, \uc790\ubc14 \uc560\ud50c\ub9ac\ucf00\uc774\uc158(\ubd80\ubaa8 \ud504\ub85c\uc138\uc2a4)\uacfc \uc678\ubd80 \ud504\ub85c\uc138\uc2a4(\uc790\uc2dd \ud504\ub85c\uc138\uc2a4) \uc0ac\uc774\uc5d0\ub294 \ud45c\uc900 \uc785\ub825, \ucd9c\ub825, \uc624\ub958 \uc2a4\ud2b8\ub9bc\uc744 \ud1b5\ud55c \ub370\uc774\ud130 \uad50\ud658\uc774 \uac00\ub2a5\ud574\uc9c4\ub2e4.\\n2. **\ub370\uc774\ud130 \uc4f0\uae30 \ubc0f \uc77d\uae30 \uc2dc\uc791**: \uc790\ubc14 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc740 \uc678\ubd80 \ud504\ub85c\uc138\uc2a4\uc758 \ud45c\uc900 \ucd9c\ub825\uacfc \ud45c\uc900 \uc624\ub958 \uc2a4\ud2b8\ub9bc\uc744 \uc77d\uae30 \uc2dc\uc791\ud55c\ub2e4. \ub3d9\uc2dc\uc5d0 \uc678\ubd80 \ud504\ub85c\uc138\uc2a4\ub294 \uc2e4\ud589 \uacb0\uacfc\ub97c \ud45c\uc900 \ucd9c\ub825 \ubc0f \ud45c\uc900 \uc624\ub958 \uc2a4\ud2b8\ub9bc\uc73c\ub85c \ucd9c\ub825\ud558\uae30 \uc2dc\uc791\ud560 \uc218 \uc788\ub2e4.\\n3. **\ubc84\ud37c \uac00\ub4dd \ucc38**: \uc678\ubd80 \ud504\ub85c\uc138\uc2a4\uac00 \ub9ce\uc740 \uc591\uc758 \ub370\uc774\ud130\ub97c \ube60\ub974\uac8c \ucd9c\ub825\ud558\uba74 \ud45c\uc900 \ucd9c\ub825 \ub610\ub294 \ud45c\uc900 \uc624\ub958 \uc2a4\ud2b8\ub9bc\uc758 \ubc84\ud37c\uac00 \uac00\ub4dd \ucc2c\ub2e4. \uc774 \uc0c1\ud0dc\uc5d0\uc11c \uc790\ubc14 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc774 \ubc84\ud37c\uc5d0\uc11c \ub370\uc774\ud130\ub97c \ube68\ub9ac \uc77d\uc9c0 \uc54a\uc73c\uba74 \uc678\ubd80 \ud504\ub85c\uc138\uc2a4\ub294 \ubc84\ud37c\uc5d0 \ub370\uc774\ud130\ub97c \ub354\uc774\uc0c1 \uc4f8 \uc218 \uc5c6\uac8c \ub41c\ub2e4.\\n4. **\uc790\ubc14 \uc560\ud50c\ub9ac\ucf00\uc774\uc158 \ucc98\ub9ac \uc9c0\uc5f0**: \ub9cc\uc57d \uc790\ubc14 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc774 \ud45c\uc900 \ucd9c\ub825\ub9cc \uc77d\uace0 \ud45c\uc900 \uc624\ub958\ub294 \ub098\uc911\uc5d0 \uc77d\uc73c\ub824\uace0 \ud55c\ub2e4\uba74, \ud45c\uc900 \uc624\ub958\uc758 \ubc84\ud37c\uac00 \uac00\ub4dd \ucc28 \uc788\uc73c\uba74\uc11c \ud45c\uc900 \ucd9c\ub825\uc758 \ucc98\ub9ac\uac00 \uc644\ub8cc\ub418\uc9c0 \uc54a\ub294 \uc0c1\ud669\uc774 \ubc1c\uc0dd\ud560 \uc218 \uc788\ub2e4. \uc774\ub294 \uc678\ubd80 \ud504\ub85c\uc138\uc2a4\uac00 \ub354 \uc774\uc0c1 \uc9c4\ud589\ub418\uc9c0 \ubabb\ud558\uac8c \ud558\uace0, \uc790\ubc14 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\ub3c4 \uc678\ubd80 \ud504\ub85c\uc138\uc2a4\uc758 \uc885\ub8cc\ub97c \uae30\ub2e4\ub9ac\ub294 \ub370\ub4dc\ub77d \uc0c1\ud0dc\uc5d0 \ube60\uc9c0\uac8c \ud55c\ub2e4.\\n5. **\ub370\ub4dc\ub77d**: \uacb0\uad6d \uc678\ubd80 \ud504\ub85c\uc138\uc2a4\ub294 \ud45c\uc900 \ucd9c\ub825 \ub610\ub294 \ud45c\uc900 \uc624\ub958 \uc2a4\ud2b8\ub9bc\uc73c\ub85c \ub370\uc774\ud130\ub97c \ub354 \ucd9c\ub825\ud560 \uc218 \uc5c6\uc5b4 \uc2e4\ud589\uc744 \uba48\ucd94\uace0, \uc790\ubc14 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc740 \uc678\ubd80 \ud504\ub85c\uc138\uc2a4\uc758 \uc644\uc804\ud55c \uc885\ub8cc\ub97c \uae30\ub2e4\ub9ac\uba74\uc11c \ub354 \uc774\uc0c1 \uc9c4\ud589\ud558\uc9c0 \ubabb\ud55c\ub2e4. \uacb0\uacfc\uc801\uc73c\ub85c, \ub450 \ud504\ub85c\uc138\uc2a4 \ubaa8\ub450 \uc11c\ub85c\uc758 \ub2e4\uc74c \ub2e8\uacc4\ub97c \uae30\ub2e4\ub9ac\ub294 \uc0c1\ud0dc\uc5d0 \ube60\uc9c0\uac8c \ub418\uc5b4 \ub370\ub4dc\ub77d\uc774 \ubc1c\uc0dd\ud55c\ub2e4.\\n\\n## \ud574\uacb0\ubc29\ubc95\\n\\n### \ub0b4\uc6a9\uc774 \ud544\uc694 \uc5c6\uc744 \uacbd\uc6b0 - close()\\n\\n\uc785\ub825\uc2a4\ud2b8\ub9bc, \ucd9c\ub825\uc2a4\ud2b8\ub9bc\uc744 \uc0ac\uc6a9\ud560 \uc77c\uc774 \uc5c6\ub2e4\uba74 `waitFor()` \uc804\uc5d0 \uc2a4\ud2b8\ub9bc\uc744 \ub2e4 \ub2eb\uc544\uc900\ub2e4.\\n\\n```java\\nProcess process = processBuilder.start(); // or\\nProcess process = Runtime.getRuntime().exec(command);\\n\\nprocess.getErrorStream().close();\\nprocess.getInputStream().close();\\nprocess.getOutputStream().close();\\n\\nprocess.waitFor();\\n```\\n\\n### Apache Commons Exec \uc0ac\uc6a9\\n\\n```gradle\\n// build.gradle\\nimplementation \'org.apache.commons:commons-exec:1.3\'\\n```\\n\\n\uc758\uc874\uc131\uc744 \ucd94\uac00\ud558\uace0, \uc0ac\uc6a9\ud558\uba74 \ud574\uacb0\ubc95\uc744 \uc9c1\uc811 \uad6c\ud604\ud560 \ud544\uc694 \uc5c6\uc774 \uc548\uc804\ud558\uac8c \uc0ac\uc6a9 \uac00\ub2a5\ud558\ub2e4\uace0 \ud55c\ub2e4. \ub098\ub294 \ub2e4\ub978 \ubc29\ubc95\uc744 \uc0ac\uc6a9\ud574 \ud574\uacb0\ud588\ub2e4.\\n\\n### \uc2a4\ub808\ub4dc \ubcd1\ub82c\ucc98\ub9ac\\n\\n- \uc678\ubd80 \ud504\ub85c\uc138\uc2a4\uc758 \ud45c\uc900 \ucd9c\ub825 \uc2a4\ud2b8\ub9bc\uc744 \uc77d\ub294 \uc2a4\ub808\ub4dc\ub97c \uc0dd\uc131\ud558\uace0 \uc2dc\uc791\ud55c\ub2e4.\\n- \uc678\ubd80 \ud504\ub85c\uc138\uc2a4\uc758 \ud45c\uc900 \uc5d0\ub7ec \uc2a4\ud2b8\ub9bc\uc744 \uc77d\uae30 \uc704\ud55c \ub610 \ub2e4\ub978 \ubcc4\ub3c4\uc758 \uc2a4\ub808\ub4dc\ub97c \uc0dd\uc131\ud558\uace0 \uc2dc\uc791\ud55c\ub2e4.\\n- \ub450 \uc2a4\ub808\ub4dc\ub97c \ubcd1\ub82c \ucc98\ub9ac \ud55c\ub2e4.\\n\\n\uc774\ub807\uac8c \ucc98\ub9ac\ud558\uba74 \ud55c \uc2a4\ud2b8\ub9bc\uc758 \ubc84\ud37c\uac00 \uac00\ub4dd \ucc28 \ube14\ub85d\ub418\ub354\ub77c\ub3c4 \ub2e4\ub978 \uc2a4\ud2b8\ub9bc\uc758 \ub370\uc774\ud130\ub97c \uacc4\uc18d \uc77d\uc744 \uc218 \uc788\uc5b4 \ub370\ub4dc\ub77d\uc744 \ubc29\uc9c0\ud560 \uc218 \uc788\ub2e4.\\n\\n### \u2705 Redirect Error Stream\\n\\n\ub098\ub294 \uc774 \ubc29\ubc95\uc744 \uc774\uc6a9\ud574 \ucc98\ub9ac\ud588\ub2e4.\\n\\n**ProcessBuilder**\uc758 `redirectErrorStream(true)` \ub97c \uc0ac\uc6a9\ud574 \ud45c\uc900 \uc5d0\ub7ec \uc2a4\ud2b8\ub9bc\uc744 \ud45c\uc900 \ucd9c\ub825 \uc2a4\ud2b8\ub9bc\uc73c\ub85c \ub9ac\ub2e4\uc774\ub809\uc158 \ud574 \ub2e8\uc77c \uc2a4\ud2b8\ub9bc\uc73c\ub85c \ubaa8\ub4e0 \ucd9c\ub825\uc744 \uc77d\uc744 \uc218 \uc788\ub3c4\ub85d \ud55c\ub2e4.\\n\\n```java\\nProcessBuilder pb = new ProcessBuilder(command);\\npb.redirectErrorStream(true); // \ud45c\uc900 \uc5d0\ub7ec \uc2a4\ud2b8\ub9bc\uc744 \ud45c\uc900 \ucd9c\ub825 \uc2a4\ud2b8\ub9bc\uc73c\ub85c \ub9ac\ub2e4\uc774\ub809\uc158\\n```\\n\\n\ub450 \uc2a4\ud2b8\ub9bc\uc744 \ud558\ub098\uc758 \uc2a4\ud2b8\ub9bc\uc73c\ub85c \ucc98\ub9ac\ud558\uba74, \ud45c\uc900 \ucd9c\ub825\uc744 \uacc4\uc18d \uc77d\uc5b4\ub0bc\ub54c \ud45c\uc900 \uc5d0\ub7ec \uc2a4\ud2b8\ub9bc\uc5d0\uc11c \ubc1c\uc0dd\ud558\ub294 \ub370\uc774\ud130\ub3c4 \ucc98\ub9ac\ub418\uae30 \ub54c\ubb38\uc5d0 \ubc84\ud37c\ub97c \uc9c0\uc18d\uc801\uc73c\ub85c \ube44\uc6cc\uc904 \uc218 \uc788\ub2e4.\\n\\nRuntime\uc73c\ub85c \uc791\uc131\ub418\uc5b4 \uc788\ub358 \ucf54\ub4dc\ub97c ProcessBuilder\ub97c \uc0ac\uc6a9\ud558\ub3c4\ub85d \ud574\uc8fc\uace0, `redirectErrorStream(true)` \ub85c \uc124\uc815\ud558\ub2c8 \ubb38\uc81c\uac00 \ud574\uacb0\ub418\uc5c8\ub2e4.\\n\\n## \ucc38\uace0\uc790\ub8cc\\n\\n- https://d2.naver.com/helloworld/1113548\\n- https://stackoverflow.com/questions/5483830/process-waitfor-never-returns\\n- https://docs.oracle.com/javase/8/docs/api/java/lang/Process.html"},{"id":"/2024/03/30/springrestdocs-openapi3","metadata":{"permalink":"/blog/2024/03/30/springrestdocs-openapi3","source":"@site/blog/2024-03-30-springrestdocs-openapi3.md","title":"Spring REST Docs + OAS \uc801\uc6a9\uae30","description":"Spring REST Docs + OAS \uc120\ud0dd\ud55c \uc774\uc720, \ubc29\ubc95, \uadf8\ub9ac\uace0 \uba87 \uac00\uc9c0 \uc720\uc6a9\ud55c \ud301","date":"2024-03-30T00:00:00.000Z","formattedDate":"March 30, 2024","tags":[],"readingTime":9.69,"hasTruncateMarker":true,"authors":[],"frontMatter":{"title":"Spring REST Docs + OAS \uc801\uc6a9\uae30","description":"Spring REST Docs + OAS \uc120\ud0dd\ud55c \uc774\uc720, \ubc29\ubc95, \uadf8\ub9ac\uace0 \uba87 \uac00\uc9c0 \uc720\uc6a9\ud55c \ud301","hide_table_of_contents":false,"toc_max_heading_level":4},"unlisted":false,"prevItem":{"title":"Process.waitFor() \uc774 \uc601\uc6d0\ud788 \ub05d\ub098\uc9c0 \uc54a\ub294\ub2e4.","permalink":"/blog/2024/04/08/Process.waitfor-dealock-occur"},"nextItem":{"title":"\ud1b5\ud569 \uc811\uadfc \uc81c\uc5b4 \uc804\ub7b5\uc744 \uc124\uacc4 \ud574\ubcf4\ub2e4.","permalink":"/blog/2024/03/11/design-access-control-strategy"}},"content":"\uc774\ubc88 \ud504\ub85c\uc81d\ud2b8\uc5d0 **Spring REST Docs + OAS**\ub97c \ub3c4\uc785\ud574\ubcf8 \ud6c4\uae30 \ubc0f \uba87\uac00\uc9c0 \ud301 :)\\n\\n\x3c!-- truncate --\x3e\\n\\n:::danger\\n\\n**restdocs-api-spec** \ub77c\uc774\ube0c\ub7ec\ub9ac \uc0ac\uc6a9\uc2dc, `@ModelAttribute`, `@RequestParts` \uc5d0 \ub300\ud55c \ubb38\uc11c\ud654\uac00 \uc5b4\ub824\uc6b4 \uc810\uc744 \ubc1c\uacac\ud588\uc2b5\ub2c8\ub2e4. \ubc18\ub4dc\uc2dc \uc774 \uc5b4\ub178\ud14c\uc774\uc158\uc774 \ud544\uc694\ud55c \uacbd\uc6b0, springdoc\uc744 \uace0\ub824\ud574\ubcf4\ub294\uac8c \uc88b\uc744 \ub4ef \ud569\ub2c8\ub2e4 :)\\n\\n:::\\n\\n## \ud544\uc694\uc131\\n\\n\uc571\uac1c\ubc1c\uc790\uc640 \ud611\uc5c5\ud560\ub54c API \ubb38\uc11c\ud654\uac00 \ud544\uc694\ud560 \uac83\uc774\ub77c \uc0dd\uac01\ub418\uc5b4 Swagger\ub97c \uc4f0\uae30\ub85c \ud588\ub294\ub370, \ub098\uc758 \uc81c\uc548\uc73c\ub85c REST DOCS + OAS\ub97c \ub3c4\uc785\ud558\uac8c \ub418\uc5c8\ub2e4. \uc774 \uae00\uc744 \ubcf4\ub294 \uc0ac\ub78c\ub4e4\uc5d0\uac8c Spring REST Docs + OAS\ub97c \ud314\uc544\ubcf4\ub824\uace0 \ud55c\ub2e4.\\n\\n### \uc2e0\ub8b0\uc131\uc774 \ub192\ub2e4.\\n\\nREST Docs\ub85c \ubb38\uc11c\ub97c \ub9cc\ub4e4\uba74 \ubc18\ub4dc\uc2dc \ud14c\uc2a4\ud2b8\ub97c \ud1b5\uacfc\ud574\uc57c \uacb0\uacfc\ubb3c\uc774 \ub9cc\ub4e4\uc5b4\uc9c0\uae30 \ub54c\ubb38\uc5d0 \uc2e0\ub8b0\uc131\uc774 \ub192\ub2e4. \uac15\ub825\ud55c \uc7a5\uc810\uc774\ub2e4.\\n\\n\ub9cc\uc57d api \uc218\uc815\ud6c4 swagger \uad00\ub828 \uc5b4\ub178\ud14c\uc774\uc158\uc744 \uc218\uc815\ud558\uc9c0 \uc54a\uc73c\uba74, \uc2e4\uc81c api\uc640 \ub2e4\ub978 \ubb38\uc11c\uac00 \ub9cc\ub4e4\uc5b4\uc9c4\ub2e4.\\n\\n\ubc18\uba74 REST Docs\ub97c \uc801\uc6a9\ud558\uba74 api \ud14c\uc2a4\ud2b8\ub97c \uac70\uccd0\uc57c\ub9cc \ubb38\uc11c\uac00 \uc644\uc131\ub418\uae30 \ub54c\ubb38\uc5d0, \ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc790\ub098 \uc678\ubd80 REST API\ub97c \uc0ac\uc6a9\ud558\ub294 \uc0ac\ub78c\ub4e4\uc5d0\uac8c \ub354 \uc2e0\ub8b0\uc131 \uc788\ub294 \ubb38\uc11c\ub97c \uc804\ub2ec\ud560 \uc218 \uc788\uac8c \ub41c\ub2e4.\\n\\n### \uc608\uc058\ub2e4.\\n\\nSpring REST Docs\ub97c \uc0ac\uc6a9\ud558\uba74, `adoc`\ud30c\uc77c \ud615\uc2dd\uc73c\ub85c api \uacb0\uacfc\ubb3c\uc774 \ub098\uc628\ub2e4. \uc878\uc5c5\ud504\ub85c\uc81d\ud2b8\uc5d0\uc11c, \uc774 REST Docs\ub97c \ub3c4\uc785\ud574 \ubaa8\ub4e0 api\uc5d0 \ub300\ud55c \ud14c\uc2a4\ud2b8\ub97c \uc791\uc131\ud558\uace0, \uc790\ub3d9 \ubb38\uc11c\ud654\ub97c \uc801\uc6a9\ud574 \ub9e4\uc6b0 \ubfcc\ub4ef\ud55c \uae30\uc5b5\uc774 \uc788\uc5c8\ub2e4.[(\ud83d\udd17\ub9c1\ud06c)](https://2e2i.github.io/mamomo-server/) \ud558\uc9c0\ub9cc,, \uc815\ub9d0 \ubabb\uc0dd\uacbc\ub2e4.\\n\\n![](../static/img/post-img/2024-03-268.58.04.png)\\n\\n\uac8c\ub2e4\uac00 \uc778\ub371\uc2f1\uc774\ub098 \ub9c1\ud06c \ub4f1 \ucee4\uc2a4\ud140\uc744 \ud558\ub824\uba74 \ud55c\ub540\ud55c\ub540 \uc218\uc815\ud574\uc918\uc57c \ud588\ub2e4. \uc790\ub3d9\ud654\ub294 \ub9de\ub294\ub370.. \ubc18 \uc790\ub3d9\ud654\ub2e4.\\n\\n\ubc18\uba74, Swagger\ub97c \uc0ac\uc6a9\ud558\uba74 \uac00\ub3c5\uc131\uc774 \ub9e4\uc6b0 \uc88b\uc740 \uacb0\uacfc\ubb3c\uc744 \ub9cc\ub4e4 \uc218 \uc788\ub2e4.\\n\uac8c\ub2e4\uac00 `Try it` \ubc84\ud2bc\uc73c\ub85c API \ud14c\uc2a4\ud2b8\uae4c\uc9c0 \ud560 \uc218 \uc788\uc5b4 \ud504\ub860\ud2b8\uac1c\ubc1c\uc790\uc5d0\uac8c\ub3c4 \ub9e4\uc6b0 \ud3b8\ub9ac\ud558\ub2e4. JWT \ud1a0\ud070\uc744 \ub123\uc5b4 \ud14c\uc2a4\ud2b8 \ud558\uac70\ub098, Request \uc815\ubcf4\ub97c \uc218\uc815\ud574\uc11c \ud14c\uc2a4\ud2b8\ub3c4 \uac00\ub2a5\ud558\ub2e4.\\n\\n![](../static/img/post-img/2024-03-271.31.42.png)\\n\\n\ud070 \uc81c\ubaa9 \uc544\ub798 \'.json\'\uc774\ub098 \'.yml\'\ub9c1\ud06c\uac00 \uc788\ub294\ub370, Swagger\ub97c \ub9cc\ub4e0 \uc6d0\ubcf8 OpenAPI \uc2a4\ud329 \ud30c\uc77c\uc774\ub2e4. \uc774\uac83\uc744 \ub2e4\uc6b4\ub85c\ub4dc \ubc1b\uc544\uc11c Postman\uc73c\ub85c Import \ud560 \uc218\ub3c4 \uc788\ub2e4.\ud83d\udc4d\\n\\n### \ud655\uc7a5\uc131\uc774 \uc88b\ub2e4.\\n\\n\ud604\uc7ac \uba40\ud2f0\ubaa8\ub4c8 \uad6c\uc870\ub97c \ud0dd\ud574\uc11c, \ubaa8\ub4c8\ubcc4\ub85c `yml`\ud30c\uc77c\uc774 \uc0dd\uc131\ub41c\ub2e4. SwaggerUI\ub97c \ud65c\uc6a9\ud558\uba74 \uc774\ub97c \ud1b5\ud569\ud560 \uc218 \uc788\ub2e4.\\n\\n![](../static/img/post-img/2024-03-271.40.37.png)\\n![](../static/img/post-img/2024-03-271.40.49.png)\\n\\n\uc774\ub807\uac8c \ud1b5\ud569\ud558\ub294 \uae30\ub2a5\ub3c4 \uc81c\uacf5\ud55c\ub2e4. \uc774\ub7ec\ud55c \uae30\ub2a5\uc740 Spring REST Docs \ubfd0\ub9cc \uc544\ub2c8\ub77c, \ub2e4\ub978 \ubc31\uc5d4\ub4dc \ud504\ub808\uc784\uc6cc\ud06c\uc758 \ubb38\uc11c\ud654 \uacb0\uacfc\ubb3c\ub3c4 \ud1b5\ud569\ud560 \uc218 \uc788\uac8c \ud574\uc900\ub2e4.\\n\\nSwagger\ub294 OpenAPI \uc2a4\ud329 \uae30\ubc18\uc73c\ub85c \ub9cc\ub4e4\uc5b4\uc9c4\ub2e4.\\n\\n- [Swagger OpenAPI Specification](https://swagger.io/specification/)\\n- [OpenAPI Specification \uacf5\uc2dd\ubb38\uc11c](https://spec.openapis.org/oas/v3.1.0)\\n\\n\uadf8\ub798\uc11c OpenAPI \uc2a4\ud329\uc5d0 \ub9de\ub294 `yml`, `json`\ud30c\uc77c\ub9cc \ub9cc\ub4e4\uba74 Swagger\ub85c \uc608\uc058\uac8c \ubb38\uc11c\ud654 \ud560 \uc218 \uc788\ub2e4. \uadf8\ub807\uae30 \ub54c\ubb38\uc5d0 Spring, Django, nest.js\ub97c \ud63c\ud569\ud574\uc11c \uc4f0\ub294 \ud504\ub85c\uc81d\ud2b8\uc5d0\uc11c\ub3c4 \uc591\uc2dd\uc5d0 \ub9de\ub294 \ud30c\uc77c\ub9cc \ub9cc\ub4e4\uba74 API \ubb38\uc11c\ub97c \ud1b5\ud569\ud560 \uc218 \uc788\ub2e4.\\n\\n![](../static/img/post-img/openapi3.png)\\n(\uc0ac\uc9c4 \ucd9c\ucc98: [https://velog.io/@letsdev](https://velog.io/@letsdev/MSA-Swagger-UI%EB%A1%9C-API-%EB%AC%B8%EC%84%9C-%ED%86%B5%ED%95%A9-%ED%94%84%EB%A1%9C%EC%84%B8%EC%8A%A4-3-Gradle-Task-from-ePages-Plugin))\\n\\n## Spring REST Docs\uc5d0 OAS \uc801\uc6a9\ud558\uae30\\n\\nSpring REST Docs\uc5d0 [restdocs-api-spec](https://github.com/ePages-de/restdocs-api-spec) \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \uc5b9\uc73c\uba74 \ub41c\ub2e4.\\n\\n### dependency \ucd94\uac00 \ubc0f gradle task \uc138\ud305\\n\\n```gradle\\nplugins {\\n    id \'com.epages.restdocs-api-spec\' version \'0.19.1\'\\n}\\n\\nopenapi3 {\\n    server = \'\uc11c\ubc84 \uc8fc\uc18c\'\\n    title = \'\uc81c\ubaa9\'\\n    description = \'\uc124\uba85\'\\n    version = \'0.1.0(\ubc84\uc804)\'\\n    outputFileNamePrefix = \'\ud30c\uc77c\uba85\'\\n    format = \'yaml or json\'\\n}\\n```\\n\\n### \ud14c\uc2a4\ud2b8 \ucf54\ub4dc \uc791\uc131\\n\\n\uae30\uc874 REST Docs \ucf54\ub4dc\uc5d0\\n\\n```java\\n- import static org.springframework.restdocs.mockmvc.MockMvcRestDocumentation.document;\\n+ import static com.epages.restdocs.apispec.MockMvcRestDocumentationWrapper.document;\\n```\\n\\n`MockMvcRestDocumentation`\ub97c `MockMvcRestDocumentationWrapper`\ub85c \ub300\uccb4\ud558\uba74 \ub41c\ub2e4.\\n\\n### \uacb0\uacfc\ubb3c \uc0dd\uc131\\n\\nOpenAPI 3.0.1 specification \uc73c\ub85c \ubb38\uc11c\ub97c \ub9cc\ub4dc\ub824\uba74 \ub2e4\uc74c\uacfc \uac19\uc740 \uba85\ub839\uc5b4\ub85c yaml \ud639\uc740 json \ud30c\uc77c\uc744 \ub9cc\ub4e0\ub2e4.\\n\\n```bash\\n./gradlew openapi3\\n```\\n\\nbuild \ub514\ub809\ud1a0\ub9ac\uac00 \uc5c6\uc73c\uba74 \uc624\ub958\uac00 \ub098\uae30 \ub54c\ubb38\uc5d0 \ube4c\ub4dc \ub3cc\ub9b0 \ud6c4 \uc9c4\ud589\ud558\uba74 \ub41c\ub2e4.\\n\\n/build/api-spec/\uc5d0 yaml \ud30c\uc77c\uc774 \uc0dd\uae34\uac83\uc744 \ud655\uc778\ud560 \uc218 \uc788\ub2e4.\\n\\n![](../static/img/post-img/2024-03-3011.10.03.png)\\n\\nIntelliJ \uc0ac\uc6a9\uc2dc OAS \uc2a4\ud329\uc744 UI\ub85c \ubc14\ub85c \ud655\uc778\ud560 \uc218\ub3c4 \uc788\ub2e4.\\n\\n## MSA\uc5d0\uc11c api \ubb38\uc11c \ud1b5\ud569\ud574 Swagger \uc801\uc6a9\ud558\uae30\\n\\n### \ud769\uc5b4\uc838 \uc788\ub294 yaml\ud30c\uc77c \ud1b5\ud569\\n\\n```gradle\\ntask copyOpenApiSpecsToDocsDir(type: Copy) {\\n\\tdependsOn \':api:member:openapi3\'\\n\\tdependsOn \':api:store:makeOpenapi\'\\n\\tdependsOn \':api:event:openapi3\'\\n\\n\\t// member\\n\\tfrom \\"${project(\':api:member\').buildDir}/api-spec/member-openapi.yaml\\"\\n\\tinto \\"${project.rootDir}/docs/openapi\\"\\n\\n\\t// product\\n\\tfrom \\"${project(\':api:store\').buildDir}/api-spec/store-openapi.yaml\\"\\n\\tinto \\"${project.rootDir}/docs/openapi\\"\\n\\n\\t//event\\n\\tfrom \\"${project(\':api:event\').buildDir}/api-spec/event-openapi.yaml\\"\\n\\tinto \\"${project.rootDir}/docs/openapi\\"\\n}\\n```\\n\\n\uc704 \uc2a4\ud06c\ub9bd\ud2b8\ub97c \uc791\uc131\ud574 \uac01 api \ubaa8\ub4c8\uc758 \ube4c\ub4dc \ub514\ub809\ud1a0\ub9ac\uc5d0\uc11c, \ub8e8\ud2b8\uc758 /docs\ub85c \uacb0\uacfc\ubb3c\uc744 \ubcf5\uc0ac\ud558\ub294 task\ub97c \uc0dd\uc131\ud588\ub2e4.\\n\\n```\\n\u251c\u2500\u2500 docs\\n\u2502\xa0\xa0 \u251c\u2500\u2500 Dockerfile\\n\u2502\xa0\xa0 \u251c\u2500\u2500 index.html\\n\u2502\xa0\xa0 \u251c\u2500\u2500 openapi\\n\u2502\xa0\xa0 \u2502\xa0\xa0 \u251c\u2500\u2500 event-openapi.yaml\\n\u2502\xa0\xa0 \u2502\xa0\xa0 \u251c\u2500\u2500 member-openapi.yaml\\n\u2502\xa0\xa0 \u2502\xa0\xa0 \u2514\u2500\u2500 store-openapi.yaml\\n\u2502\xa0\xa0 \u2514\u2500\u2500 urls.json\\n```\\n\\n`copyOpenApiSpecsToDocsDir`\ub97c \uc2e4\ud589\ud558\uba74, /docs/openapi\ub85c \ud30c\uc77c\ub4e4\uc774 \ubaa8\uc774\uac8c \ub41c\ub2e4.\\n\\n### Swagger \uc801\uc6a9\ud558\uae30\\n\\nspring\uc5d0\uc11c \uc815\uc801 \ub9ac\uc18c\uc2a4\ub97c \ud638\uc2a4\ud305\ud558\uace0 \uc774 \uc8fc\uc18c\ub85c docker swagger \uc774\ubbf8\uc9c0\ub97c \uc0ac\uc6a9\ud574 \ub744\uc6b0\ub294 \ubc29\ubc95\ub3c4 \uc788\uc9c0\ub9cc, \uc11c\ubc84\uac00 \uac00\ub3d9\uc911\uc774 \uc544\ub2d0\ub54c\ub3c4 \ud655\uc778 \ud560 \uc218 \uc788\uac8c \uc138\ud305\ud558\uace0 \uc2f6\uc5c8\ub2e4.\\n\\n\uac04\ub2e8\ud558\uac8c Swagger\uc758 `index.html` \ub9cc \uac00\uc838\uc640 standalone \ubaa8\ub4dc\ub85c \uc138\ud305\ud588\ub2e4.\\n\\n\uba3c\uc800 \uc804\uccb4 \uc18c\uc2a4\ucf54\ub4dc\uc774\ub2e4. [Swagger UI Installation](https://swagger.io/docs/open-source-tools/swagger-ui/usage/installation/) \ucc38\uace0\ud574\uc11c base html\uc744 \uac00\uc838\uc654\uace0, \uc870\uae08 \uc218\uc815\ud588\ub2e4.\\n\\n**\ud83d\udcc4 index.html \uc804\uccb4 \uc18c\uc2a4\ucf54\ub4dc**\\n\\n```html\\n<!DOCTYPE html>\\n<html lang=\\"en\\">\\n  <head>\\n    <meta charset=\\"utf-8\\" />\\n    <meta name=\\"viewport\\" content=\\"width=device-width, initial-scale=1\\" />\\n    <meta name=\\"description\\" content=\\"SwaggerUI\\" />\\n    <title>\ubc31\ud22c\ub354\ud4e8\ucc98 API Docs</title>\\n    <link\\n      rel=\\"stylesheet\\"\\n      href=\\"https://unpkg.com/swagger-ui-dist@5.11.8/swagger-ui.css\\"\\n    />\\n  </head>\\n  <body>\\n    <div id=\\"swagger-ui\\"></div>\\n    <script\\n      src=\\"https://unpkg.com/swagger-ui-dist@5.11.8/swagger-ui-bundle.js\\"\\n      crossorigin\\n    ><\/script>\\n    <script\\n      src=\\"https://unpkg.com/swagger-ui-dist@5.11.8/swagger-ui-standalone-preset.js\\"\\n      crossorigin\\n    ><\/script>\\n    <script src=\\"https://unpkg.com/axios/dist/axios.min.js\\"><\/script>\\n    <script>\\n      window.onload = () => {\\n        axios.get(\\"urls.json\\").then((res) => {\\n          const urls = res.data;\\n          window.ui = SwaggerUIBundle({\\n            urls: urls,\\n            dom_id: \\"#swagger-ui\\",\\n            presets: [SwaggerUIBundle.presets.apis, SwaggerUIStandalonePreset],\\n            layout: \\"StandaloneLayout\\",\\n            queryConfigEnabled: true,\\n          });\\n        });\\n      };\\n    <\/script>\\n  </body>\\n</html>\\n```\\n\\n\ubc14\uafbc \ubd80\ubd84\uc740 \ub2e4\uc74c\uacfc \uac19\ub2e4.\\n\\n```js\\naxios.get(\\"urls.json\\").then((res) => {\\n    const urls = res.data;\\n    window.ui = SwaggerUIBundle({\\n        urls: urls,\\n        // \uc0dd\ub7b5\\n```\\n\\n\uc5ec\ub7ec yaml \ud30c\uc77c\ub4e4\uc744 \ud1b5\ud569\ud558\uae30 \uc704\ud574\uc11c json\ud30c\uc77c\ub85c \ubaa9\ub85d\uc744 \uc791\uc131\ud574 index.html\uc5d0\uc11c \ubd88\ub7ec\uc62c \uc218 \uc788\ub3c4\ub85d \ud588\ub2e4. `url.json`\uc740 \uc544\ub798\ucc98\ub7fc \uc0dd\uacbc\ub2e4.\\n\\n**\ud83d\udcc4 url.json**\\n\\n```json\\n[\\n  { \\"url\\": \\"./openapi/member-openapi.yaml\\", \\"name\\": \\"member-api\\" },\\n  { \\"url\\": \\"./openapi/store-openapi.yaml\\", \\"name\\": \\"store-api\\" },\\n  { \\"url\\": \\"./openapi/event-openapi.yaml\\", \\"name\\": \\"event-api\\" }\\n]\\n```\\n\\nurl\uacfc ui\uc0c1\uc5d0\uc11c \ubcf4\uc5ec\uc9c8 name\uc744 \uc124\uc815\ud55c\ub2e4.\\n\\n### Swagger \uacb0\uacfc\ubb3c\uc744 Docker \uc774\ubbf8\uc9c0\ub85c \ub9cc\ub4e4\uae30\\n\\n\ub2e8\uc21c\ud558\uac8c index.html\ub9cc \ub744\uc6b0\uba74 \ub418\uc5b4\uc11c, nginx\uc774\ubbf8\uc9c0\ub97c \uc0ac\uc6a9\ud588\ub2e4. \uc544\ub798\ub294 Dockerfile \ub0b4\uc6a9\uc774\ub2e4.\\n\\n```Dockerfile\\nFROM nginx:alpine\\nCOPY . /usr/share/nginx/html/\\n```\\n\\n\uc774\ub807\uac8c \ud574\uc11c \uc774\ubbf8\uc9c0\ub97c \ube4c\ub4dc\ud558\uace0 \uc544\ub798\ucc98\ub7fc (docker compose \uc77c\ubd80) \uc124\uc815\ud558\uba74 \ub0b4\uac00 \uc815\ud55c \ud3ec\ud2b8\ub85c \uc811\uadfc\ud588\uc744\ub54c, Swagger UI\ub97c \ud655\uc778\ud560 \uc218 \uc788\ub2e4.\\n\\n```yaml\\nswagger-ui:\\n  image: bagtothefuture-swagger:latest # swagger \uc774\ubbf8\uc9c0\\n  ports:\\n    - \\"8000:80\\" # 80\ud3ec\ud2b8\ub97c host\uc758 8000\ub85c \uc624\ud508\\n```\\n\\n## JWT \ud1a0\ud070 \uc801\uc6a9\ud558\uae30\\n\\n`requestHedaer()`\ub85c \uc791\uc131\ud558\uba74 \ud5e4\ub354 \uc785\ub825\uce78\uc774 \uc0dd\uae30\uc9c0\ub9cc, Try it\uc73c\ub85c \ud14c\uc2a4\ud2b8\uc2dc \uc2e4\uc81c\ub85c \ubcf4\ub0b4\uc9c0\uc9c0 \uc54a\ub294\ub2e4. \uac70\uc758 \ubaa8\ub4e0 API\uac00 JWT \ud1a0\ud070\uc744 \uc0ac\uc6a9\ud558\ub294\ub370, \uc774\uac78 \uc801\uc6a9\ud558\uc9c0 \ubabb\ud55c\ub2e4\uba74 Swagger\ub97c \uc801\uc6a9\ud558\ub294 \ud070 \uc774\uc720\uac00 \ud558\ub098 \uc0ac\ub77c\uc9c4\ub2e4..\\n\\nepages\uc5d0\uc11c \ub4a4\uc838\ubcf4\uc558\ub294\ub370 \uc544\uc9c1 \ucc3e\uc9c0 \ubabb\ud588\ub2e4 \ud760..\\n\\n\uadf8\ub7ec\ub358 \ucc38\uc5d0 [hwsa1004\ub2d8\uc758 \ube14\ub85c\uadf8 \uae00](https://velog.io/@hwsa1004/Spring-restdocs-swagger-%EA%B0%99%EC%9D%B4-%EC%82%AC%EC%9A%A9%ED%95%98%EA%B8%B0)\uc744 \ubc1c\uacac\ud574 \uc801\uc6a9\ud574 \ubcf4\uc558\ub2e4.\\n\\n```gradle\\ntask makeOpenapi {\\n    dependsOn \'openapi3\'\\n    doFirst {\\n        def swaggerUIFile = file(\\"${openapi3.outputDirectory}/store-openapi.yaml\\")\\n\\n        def securitySchemesContent =  \\"  securitySchemes:\\\\n\\" +  \\\\\\n                                      \\"    bearerAuth:\\\\n\\" +  \\\\\\n                                      \\"      type: http\\\\n\\" +  \\\\\\n                                      \\"      scheme: bearer\\\\n\\" +  \\\\\\n                                      \\"      bearerFormat: JWT\\\\n\\" + \\\\\\n                                      \\"security:\\\\n\\" +\\n                \\"  - bearerAuth: []\\"\\n\\n        swaggerUIFile.append securitySchemesContent\\n    }\\n}\\n```\\n\\n\uc9c1\uc811 \uc774\ubd80\ubd84\uc744 \ubb38\uc11c \ub9c8\uc9c0\ub9c9\uc5d0 \ucd94\uac00\ud574\uc8fc\ub294 \uac83\uc774\ub2e4.\\n\\n\ube14\ub85c\uadf8\uc640 \ub2e4\ub978 \uc810\uc740, [openapi3 docs](https://swagger.io/docs/specification/authentication/bearer-authentication/)\ub97c \ucc38\uace0\ud574, bearer \ud0c0\uc785\uc73c\ub85c \ub123\uc740 \uac83\uc774\ub2e4.\\n\\n\uc774\ub807\uac8c \uc801\uc6a9\ud558\uba74 \\"Bearer \\"\uc744 \ud3ec\ud568\ud558\uc9c0 \uc54a\uace0 \ud1a0\ud070\ub9cc \uc785\ub825\ud574\ub3c4 \ub41c\ub2e4.\\n\\n![alt text](https://github.com/backtothefuture-team/backtothefuture-backend/assets/67352902/4cd94907-c846-4e9a-a56f-e342f934ba3a)\\n\\n\ud1a0\ud070\uc744 \uc785\ub825\ud558\uace0, Authorize \ubc84\ud2bc\uc744 \ub204\ub974\uba74 \uc774\ud6c4\uc5d0 api \ud14c\uc2a4\ud2b8\uc5d0\uc11c header\uc5d0 \ud1a0\ud070\uc744 \uc785\ub825\ud558\uc9c0 \uc54a\uc544\ub3c4 \ubcf4\ub0b4\uc9c0\uac8c \ub41c\ub2e4.\\n\\n## \ub9c8\ubb34\ub9ac\\n\\n\ud504\ub85c\uc81d\ud2b8\ub97c \uc9c4\ud589\ud558\uba70 Spring REST Docs + OAS\ub97c \uc801\uc6a9\ud574\ubcf4\uc558\uace0, \uc7a5\uc810\uc774 \ub108\ubb34\ub098 \ub9ce\ub2e4\uace0 \uc0dd\uac01\ub418\uc5b4 \uae00\uc744 \uc791\uc131\ud558\uac8c \ub418\uc5c8\ub2e4. \ud558\uba74\uc11c \uacf5\uc720\ud560 \ud301\uc774 \ub354 \uc0dd\uae34\ub2e4\uba74 \ub0b4\uc6a9\uc744 \ucd94\uac00 \ud560 \uac83\uc774\ub2e4.\\n\\n> \ud55c \ubc88 \uc368\ubcf4\uc138\uc694!! \uc7a5\uc810\uc774 \ub9ce\uc2b5\ub2c8\ub2e4 :)"},{"id":"/2024/03/11/design-access-control-strategy","metadata":{"permalink":"/blog/2024/03/11/design-access-control-strategy","source":"@site/blog/2024-03-11-design-access-control-strategy.md","title":"\ud1b5\ud569 \uc811\uadfc \uc81c\uc5b4 \uc804\ub7b5\uc744 \uc124\uacc4 \ud574\ubcf4\ub2e4.","description":"\ud769\uc5b4\uc838 \ud558\ub4dc\ucf54\ub529\ub418\uc5b4 \uc788\ub294 \uad8c\ud55c \uad00\ub9ac\ub97c \ud558\ub098\ub85c \ud1b5\ud569\ud558\ub294 \uc124\uacc4\ub97c \uc9c4\ud589\ud574 \ubcf4\uc558\ub2e4.","date":"2024-03-11T00:00:00.000Z","formattedDate":"March 11, 2024","tags":[{"label":"design","permalink":"/blog/tags/design"}],"readingTime":17.375,"hasTruncateMarker":true,"authors":[],"frontMatter":{"title":"\ud1b5\ud569 \uc811\uadfc \uc81c\uc5b4 \uc804\ub7b5\uc744 \uc124\uacc4 \ud574\ubcf4\ub2e4.","description":"\ud769\uc5b4\uc838 \ud558\ub4dc\ucf54\ub529\ub418\uc5b4 \uc788\ub294 \uad8c\ud55c \uad00\ub9ac\ub97c \ud558\ub098\ub85c \ud1b5\ud569\ud558\ub294 \uc124\uacc4\ub97c \uc9c4\ud589\ud574 \ubcf4\uc558\ub2e4.","tags":["design"],"hide_table_of_contents":false,"toc_max_heading_level":4},"unlisted":false,"prevItem":{"title":"Spring REST Docs + OAS \uc801\uc6a9\uae30","permalink":"/blog/2024/03/30/springrestdocs-openapi3"},"nextItem":{"title":"[TroubleShooting] express\uac00 \uc751\ub2f5\uc744 \ubcf4\ub0b4\uc9c0 \uc54a\uc558\ub2e4. \uc65c..?","permalink":"/blog/2024/02/04/troubleshooting-express-no-response"}},"content":"## Intro\\n\\n\ud68c\uc0ac\uc5d0\uc11c \ub0b4\uac00 \ub9e1\uc740 \uc11c\ube44\uc2a4\uc758 **\uad8c\ud55c \uad00\ub9ac**\ub294 \uc5ec\ub7ec \uacf3\uc5d0 \ud769\uc5b4\uc838 \ud558\ub4dc\ucf54\ub529 \ub418\uc5b4 \uc788\ub2e4. \uc5ec\uae30\uc11c \uad8c\ud55c \uad00\ub9ac\ub780 \uc5b4\ub5a4 \ub9ac\uc18c\uc2a4\uc5d0 \ub300\ud55c \uc811\uadfc \uad8c\ud55c\uc744 \ub9d0\ud55c\ub2e4.\\n\\n\uc774\ub54c \uace0\ub824\ub418\ub294 \uc694\uc18c\ub3c4 \ub2e4\uc591\ud558\ub2e4. \uadf8\ub7f0\ub370 \uc774\ubc88\uc5d0 \uc0c8 \uae30\ub2a5\uc774 \ucd94\uac00\ub418\uc5b4 \ub354\uc6b1 \ub354 \ubcf5\uc7a1\ud558\uac8c \uad00\ub9ac\ud574\uc57c \ud560 \uc0c1\ud669\uc774 \uc0dd\uacbc\ub2e4. REST API\ub3c4 \uc81c\uacf5\ud558\uac8c \ub418\uc5c8\ub2e4. \uc774\uc5d0 \uad8c\ud55c \uad00\ub9ac\ub97c \ud55c\uacf3\uc5d0\uc11c \ud1b5\ud569\ud574\uc57c \ud560 \ud544\uc694\uac00 \uc0dd\uacbc\uace0, \uc774 \uc784\ubb34\uac00 \ub098\uc5d0\uac8c(!!) \ub9e1\uaca8\uc84c\ub2e4.\\n\\n\uadf8\ub798\uc11c \uc124\uacc4\ub97c \ud574\ubcf4\uba70 \uace0\ub824\ud55c \uc694\uc18c\ub4e4\uacfc, \uc774 \uacfc\uc815\uc5d0\uc11c \ub290\ub080\uc810\uc744 \uc815\ub9ac\ud574 \ubcf4\ub824\uace0 \ud55c\ub2e4.\\n\\n\x3c!-- truncate --\x3e\\n\\n:::warning\\n\\n\uc870\uae08\uc774\ub77c\ub3c4 \ubbfc\uac10\ud560 \uc218 \uc788\ub294 \ud68c\uc0ac \ub0b4\uc6a9\uc744 \ubaa8\ub450 \uc81c\uc678\ud558\uc600\uae30 \ub54c\ubb38\uc5d0, **\uc559\uaf2c\uc5c6\ub294 \ucc10\ube75**\uac19\uc740 \uae00\uc77c \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n`ROLE_` \ud615\uc2dd\uc758 \uad8c\ud55c \uc124\uacc4\uac00 \uc544\ub2cc, \ub2e4\ub978 \uc124\uacc4\ub97c \uacc4\ud68d\ud560 \ub54c \uc870\uae08\uc774\ub77c\ub3c4 \uc778\uc0ac\uc774\ud2b8\uac00 \ub41c\ub2e4\uba74 \uc88b\uaca0\uc2b5\ub2c8\ub2e4. \u263a\ufe0f\\n\\n:::\\n\\n## \uace0\ub824\ud574\uc57c \ud560 \uc694\uc18c\\n\\n\uc0ac\ub0b4 \uc11c\ube44\uc2a4\ub294 \ub9dd \ubd84\ub9ac\uac00 \ub418\uc5b4 \uc788\ub2e4. \uadf8\ub798\uc11c \uc0ac\ub0b4\uc9c1\uc6d0 / \uc678\ubd80\uc720\uc800\uac00 \ub85c\uadf8\uc778 \ubc29\uc2dd\uc774 \ub2e4\ub974\uace0, \uc811\uadfc\ud558\ub294 \ub3c4\uba54\uc778\ub3c4 \ub2e4\ub974\ub2e4. \uc0ac\ub0b4\uc9c1\uc6d0\uc77c \uacbd\uc6b0 \ubd80\uc11c\ubcc4\ub85c \uc5b4\ub5a4 \ub9ac\uc18c\uc2a4\uc5d0 \ub300\ud55c \uc811\uadfc \uad8c\ud55c\uc774 \ub2e4\ub974\ub2e4. \ud2b9\uc815 \ubd80\uc11c\uac00 \uc0dd\uc131\ud55c \uac83\uc740 \uadf8 \ubd80\uc11c\ub9cc \uc218\uc815\ud560 \uc218 \uc788\ub2e4\ub358\uac00 \ud558\ub294 \ucc98\ub9ac\uac00 \ud544\uc694\ud558\ub2e4. \uc678\ubd80\uc720\uc800\uc77c \uacbd\uc6b0 \uac19\uc740 \ud68c\uc0ac\ub9cc \uc5f4\ub78c\ud560 \uc218 \uc788\uc5c8\ub2e4. \uc774\uac78 \ubaa8\ub450 \ud558\ub4dc\ucf54\ub529 \ud558\uace0 \uc788\uc5c8\ub2e4. (if ~ else\ub85c.)\\n\\n\ub610, \uc77c\ubc18\uc801\uc778 \uc6f9 \ubc31\uc5d4\ub4dc API \ub9d0\uace0\ub3c4 REST API \ub85c\uc758 \uc811\uadfc\ub3c4 \uc81c\uacf5\ud558\uac8c \ub418\uc5c8\ub2e4. \uc774\ub54c Token\uc744 \ubc1c\uae09\ubc1b\uc740 \uc720\uc800\ub9cc \ud574\ub2f9 API \ub97c \uc0ac\uc6a9\ud560 \uc218 \uc788\ub2e4.\\n\\n\uc774\ub7f0 \uc694\uc18c\ub4e4\uc744 \uace0\ubbfc\ud574\uc57c \ud588\ub2e4. \uc815\ub9ac\ud558\uba74 \uc544\ub798\uc640 \uac19\ub2e4.\\n\\n- \uc0ac\ub0b4\uc9c1\uc6d0 (Jira API \uc5d0\uc11c \ub85c\uadf8\uc778 \uc815\ubcf4\ub97c \uac00\uc838\uc634) / \uc678\ubd80\uc720\uc800 (\ud68c\uc6d0 \uad00\ub9ac DB\uac00 \ub530\ub85c \uc788\uc74c)\\n- \uc0ac\ub0b4\uc9c1\uc6d0 \uc77c \uacbd\uc6b0 \ubd80\uc11c \uc815\ubcf4\uc5d0 \ub530\ub77c \uc811\uadfc \uad8c\ud55c\uc744 \uad6c\ubd84\ud574\uc57c \ud568\\n- \uc678\ubd80\uc720\uc800\uc77c \uacbd\uc6b0 \ud68c\uc0ac \uc815\ubcf4\uc5d0 \ub530\ub77c \uc811\uadfc \uad8c\ud55c\uc744 \uad6c\ubd84\ud574\uc57c \ud568\\n\\n\uc9c0\uae08\uae4c\uc9c0 \ubd80\uc11c\ubcc4 \uad8c\ud55c\uc744 \ub098\ub204\ub294 \uc77c\uc740 \uc5c6\uc5c8\uc73c\ub098, \uc774\ubc88\uc5d0 \ucd94\uac00\ub418\uc5c8\ub2e4. Jira API\ub294 \ubd80\uc11c \uc815\ubcf4\ub97c \ud55c\uae00\ub85c \ub41c String\uc73c\ub85c \ubc18\ud658\ud588\ub2e4. \ub9cc\uc57d \uc870\uc9c1 \uac1c\ud3b8\uc774 \uc774\ub8e8\uc5b4\uc9c8 \uacbd\uc6b0 **\ub9c8\uc774\uadf8\ub808\uc774\uc158** \ub3c4 \uace0\ub824\ud574\uc57c \ud588\ub2e4.\\n\\n## \uc804\ub7b5\uc758 \uc120\ud0dd\\n\\n\ubcf4\ud1b5 \uad8c\ud55c\uad00\ub9ac \uc2dc\uc5d0 Spring\uc73c\ub85c \uc5b4\ud50c\ub9ac\ucf00\uc774\uc158\uc744 \uac1c\ubc1c\ud558\uba74 Spring Security\ub85c Role\uc744 \ubd80\uc5ec\ud558\uc5ec \ud558\uace4 \ud55c\ub2e4. \ud558\uc9c0\ub9cc \ub098\uc758 \uacbd\uc6b0 \uad8c\ud55c \uad00\ub9ac\uac00 \uc774\ub8e8\uc5b4 \uc9c0\ub294 \uacf3\uc740 Express \uc600\uace0, \ud68c\uc6d0 \uad00\ub828 \uc815\ubcf4\uac00 \uc6b0\ub9ac DB\uc5d0 \uc788\ub294 \uac83\uc774 \uc544\ub2c8\ub77c \uc678\ubd80\ub85c\ubd80\ud130 \uac00\uc838\uc624\ub294 \uc815\ubcf4\uc774\uae30 \ub54c\ubb38\uc5d0 \ud754\ud558\uac8c \uc801\uc6a9\ud558\ub294 \ubc29\ubc95\uc744 \uc27d\uac8c \uc4f8 \uc218 \uc5c6\uc5c8\ub2e4.\\n\\n\uad8c\ud55c\uad00\ub9ac\ub97c \uc124\uacc4\ud560 \ub54c \uc5b4\ub5a4 \uc804\ub7b5\uc744 \ucde8\ud558\ub294\uc9c0 \uc870\uc0ac\ub97c \ud574 \ubcf4\uc558\ub2e4. `Role Based Access Control (\uc5ed\ud560 \uae30\ubc18 \uc81c\uc5b4)` \uc640 `Attribute Based Access Control (\uc18d\uc131 \uae30\ubc18 \uc81c\uc5b4)` \uac00 \uac00\uc7a5 \ub9ce\uc774 \ucde8\ud558\ub294 \uc804\ub7b5\uc774\uc600\ub2e4.\\n\\n\uc774 \ub450 \uc804\ub7b5\uc744 \uc911\uc2ec\uc73c\ub85c \uc124\uacc4\ub97c \ud588\ub294\ub370, \ub9ac\ubdf0 \uc2dc\uac04\uc5d0 PL\ubd84\uaed8\uc11c \uc778\uc0ac\uc774\ud2b8\ub97c \uc81c\uacf5\ud574 \uc8fc\uc154\uc11c(!) \ud574\ub2f9 \ub9ac\uc18c\uc2a4\ubcc4\ub85c \uad8c\ud55c\uc744 \uc81c\uc5b4\ud558\ub294 \ubc29\uc2dd, \ub0b4\ub9d8\ub300\ub85c `Resource Based Access Control (\ub9ac\uc18c\uc2a4 \uae30\ubc18 \uc81c\uc5b4)` \ub77c \uc774\ub984\uc744 \uc9d3\uace0, \uc774 \uc804\ub7b5\ub3c4 \ucd94\uac00\ub85c \uc124\uacc4\ud588\ub2e4.\\n\\n\uc544, \ucd94\uac00\ub85c \uad8c\ud55c\uc744 \uad00\ub9ac\ud560 \uc218 \uc788\ub294 \uc624\ud508\uc18c\uc2a4\ub4e4 (OPA \ub4f1 ...) \uc774 \uc788\uae34 \ud588\ub294\ub370, \uac1c\ubc1c\uc790\uac00 \ub098 \ud63c\uc790\ub77c \uc11c\ube44\uc2a4\ub97c \ud558\ub098 \ub354 \ucd94\uac00\ud574 \uad00\ub9ac\ud558\ub294\uac83\uc774 \ubd80\ub2f4\uc2a4\ub7fd\uace0 \uadf8\ub807\uac8c \ud070 \uc2dc\uc2a4\ud15c\ub3c4 \uc544\ub2c8\ub77c\uc11c \uc774\uac74 \uc81c\uc678\ud588\ub2e4.\\n\\n### Role Based Access Control, \uc5ed\ud560 \uae30\ubc18 \uc811\uadfc \uc81c\uc5b4\\n\\n#### \ud2b9\uc9d5\\n\\n\uc5ed\ud560\uc5d0 \ub530\ub77c \uc2dc\uc2a4\ud15c, \ub9ac\uc18c\uc2a4\uc5d0 \ub300\ud55c \uc0ac\uc6a9\uc790 \uc811\uadfc \uad8c\ud55c\uc744 \uad00\ub9ac\ud558\ub294 \ubc29\ubc95\uc774\ub2e4. \uac01 \uac1c\ubcc4 \uc0ac\uc6a9\uc790\uc5d0 \ub300\ud574 \uc5ed\ud560\uc744 \ubd80\uc5ec\ud558\uace0, \uc5b4\ub5a4 \uae30\ub2a5\uc5d0 \ub300\ud574 \ud5c8\uc6a9\ud560 \uc5ed\ud560\uc744 \ub098\uc5f4\ud574 \uc81c\uc5b4\ud55c\ub2e4.\\n\\n\uc5ed\ud560 \uae30\ubc18 \uc811\uadfc \uc81c\uc5b4\ub294 \ub2e4\uc74c \ud2b9\uc9d5\uc744 \uac00\uc9c4\ub2e4.\\n\\n- \uac01 \uc0ac\uc6a9\uc790\uc5d0\uac8c\ub294 \ud558\ub098 \uc774\uc0c1\uc758 \uc5ed\ud560\uc774 \ud560\ub2f9\ub41c\ub2e4.\\n- \uc0ac\uc6a9\uc790 \uc5ed\ud560\uc5d0\ub294 \uad8c\ud55c\uc774 \ud560\ub2f9\ub41c\ub2e4.\\n- \uc0ac\uc6a9\uc790\uac00 \uc5ed\ud560\uc744 \ud65c\uc131 \uad6c\uc131\uc6d0\uc774 \ub418\uba74 \uad8c\ud55c\uc5d0 \uc561\uc138\uc2a4 \ud560 \uc218 \uc788\ub2e4.\\n\\n\uc608\ub97c\ub4e4\uc5b4, \uc5b4\ub5a4 \uc0c1\ud488\uc744 \ud310\ub9e4\ud558\ub294 \ud398\uc774\uc9c0\ub77c\uba74, `ROLE_ADMIN`, `ROLE_SELLER`, `ROLE_USER`\uc640 \uac19\uc740 \uc5ed\ud560\uc744 \uc815\uc758\ud558\uace0, \uadf8 \uc5ed\ud560\uc5d0 \ub530\ub77c \uc81c\uc5b4\ud558\uac8c \ub41c\ub2e4.\\n\\n**\uc5ed\ud560 \uc815\uc758**\\n\\n```json\\n{\\n  \\"name\\": \\"roles/testRole\\",\\n  \\"title\\": \\"billing role\\",\\n  \\"includedPermissions\\": [\\"paymentStatement.list\\", \\"usage.list\\"]\\n}\\n```\\n\\n**\uc5ed\ud560 \ubd80\uc5ec**\\n\\n```json\\n{\\n  \\"bindings\\": [\\n    {\\n      \\"role\\": \\"roles/testRole\\",\\n      \\"members\\": [\\"user:viewrain@hohoho.com\\"]\\n    }\\n  ]\\n}\\n```\\n\\n#### \uc7a5\uc810\\n\\n- \uad8c\ud55c\uc5d0 \ub300\ud55c \ubd80\uc5ec, \ud68c\uc218\uac00 \uac04\ud3b8\ud558\ub2e4. \ud2b9\uc815 \uad8c\ud55c\uc744 \ubd80\uc5ec\ud558\ub824\uba74 \uc5ed\ud560\uc744 \ubd80\uc5ec\ud558\uace0, \ube8f\uc73c\ub824\uba74 \uc5ed\ud560\uc744 \uc81c\uac70\ud558\uba74 \ub41c\ub2e4. \ub530\ub77c\uc11c \uc720\uc5f0\ud558\uace0 \uc9c1\uad00\uc801\uc778 \ud1b5\uc81c\uac00 \uac00\ub2a5\ud558\ub2e4.\\n\\n#### \ub2e8\uc810\\n\\n- \uc5ed\ud560\uc774 \ud3ed\ubc1c\uc801\uc73c\ub85c \ub298\uc5b4\ub0a0 \uc218 \uc788\ub2e4. \uc0c8 \uae30\ub2a5\uc774 \ucd94\uac00\ub418\uace0, \uadf8 \uae30\ub2a5\uc5d0\ub9cc \ud5c8\uc6a9\ub418\ub294 \uad8c\ud55c\uc774 \uc0dd\uae34\ub2e4\uba74 \uc0c8\ub85c \uc5ed\ud560\uc744 \uc815\uc758\ud558\uace0 \ubd80\uc5ec\ud55c\ub2e4. \uc774\ub7f0\uc2dd\uc73c\ub85c \ubc18\ubcf5\ud558\uba74 \uc11c\ube44\uc2a4\uac00 \ucee4\uc9c8\uc218\ub85d \uadf8 \uc5ed\ud560\ub3c4 \ud568\uaed8 \ub298\uc5b4\ub09c\ub2e4.\\n- \uc138\ubc00\ud55c \uad8c\ud55c \uc815\ucc45\uc744 \ubd80\uc5ec\ud560 \uc218 \uc5c6\ub2e4.\\n  - \uc5ec\ub7ec \uc0c1\ud669\uc744 \ucd94\uc0c1\ud654\ud574\uc11c \uc5ed\ud560\uc744 \ub9cc\ub4e4\uc9c0\ub9cc, \ub531 \ub9de\ub294 \uc5ed\ud560\uc744 \uc815\ud558\uae30\uac00 \ud798\ub4e4\ub2e4. \ud2b9\uc815 \uad8c\ud55c\uc5d0 \ub300\ud574 \ub9ce\uc740 \uace0\ub824\uc694\uc18c\uac00 \uc788\ub294 \uacbd\uc6b0, \ud558\ub098\ub9cc \uc694\uc18c\uac00 \ubc14\ub00c\uc5b4\ub3c4 \ub2e4\ub978 \uc5ed\ud560\uc744 \ubd80\uc5ec\ud574\uc57c \ud558\ub294\ub370 \uadf8\ub807\uac8c \ub108\ubb34 \uc791\uc740 \ub2e8\uc704\ub85c \uc5ed\ud560\uc744 \ub098\ub204\uba74 \uc790\uc8fc \ubcc0\uacbd\ud558\uac8c \ub420 \uc218 \uc788\ub2e4.\\n\\n#### \uc6b0\ub9ac \uc11c\ube44\uc2a4\uc5d0\ub294 ...\\n\\n\uc801\uc6a9\ud558\uae30 \uc27d\uc9c0 \uc54a\uc558\ub2e4. \uc77c\ub2e8 \uc720\uc800\uc5d0 \ub300\ud55c DB \uc815\ubcf4\uac00 \uc6b0\ub9ac\ud55c\ud14c \uc788\ub294 \uac83\uc774 \uc544\ub2c8\uae30 \ub54c\ubb38\uc5d0, \'\ud68c\uc6d0\uac00\uc785\' \uc774\ub77c\ub294 \uc808\ucc28\uac00 \uc5c6\uc5b4 \uc720\uc800 \uc815\ubcf4\ub97c \uae30\ubcf8\uc801\uc73c\ub85c \uac00\uc9c0\uace0 \uc788\uc9c0 \uc54a\ub2e4.\\n\\n\ub85c\uadf8\uc778 \ud55c \uc801 \uc5c6\ub294 \uc720\uc800\uac00 \ub85c\uadf8\uc778\ud558\uba74 \uc790\ub3d9\uc73c\ub85c \uc5ed\ud560\uc744 \ud310\ub2e8\ud574 \ubd80\uc5ec\ud55c\ub2e4\ub358\uac00 \ud558\ub294 \uc808\ucc28\uac00 \ud544\uc694\ud588\ub2e4.\\n\\n\uadf8\ub9ac\uace0, \ubd80\uc11c\ubcc4 \uc5ed\ud560 \uc815\uc758\ub3c4 \ud544\uc694\ud558\uace0, \ud68c\uc0ac\ubcc4 \uc5ed\ud560 \uc815\uc758\ub3c4 \ud544\uc694\ud588\ub2e4. \ub108\ubb34 \ub9ce\uc740 \uc5ed\ud560\uc758 \uc815\uc758\uac00 \ud544\uc694\ud588\uace0, \ubd80\uc5ec\ud558\uae30 \uc704\ud55c \ubcc4\ub3c4 \ub85c\uc9c1\uc774 \ud544\uc694\ud588\ub2e4. \uae4c\ub531\ud574\uc11c \uc798\ubabb \ud310\ub2e8\ud558\uba74 \ubd10\uc57c\ud558\ub294 \uc720\uc800\uac00 \ubabb\ubcf4\ub294 \uc815\ubcf4\uac00 \uc0dd\uae38 \uc218\ub3c4 \uc788\uc5c8\ub2e4.\\n\\n\uc774\ub807\uac8c \ub418\ub2c8 \ub354\uc6b1 \ubcf5\uc7a1\ud574\uc838\uc11c \uc774 \uc804\ub7b5\uc740 \ud0c8\ub77d\ud588\ub2e4.\\n\\n### Attribute Based Access Control, \uc18d\uc131 \uae30\ubc18 \uc811\uadfc \uc81c\uc5b4\\n\\n#### \ud2b9\uc9d5\\n\\n\uc8fc\uccb4, \uc790\uc6d0 \ub610\ub294 \ud658\uacbd\uc758 \uc18d\uc131\uc744 \uae30\ubc18\uc73c\ub85c \uc811\uadfc\uc744 \uc81c\uc5b4\ud558\ub294 \ubc29\uc2dd\uc774\ub2e4. \uc5b4\ub5a4 \uc0ac\ub78c\uc774 \ud2b9\uc815 \uac1d\uccb4\uc5d0 \uc811\uadfc\ud558\ub824 \ud560\ub54c \uac1d\uccb4\uc758 \uc18d\uc131, \uc0ac\ub78c\uc758 \uc18d\uc131, \uadf8\ub54c\uc758 \ud658\uacbd \uc815\ubcf4 \ub4f1\uc744 \ub2e4\uc591\ud558\uac8c \uace0\ub824\ud574\uc11c \ud310\ub2e8\ud55c\ub2e4.\\n\\n\uad00\uacc4\ub294 if ~ then \ubb38\uc73c\ub85c \uc815\uc758\ud55c\ub2e4. \uc608\ub97c\ub4e4\uba74 \ub2e4\uc74c\uacfc \uac19\ub2e4.\\n\\n- \uc9c1\uae09\uc774 \'\uc8fc\uc784\' \uc774\uc0c1\uc774\uace0, \uc811\uadfc\ud55c ip\uc8fc\uc18c\uac00 000.000.000.000/ \ub300\uc5ed\ub300 \uc774\uace0, \ud574\ub2f9 \uac8c\uc2dc\ubb3c\uc744 \uc791\uc131\ud55c \uc0ac\ub78c\uc774 \uac19\uc740\ud300 \uc77c\ub54c \uc561\uc138\uc2a4 \uac00\ub2a5\ud558\ub2e4.\\n- \ud68c\uc0ac \uc815\ucc45\uc5d0 \\"\ud1a0\uc694\uc77c \ud734\ubb34\\"\ub85c \uc9c0\uc815\ub418\uc5b4 \uc788\ub294 \uacbd\uc6b0, \ud1a0\uc694\uc77c\uc5d0\ub294 \ub204\uad6c\ub3c4 \ud30c\uc77c\uc5d0 \uc561\uc138\uc2a4\ud560 \uc218 \uc5c6\uc2b5\ub2c8\ub2e4.\\n  \uc774\ub7f0 \uc815\ucc45\uc744 \ubaa8\ub450 if ~ then \ubb38\uc73c\ub85c \uc815\uc758\ud558\ub294 \uac83\uc774\ub2e4.\\n\\n\ub098\ub294 \'\uc815\ucc45\' \uc744 \uc800\uc7a5\ud560 json array\ub97c \uc815\uc758\ud574 \ud558\ub098\uc758 object\uc5d0\ub294 \ub9ac\uc18c\uc2a4\uc758 \uc815\ubcf4\uc640, \uc811\uadfc\ud558\ub824\ub294 \uc720\uc800\uc758 \uc815\ubcf4, \ucd5c\uc885\uc801\uc73c\ub85c \uadf8\uac83\uc744 allow \ud560\uac74\uc9c0 deny \ud560\uac74\uc9c0 \uacb0\uc815\ud558\ub294 \uc815\ubcf4\ub97c \uc801\uc5b4\uc8fc\uc5c8\ub2e4. \uc608\ub97c \ub4e4\uba74 \ub2e4\uc74c\uacfc \uac19\ub2e4.\\n\\n```json\\n[\\n  {\\n    \\"id\\": \\"policy_1\\",\\n    \\"description\\": \\"A \ubd80\uc11c\uac00 \ub9cc\ub4e0 \ud30c\uc77c\uc911 aa\ud0c0\uc785\uc758 \ud30c\uc77c\uc740 A \ubd80\uc11c\ub9cc \uc811\uadfc\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\",\\n    \\"condition\\": {\\n      \\"request\\": {\\n        \\"access\\": \\"inner\\", // \uc0ac\ub0b4\uc9c1\uc6d0\\n        \\"department\\": \\"A\\"\\n      },\\n      \\"resource\\": {\\n        \\"department\\": \\"A\\",\\n        \\"type\\": \\"aa\\"\\n      }\\n    },\\n    \\"action\\": \\"allow\\"\\n  }\\n  // \uc0dd\ub7b5\\n]\\n```\\n\\n\uc774\ub807\uac8c \ubaa8\ub4e0 \ucf00\uc774\uc2a4\ub97c \uad6c\ubd84\ud574 \ub098\uc5f4\ud574\uc8fc\ub294 \uac83\uc774\ub2e4.\\n\\n#### \uc7a5\uc810\\n\\n- \uad49\uc7a5\ud788 \uc138\ubc00\ud55c \uc811\uadfc \uc81c\uc5b4\uac00 \uac00\ub2a5\ud558\ub2e4. \uac70\uc758 \ubaa8\ub4e0 \uacbd\uc6b0\uc758 \uc218\ub97c \ud1b5\uc81c\ud560 \uc218 \uc788\ub2e4.\\n- \ub530\ub77c\uc11c \ud45c\ud604\uc131, \uc720\uc5f0\uc131\uc774 \ub9e4\uc6b0 \uc88b\ub2e4.\\n\\n#### \ub2e8\uc810\\n\\n- \uc2dc\uc2a4\ud15c \uaddc\ubaa8\uac00 \ucee4\uc9c0\uace0, \uad8c\ud55c\uc5d0 \ub300\ud55c \ucf00\uc774\uc2a4\uac00 \ub9ce\uc544\uc9c8\uc218\ub85d \uc131\ub2a5 \uc800\ud558\uac00 \uc62c \uc218 \uc788\ub2e4.\\n- \uc815\ucc45\uc744 \uc815\uc758\ud558\ub294 \ub370 \ub9ce\uc740 \uc2dc\uac04\uc774 \ud544\uc694\ud558\ub2e4.\\n\\n#### \uc6b0\ub9ac \uc11c\ube44\uc2a4\uc5d0\ub294 ...\\n\\n\ub9ac\uc18c\uc2a4\uc5d0 \ub300\ud55c \uc885\ub958\uac00 \ub2e4\uc591\ud574, \ub9ce\uc740 \ucf00\uc774\uc2a4\uc758 \uc815\uc758\uac00 \ud544\uc694\ud588\ub2e4. \uadf8\ub807\uac8c\ub418\uba74 \ub9e4\ubc88 \ubaa8\ub4e0 \uc815\ucc45\uc744 \ud6d1\uc5b4\ubd10\uc57c \ud588\ub2e4. \ubd80\uc11c\ub9cc \uba87\uc2ed\uac1c\uace0, \ud68c\uc0ac\ub294 \uba87\ucc9c\uac1c\uc600\uc5b4\uc11c \uadf8 \uc815\ucc45\uc744 \ub2e4 \ud55c\ubc88\uc529 \ubcf4\ub294 \uac83\uc740 \uc131\ub2a5\uc5d0 \ubb38\uc81c\uac00 \uc788\uc5c8\ub2e4.\\n\\n\uadf8\ub9ac\uace0 \uac1c\ubc1c\uc790\uac00 \ub098\ubfd0\uc774\ub77c.. \uc774\uac78 \uc815\uc758\ud560 \uc0ac\ub78c\ub3c4 \ub098\uc600\uace0, \uc2e4\uc218\ud558\uac8c \ub418\uba74 \uce58\uba85\uc801\uc778 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud560 \uac83\uc774\uae30 \ub54c\ubb38\uc5d0 \uacb0\uad6d \uc774\uac83\ub3c4 \ud0c8\ub77d\ud588\ub2e4.\\n\\n\uadf8\ub9ac\uace0 \uc870\uc9c1 \uac1c\ud3b8\uc2dc \ub9c8\uc774\uadf8\ub808\uc774\uc158\ub3c4 \uc815\ucc45\uc744 \ubaa8\ub450 \uc0b4\ud3b4\ubcf4\uba70 String\uc744 \ucc3e\uace0 \ubc14\uafd4\uc918\uc57c \ud558\ub294\ub370, \uc815\ucc45\uc774 \ub9ce\uc73c\uba74 \ub9c8\uc774\uadf8\ub808\uc774\uc158 \ud560\ub54c \uc2e0\ub8b0\uc131\ub3c4 \ub5a8\uc5b4\uc9c0\uace0 \uc624\ub798\uac78\ub9ac\uae30 \ub54c\ubb38\uc5d0 \uc774\uac74 \ud0c8\ub77d\ud588\ub2e4.\\n\\n### Resource Based Access Control, \ub9ac\uc18c\uc2a4 \uae30\ubc18 \uc81c\uc5b4 (\ub0b4\ub9d8\ub300\ub85c \uba85\uce6d)\\n\\n#### \ud2b9\uc9d5\\n\\n\ub9ac\uc18c\uc2a4\uc758 \uc720\ud615\uc744 \uc815\uc758\ud558\uace0, \uadf8\uac83\uc5d0 \ub300\ud574 \ud2b9\uc815 \uc720\uc800\ub97c \uac70\ubd80\ud560\uac74\uc9c0 \ud5c8\uc6a9\ud560\uac74\uc9c0 \uc124\uc815\ud574\uc8fc\ub294 \ubc29\uc2dd\uc774\ub2e4. \uc608\ub97c \ub4e4\uba74 \ub2e4\uc74c\uacfc \uac19\ub2e4. \ud68c\uc0ac \uc815\ubcf4\uac00 \uc544\ub2cc \ub2e4\ub978 \uc608\uc2dc\ub77c \uc870\uae08 \uc5c9\uc131\ud560\uc218\ub3c4 \uc788\ub2e4 ..\\n\\n```json\\n[\\n    \\"post\\": {\\n        \\"a\\": {\\n            \\"USER|kildong.hong\\": {\\n                \\"allow\\": [\\n                    \\"read\\"\\n                ],\\n                \\"deny\\": [\\n                    \\"create\\",\\n                    \\"update\\",\\n                    \\"delete\\"\\n                ]\\n            },\\n            \\"GROUP|\ud68c\uacc4\ud300\\": {\\n                // \uc0dd\ub7b5\\n            },\\n            \\"ACCESS|\uc0ac\ub0b4\uc9c1\uc6d0\\": {\\n                // \uc0dd\ub7b5\\n            }\\n        }\\n    }\\n]\\n```\\n\\n\uc774\ub807\uac8c \ub9ac\uc18c\uc2a4\uc758 \uc720\ud615\uc744 \uc815\uc758\ud558\uace0, \ud574\ub2f9 \ub9ac\uc18c\uc2a4\uc5d0 \uc811\uadfc\ud560 \uc218 \uc788\ub294 \uc720\uc800\uc640 \uadf8 \uc720\uc800\uc5d0\uac8c \ud5c8\uc6a9\ub41c \ud589\uc704 \uc815\ubcf4\ub97c \uc801\uc5b4\uc900\ub2e4.\\n\\n- a\ud0c0\uc785 post\uc5d0 \ub300\ud574 \ud64d\uae38\ub3d9 \uc720\uc800\ub294 \uc870\ud68c \uac00\ub2a5\ud558\uace0, \ub4f1\ub85d \uc218\uc815 \uc0ad\uc81c\ub294 \ud560 \uc218 \uc5c6\ub2e4.\\n\\n\uc640 \uac19\uc774 \ub9d0\uc774\ub2e4.\\n\\n#### \uc7a5\uc810\\n\\n\uc5bc\ud54f\ubcf4\uba74 ABAC\uc640 \ube44\uc2b7\ud558\ub2e4\uace0 \uc0dd\uac01\ud560 \uc218 \uc788\ub294\ub370, \uc77c\ub2e8 \ub9ac\uc18c\uc2a4\uc758 \ud0c0\uc785\ubcc4\ub85c \ub098\ub204\uc5b4 \uc131\ub2a5\uc5d4 \uc870\uae08 \ub354 \uc774\uc810\uc774 \uc788\uc744 \uc218 \uc788\ub2e4. \uadf8\ub9ac\uace0 user, group, access\uc640 \uac19\uc774 prefix\ub97c \uc815\uc758\ud558\uace0 \uc124\uc815\ud574 \uc720\uc800\uc758 \ud0c0\uc785\uc744 \uad6c\ubd84\ud55c\ub2e4. \ud55c\ub208\uc5d0 \uc5b4\ub5a4 \uc720\uc800\uc778\uc9c0, \uc5b4\ub5a4 \ud0c0\uc785\uc758 \ub9ac\uc18c\uc2a4\uc778\uc9c0 \ud30c\uc545\uc774 \uac00\ub2a5\ud558\ub2e4. \ub098\uc911\uc5d0 \uc124\uc815\uc744 \uc218\uc815\ud558\ub294 \uc5b4\ub4dc\ubbfc \ud398\uc774\uc9c0\ub97c \uac1c\ubc1c\ud55c\ub2e4\uace0 \ud574\ub3c4 \uc774 \ud3b8\uc774 \ub354 \uad00\ub9ac\ud558\uae30 \uc26c\uc6b8 \uac83\uc774\ub77c \uc0dd\uac01\ud588\ub2e4.\\n\\n#### \ub2e8\uc810\\n\\n\uc774 \uacbd\uc6b0\uc5d0\ub3c4 \uc870\uc9c1\uac1c\ud3b8\uc2dc \ub9c8\uc774\uadf8\ub808\uc774\uc158\uc744 \ud558\ub824\uba74 \ubaa8\ub4e0 \uc815\ucc45\uc744 \ud55c\ubc88 \ub2e4 \ud6d1\uc5b4\uc57c \ud55c\ub2e4.\\n\\n#### \uc6b0\ub9ac \uc11c\ube44\uc2a4\uc5d0\ub294 ...\\n\\n\uc704 \uc804\ub7b5\ub4e4\uc744 \ubaa8\ub450 \uc0b4\ud3b4\ubcf4\uc558\uc744 \ub54c, \ubcf5\uc7a1\ud55c \uc6b0\ub9ac \uc11c\ube44\uc2a4\uc758 \uc0c1\ud669\uc5d0 \uac00\uc7a5 \uc54c\ub9de\ub2e4\uace0 \uc0dd\uac01\ud588\ub2e4. \uc720\uc800\uc5d0 \ub300\ud55c DB\uc815\ubcf4\uac00 \ub514\ud3f4\ud2b8\ub85c \uc5c6\ub294 \ud658\uacbd\uc5d0\uc11c \uc720\uc800\uc758 \uc811\uc18d \uc815\ubcf4\uc640 \uc544\uc774\ub514 \ub4f1\ub9cc \ubcf4\uace0 \ud310\ub2e8\ud560 \uc218 \uc788\ub294 \uac00\uc7a5 \uc88b\uc740 \ubc29\ubc95\uc774\ub77c\uace0 \uc0dd\uac01\ud588\ub2e4.\\n\\n\ub530\ub77c\uc11c \uc774 \uc804\ub7b5\uc744 \ub354\uc6b1 \ub354 \uace0\ub3c4\ud654 \ud574 \uc801\uc6a9\ud558\uae30\ub85c \ud588\ub2e4.\\n\\n## \ub290\ub080\uc810\\n\\n\ubaa8\ub4e0 \uc804\ub7b5\uc744 \uc0b4\ud3b4\ubcfc \ub54c, \uac04\ub2e8\ud55c \uc0d8\ud50c \ucf54\ub4dc\ub97c \uc791\uc131\ud588\ub2e4. \uc815\ucc45\uc744 \uc815\uc758\ud558\uace0, \uadf8\uac83\uc744 \ud310\ub2e8\ud558\ub294 \ucf54\ub4dc\ub97c \uc791\uc131\ud574\ubcf4\uace0, \uc2e4\uc81c\ub85c \ud638\ucd9c\ub3c4 \ud574\ubcf4\uc558\ub2e4.\\n\\n\uadf8\ub807\uac8c \ud574\ubcf4\uba70 \ub0b4\uac00 \uae00\ub85c \uc801\uc740 \uc124\uacc4\uc640 \ucf54\ub4dc\uac04\uc758 \ucc28\uc774\uac00 \ubb34\uc5c7\uc778\uc9c0 \ud30c\uc545\ud560 \uc218 \uc788\uc5c8\uace0 \uc2e4\ud604 \uac00\ub2a5\uc131\uc744 \ud310\ub2e8\ud558\ub294 \ub370\uc5d0\ub3c4 \ub3c4\uc6c0\uc774 \ub418\uc5c8\ub2e4. \uc2e4\uc81c \uad6c\ud604\ud588\uc744\ub54c \ucf54\ub4dc\uac00 \uc608\uc058\uac8c \uc9dc\uc9c0\ub294\uc9c0 \uc54c \uc218 \uc788\uc5c8\ub2e4.\\n\\n\uc2dc\uac04\uc740 \uc880 \ub354 \uac78\ub838\uc744 \uc9c0\ub77c\ub3c4, \uc2e4\uc81c\ub85c \uc801\uc6a9\ud574\uc57c \ud558\ub294 \uc0ac\uc548\uc774\uae30 \ub54c\ubb38\uc5d0 \uc2e0\uc911\ud788 \uacb0\uc815\ud558\ub824\uba74 \ud544\uc694\ud55c \uacfc\uc815\uc774\uc600\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4.\\n\\n\uc774 \uc124\uacc4\ub97c \uc9c4\ud589\ud558\uba74\uc11c \uae30\uc220\uc801\uc778 \ubd80\ubd84 \ub9d0\uace0\ub3c4, \ucee4\ubba4\ub2c8\ucf00\uc774\uc158\uc5d0 \ub300\ud55c \uae68\ub2ec\uc74c\ub3c4 \uc788\uc5c8\ub2e4. \ub098\uc640 PL\ubd84 \ub458\uc774\uc11c \uc124\uacc4-\ub9ac\ubdf0\ub97c \uc9c4\ud589\ud588\ub2e4. \ub9ac\ubdf0\ub97c \uc9c4\ud589\ud558\uace0 \ub2e4\uc2dc \uc124\uacc4\ub97c \ubcf4\uc644\ud574 \uc624\ub294 \uacfc\uc815\uc5d0\uc11c, \ub9ac\ubdf0\uc5d0\uc11c\uc758 PL\ub2d8\uc758 \ub9d0\uc500\uacfc \ub098\uc758 \uc774\ud574\uac00 \ub2e4\ub978 \uacbd\uc6b0\uac00 \uc788\uc5c8\ub2e4. \ub0b4\uac00 \uc774\ud574\ud55c \uc904 \uc54c\uc558\ub294\ub370, \uc544\ub2c8\uc600\ub358 \uac70\ub2e4. \uc774\uac78 \ud655\uc778\ud558\ub294 \uacfc\uc815\uc774 \uc5c6\ub2e4\uba74 \uadf8\uac78 \ubc14\ud0d5\uc73c\ub85c \uc124\uacc4\ud558\uace0, \ub2e4\ub978 \uac83\uc744 \ub2e4\uc74c \ub9ac\ubdf0\ub54c \ud655\uc778\ud558\uae30 \ub54c\ubb38\uc5d0 \uc2dc\uac04\uc744 \ub0ad\ube44\ud558\uac8c \ub418\uc5c8\ub2e4.\\n\\n\ud55c\ubc88 \uadf8\ub7f0 \ub4a4\ub85c\ub294 \ub9ac\ubdf0 \ubc1b\uc740 \ud6c4 \ub0b4\uac00 \uc774\ud574\ud55c \uac83\uc774 \ub9de\ub294\uc9c0 \uc815\ub9ac\ud574\uc11c \ud55c\ubc88 \ud655\uc778\uc744 \uc694\uccad\ub4dc\ub838\ub2e4. \uc774\ub807\uac8c \ud558\ub2c8\uae4c \ub098\ub3c4 \ub354 \uc548\uc2ec\uc774 \ub418\uc5c8\uace0 \uc4f8\ub370\uc5c6\ub294 \uc2dc\uac04 \ub0ad\ube44\ub97c \uc904\uc77c \uc218 \uc788\uc5c8\ub2e4.\\n\\n\uc774 \uc124\uacc4\ub97c \uacbd\ud5d8\ud558\uba74\uc11c \uc2dc\uc2a4\ud15c\uc5d0 \ub300\ud55c \uc774\ud574\ub3c4\ub3c4 \ub354 \ub192\uc544\uc84c\ub2e4. \uadf8\ub3d9\uc548\uc740 \ubb49\ub6b1\uadf8\ub824 \uc54c\uace0\uc788\uc5c8\ub358 \uac83\ub4e4\uc744 \ud655\uc2e4\ud788 \ubb38\uc11c\ub85c \uc801\uc5b4\ubcf4\uba74\uc11c, \uc815\ud655\ud558\uac8c \ud30c\uc545\ud560 \uc218 \uc788\uc5c8\ub2e4. \uadf8\ub9ac\uace0 \ud558\ub4dc\ucf54\ub529\ub41c \uc18c\uc2a4\ub4e4\uc744 \uc0b4\ud3b4\ubcf4\uba70 \ubc84\uadf8\ub3c4 \ubc1c\uacac\ud560 \uc218 \uc788\uc5c8\ub2e4.\\n\\n\ud300\uc5d0 \uc6f9\uac1c\ubc1c\uc790\uac00 \uc5c6\uc5b4(\uc9c0\uae08\uc740 \ub098\ubfd0,,) \uc5b4\ub824\uc6b8 \ub54c\ub3c4 \uc788\uc9c0\ub9cc, 1\ub144\ucc28\ub3c4 \uc548\ub418\uc5c8\ub294\ub370 \uc774\ub7f0 \uc124\uacc4\ub3c4 \uacbd\ud5d8\ud560 \uc218 \uc788\ub294 \uac83\uc740 \uc7a5\uc810\uc774\uace0, \ub610 \ub0b4 \uc758\uacac\uc744 \ud45c\ucd9c\ud574\ubcfc \uc218 \uc788\ub294 \uac83\ub3c4 \uc7a5\uc810\uc774\ub2e4. (\uc2dc\uac04\uc0c1 \ubabb\ud558\ub294\uac8c \ub354 \ub9ce\uc9c0\ub9cc ..) \uc544\ubb34\ud2bc \uc88b\uc740 \uacbd\ud5d8\uc774\uc600\ub2e4. \uad8c\ud55c\uc774\ub77c \ud568\uc740 \ubb34\uc870\uac74 ROLE\\\\_ \uc774\ub807\uac8c \uc124\uc815\ud558\ub294 \uc904 \uc54c\uc558\ub294\ub370 \uc0c8\ub85c\uc6b4 \uc811\uadfc \ubc29\uc2dd\uc744 \ub9ce\uc774 \uc54c\uac8c \ub418\uc5c8\ub2e4. \uc5bb\uc5b4\uac00\ub294 \uac8c \uaf64 \ub9ce\uc558\ub358 \uacbd\ud5d8\uc774\ub2e4.\\n\\n# \ub808\ud37c\ub7f0\uc2a4\\n\\n- https://www.samsungsds.com/kr/insights/cloud_platform_manage.html\\n- https://www.okta.com/kr/identity-101/role-based-access-control-vs-attribute-based-access-control/"},{"id":"/2024/02/04/troubleshooting-express-no-response","metadata":{"permalink":"/blog/2024/02/04/troubleshooting-express-no-response","source":"@site/blog/2024-02-04-troubleshooting-express-no-response.md","title":"[TroubleShooting] express\uac00 \uc751\ub2f5\uc744 \ubcf4\ub0b4\uc9c0 \uc54a\uc558\ub2e4. \uc65c..?","description":"\uc5d0\ub7ec\uac00 \ubc1c\uc0dd\ud558\uc9c0 \uc54a\uc558\ub294\ub370, \ud074\ub77c\uc774\uc5b8\ud2b8\ub294 \uc11c\ubc84\uc758 \uc751\ub2f5\uc744 \ubc1b\uc9c0 \ubabb\ud588\ub2e4. \uc774\uc720\ub97c \ucc3e\uc544\ubcf4\uc790 !","date":"2024-02-04T00:00:00.000Z","formattedDate":"February 4, 2024","tags":[{"label":"troubleshooting","permalink":"/blog/tags/troubleshooting"},{"label":"express","permalink":"/blog/tags/express"},{"label":"nginx","permalink":"/blog/tags/nginx"}],"readingTime":7.09,"hasTruncateMarker":true,"authors":[],"frontMatter":{"title":"[TroubleShooting] express\uac00 \uc751\ub2f5\uc744 \ubcf4\ub0b4\uc9c0 \uc54a\uc558\ub2e4. \uc65c..?","description":"\uc5d0\ub7ec\uac00 \ubc1c\uc0dd\ud558\uc9c0 \uc54a\uc558\ub294\ub370, \ud074\ub77c\uc774\uc5b8\ud2b8\ub294 \uc11c\ubc84\uc758 \uc751\ub2f5\uc744 \ubc1b\uc9c0 \ubabb\ud588\ub2e4. \uc774\uc720\ub97c \ucc3e\uc544\ubcf4\uc790 !","tags":["troubleshooting","express","nginx"],"hide_table_of_contents":false},"unlisted":false,"prevItem":{"title":"\ud1b5\ud569 \uc811\uadfc \uc81c\uc5b4 \uc804\ub7b5\uc744 \uc124\uacc4 \ud574\ubcf4\ub2e4.","permalink":"/blog/2024/03/11/design-access-control-strategy"},"nextItem":{"title":"\uc624\ud508\uc18c\uc2a4 \uccab \uae30\uc5ec \u2728","permalink":"/blog/2024/01/29/first-opensearch-contribute-docker-compose"}},"content":"## \ubb38\uc81c \uc0c1\ud669\\n\\n\ubb38\uc81c \uc0c1\ud669\uc774 \ubb34\uc5c7\uc778\uc9c0 \uc124\uba85\ud558\uae30 \uc804\uc5d0, \uc6b0\ub9ac \uc11c\ube44\uc2a4\ub294 \ud30c\uc77c\uc744 `\ub4f1\ub85d` \ud558\ub294 \uae30\ub2a5\uc774 \uc788\uace0, \ub611\uac19\uc740 \ud30c\uc77c\uc744 \uc911\ubcf5\uc73c\ub85c \ub4f1\ub85d\ud560 \uc218 \uc5c6\ub2e4. \uc911\ubcf5 \ub4f1\ub85d\uc744 \uc2dc\ub3c4\ud560 \uacbd\uc6b0\uc5d4 \uba54\uc138\uc9c0\uac00 \ub728\uba70 \ub4f1\ub85d\uc774 \uc548\ub41c\ub2e4.\\n\\n\uadf8\ub7f0\ub370 \uc0ac\uc6a9\uc790\ub85c\ubd80\ud130 \uc774\ub7f0 \uc5f0\ub77d\uc744 \ubc1b\uc558\ub2e4.\\n\\n> \ud30c\uc77c\uc744 \uc5c5\ub85c\ub4dc \ud558\uace0, `\ub4f1\ub85d` \ubc84\ud2bc\uc744 \ub20c\ub800\ub294\ub370 **\ub4f1\ub85d\uc5d0 \uc2e4\ud328** \ud588\ub2e4\ub294 \uba54\uc2dc\uc9c0\uac00 \ub5b4\uc2b5\ub2c8\ub2e4.<br/>\\n> \uadf8\ub798\uc11c \ub2e4\uc2dc \ub4f1\ub85d\uc744 \uc2dc\ub3c4\ud588\ub294\ub370, **\uc911\ubcf5 \ub4f1\ub85d** \uc774\ub77c\uace0 \ud558\uba70 \ub4f1\ub85d\uc774 \ub418\uc9c0 \uc54a\uc558\uc2b5\ub2c8\ub2e4. <br/>\\n> \ud655\uc778\ud574\ubcf4\ub2c8 \ucc98\uc74c\uc5d0 \ub4f1\ub85d\ud55c \ub9ac\ud3ec\ud2b8\uac00 \uc798 \ub4f1\ub85d\uc774 \ub418\uc5b4 \uc788\ub124\uc694?\\n\\n\uadf8\ub7ec\ub2c8\uae4c, \uc2e4\ud328\ud588\ub2e4\ub294 \uba54\uc138\uc9c0\uac00 \ub5b4\ub294\ub370 \uc2e4\uc81c\ub860 \uc131\uacf5\ud588\ub2e4\ub294 \uac70\ub2e4.\\n\\n\x3c!-- truncate --\x3e\\n\\n## \uc99d\uc0c1 \ud30c\uc545\\n\\n![](../static/img/post-img/diagram1.png)\\n\\n\ub4f1\ub85d \uae30\ub2a5 \ud504\ub85c\uc138\uc2a4\ub294 \uc774\ub807\ub2e4. express\uc5d0\uc11c \uc694\uccad\uc744 \uc804\ub2ec\ud558\uace0, spring\uc5d0\uc11c \ub4f1\ub85d \uacfc\uc815\uc744 \uc218\ud589\ud558\uace0, \uadf8\uac83\uc744 \ub2e4\uc2dc express\uc5d0 \ub118\uae30\uace0, express\ub294 \ud074\ub77c\uc774\uc5b8\ud2b8\uc5d0 \uc644\ub8cc\ub97c \uc804\ub2ec\ud55c\ub2e4.\\n\\n\ub85c\uadf8\ub97c \uc0b4\ud3b4\ubcf4\ub2c8, **express\uac00 client\ub85c \ub4f1\ub85d \uc644\ub8cc \uc804\ub2ec** \ud558\ub294 \ubd80\ubd84 \uc678\uc5d0\ub294 \ubaa8\ub450 success, 200\uc73c\ub85c \uc5d0\ub7ec \uc5c6\uc774 \uc798 \uc218\ud589\ub418\uc5c8\ub2e4.\\n\\n![](../static/img/post-img/diagram2.png)\\n\\n**express\uac00 client\ub85c \ub4f1\ub85d \uc644\ub8cc \uc804\ub2ec** \ud558\ub294 \ubd80\ubd84\uc740 ? \ub85c \ub0a8\uc558\ub2e4. \uc774\ub7f0 \ub85c\uadf8\ub97c \ubc1c\uacac\ud588\uae30 \ub54c\ubb38\uc774\ub2e4.\\n\\n```\\n[\uc2dc\uac04] \\"POST /api/*** HTTP/1.1\\" - - \\"\ucd9c\ucc98\\" \\"\ud074\ub77c\uc774\uc5b8\ud2b8 \uc815\ubcf4\\" - - ms\\n```\\n\\n\uc774 \ub85c\uadf8\ub294 express\uc758 [morgan](https://expressjs.com/en/resources/middleware/morgan.html) \ubbf8\ub4e4\uc6e8\uc5b4\uac00 \ub0a8\uae34 \ub85c\uadf8\uc774\ub2e4. \uc6d0\ub798\ub77c\uba74 \uc544\ub798\ucc98\ub7fc \uc751\ub2f5\uc2dc\uac04, \uc751\ub2f5\ud06c\uae30, \uac78\ub9b0\uc2dc\uac04\uc774 \ud45c\uc2dc \ub418\uc5b4\uc57c \ud55c\ub2e4.\\n\\n```\\n[\uc2dc\uac04] \\"POST /api/*** HTTP/1.1\\" 200 49 \\"\ucd9c\ucc98\\" \\"\ud074\ub77c\uc774\uc5b8\ud2b8 \uc815\ubcf4\\" - 317.922 ms\\n```\\n\\n\ubb54\uac00 \uc751\ub2f5\uc774 \uc815\uc0c1\uc801\uc73c\ub85c \ucc98\ub9ac\ub418\uc9c0 \uc54a\uc740 \ub4ef \ud588\ub2e4.\\n\\n\uadf8\ub7ec\ub098 \ubbf8\uc2a4\ud14c\ub9ac\uc778 \uc810\uc740 .. express\uac00 `await` \uc73c\ub85c spring\uc758 \uc751\ub2f5\uc744 \uae30\ub2e4\ub9ac\uace0, \uadf8 \ub4a4\uc5d0 \uc131\uacf5\ud588\uc744\ub550 \ub85c\uadf8\ub97c \ub0a8\uae30\uac8c \ub418\uc5b4 \uc788\ub294\ub370, \uadf8 \ub85c\uadf8\ub294 \ub0a8\uc558\ub2e4\ub294 \uac83\uc774\ub2e4.\\n\\nexpress\uc5d0 \uc5d0\ub7ec\ub294 \uc5c6\uc5c8\uace0, spring\uc5d0\ub3c4 \uc5d0\ub7ec\ub294 \uc5c6\uc5c8\ub294\ub370, \ud074\ub77c\uc774\uc5b8\ud2b8\uc5d0 \uc751\ub2f5 \uc804\uc1a1\ub9cc \uc815\uc0c1\uc801\uc73c\ub85c \ub418\uc9c0 \uc54a\uc558\ub2e4.\\n\\n\ucc3e\uc544\ubcf4\ub2c8, \uc704 -- \ub85c \ucc98\ub9ac\ub41c \ub85c\uadf8\ub294 express\uac00 \uc751\ub2f5 \uc790\uccb4\ub97c \ud558\uc9c0 \uc54a\uc558\uc744 \ub54c \uc77c\uc5b4\ub098\ub294 \uc77c\uc774\ub77c\uace0 \ud55c\ub2e4.\\n\\n> https://stackoverflow.com/questions/39728057/no-request-status-in-node-js-morgan-logging > https://github.com/expressjs/morgan/issues/121\\n\\n\uc704 \ub9c1\ud06c\uc5d0\uc11c \ucd94\uc815\ud55c \uc6d0\uc778\uc740 \ub2e4\uc74c\uacfc \uac19\ub2e4.\\n\\n:::tip[\uc544\ub9c8\ub3c4..]\\n\\nresponse\ub97c \ubcf4\ub0b4\uae30 \uc804\uc5d0 TCP \uc5f0\uacb0\uc774 \ub04a\uc5b4\uc9c4 \ub4ef!\\n\\n:::\\n\\n\ub85c\uadf8 \uc2dc\uac04\uc744 \ud30c\uc545\ud574\ubcf4\ub2c8 \ud074\ub77c\uc774\uc5b8\ud2b8 \uc694\uccad ~ spring\uc758 \uc751\ub2f5 \uae4c\uc9c0\ub294 1\ubd84 5\ucd08 \ub0b4\uc678\ub85c \uc18c\uc694\ub418\uc5c8\ub2e4.\\n\\n\uadf8\ub807\ub2e4\uba74 \uc5b4\ub514\uc11c TCP \uc5f0\uacb0\uc744 \ud574\uc81c\ud588\uc744\uae4c?\\n\\n## \uc6d0\uc778 \ud30c\uc545\\n\\n\ub0b4\uac00 \uc758\uc2ec\ud55c \uac83\ub4e4\uc740 \ub2e4\uc74c\uacfc \uac19\ub2e4.\\n\\n- axios timeout\\n- node.js timeout\\n- OS\uc758 TCP timeout\\n- L4 \uc2a4\uc704\uce58 timeout\\n- Nginx timeout\\n\\n### axios timeout\\n\\n\ub530\ub85c \uc124\uc815\ud55c \uac83\uc774 \uc5c6\ub2e4. [default\ub294 no timeout](https://github.com/axios/axios/blob/main/README.md#request-config) \uc774\ub2e4.\\n\\n### node.js timeout\\n\\n\ub178\ub4dc13 \ubc84\uc804 \uc774\ud6c4\ub85c\ub294 no timeout\uc774\ub2e4.\\n\\n\ub178\ub4dc8 \ubc84\uc804\uc744 \uc4f0\ub294 \uc6b0\ub9ac \ud504\ub85c\uc81d\ud2b8\ub294 120s\uac00 \uae30\ubcf8\uc73c\ub85c \uc124\uc815 \ub418\uc5b4 \uc788\ub2e4. (\ucc98\uc74c\uc54c\uc558\ub2e4 ..)\\n\\n> https://nodejs.org/api/http.html#servertimeout\\n\\n\uadf8\ub7ec\ub098 2\ubd84 \ubcf4\ub2e4 \uc624\ub798 \uac78\ub9ac\uc9c4 \uc54a\uc558\uc73c\ubbc0\ub85c \uc774\uac83\ub3c4 \uc544\ub2c8\ub2e4.\\n\\n### OS\uc758 TCP timeout\\n\\n```\\ncat /proc/sys/net/ipv4/tcp_keepalive_time\\n```\\n\\n- tcp_keepalive : tcp \uc5f0\uacb0\uc774 \ub418\uace0 \ub098\uc11c \uc5bc\ub9c8\ub098 \uadf8 \uc5f0\uacb0\uc744 \uc9c0\uc18d\ud558\ub294\uc9c0\\n\\n7200(2\uc2dc\uac04) \uc73c\ub85c \ub110\ub110\ud588\ub2e4.\\n\\n### L4 \uc2a4\uc704\uce58\\n\\nL4 \uc5d0\uc11c\ub3c4 \ubb54\uac00 \ud0c0\uc784\uc544\uc6c3\uc774 \uac78\ub824\uc788\ub294\uac83\uc740 \uc544\ub2d0\uae4c..? \ud558\uace0 \ud574\ub2f9 \uc0ac\ud56d\uc744 \uc778\ud504\ub77c\ud300\uc5d0 \ubb38\uc758\ud588\ub294\ub370\\n\\n\uc544\ub2c8\ub2e4. (~\uc758\uc2ec\ud574\uc11c \uc8c4\uc1a1\ud569\ub2c8\ub2e4...~)\\n\\n\uc804\uc5d0\ub3c4 L4 \uc124\uc815 \ubb38\uc81c\ub54c\ubb38\uc5d0 \uc548\ub41c \uae30\ub2a5\uc774 \uc788\uc5b4\uc11c, \uc0dd\uac01\ub0ac\uc5c8\ub294\ub370 .. \uc5b4\uca0c\ub4e0 \uc774\uac8c \ubb38\uc81c\ub294 \uc544\ub2c8\uc600\ub2e4.\\n\\n### Nginx timeout\\n\\n```nginx title=\\"/etc/nginx/nginx.conf\\"\\nproxy_connection_timeout\\nproxy_send_timeout\\nproxy_read_timeout\\nsend_timeout\\n```\\n\\n\uc5ec\uae30\uc600\ub2e4 !!\\n\\nnginx\ub85c \ub9ac\ubc84\uc2a4 \ud504\ub85d\uc2dc \uc124\uc815\uc774 \ub418\uc5b4 \uc788\ub294\ub370, [nginx \uacf5\uc2dd\ubb38\uc11c](https://nginx.org/en/docs/http/ngx_http_proxy_module.html)\ub97c \ubcf4\uba74 \uc704 \uc124\uc815\ub4e4\uc758 \uae30\ubcf8 timeout\uc774 60s\ub85c \uc7a1\ud600\uc788\ub2e4.\\n\\n\uc774 \uc0ac\uc2e4\uc744 \uc54c\uace0 \ub09c \ub4a4 nginx \uc5d0\ub7ec \ub85c\uadf8\ub97c \ubcf4\ub2c8 (\ub85c\uadf8 \uacbd\ub85c\ub294 nginx.conf \uc5d0 \uc788\ub2e4.)\\n\\n```text title=\\"error.log\\"\\n[error] upstream timed out (110: Connection timed out) while reading response header from upstream .. \uc0dd\ub7b5\\n```\\n\\nconnection timeout \uc5d0\ub7ec \ub85c\uadf8\uac00 \ucc0d\ud614\ub2e4.\\n\\nspring\uc774 express\ub85c \uc751\ub2f5\uc744 \ubcf4\ub0b4, express\uac00 client\uc5d0 \uc751\ub2f5\ud558\uae30 \uc804\uc5d0 client\uc640 express\uac04\uc758 \uc5f0\uacb0\uc774 \ub04a\uc5b4\uc838 client\uac00 \uc751\ub2f5\uc744 \ubc1b\uc9c0 \ubabb\ud55c \uac83\uc774\ub2e4.\\n\\n\ud56d\uc0c1 \ub3c4\ucee4\uac00 \uc218\uc9d1\ud55c express \ub85c\uadf8\ub9cc \ub4e4\uc5ec\ub2e4\ubd24\uc9c0, nginx\uc758 \ub85c\uadf8\ub97c \ubd10\uc57c\ud55c\ub2e4\ub294 \uc0ac\uc2e4\uc744 \uc778\uc9c0\ud558\uc9c0 \ubabb\ud588\ub2e4.\\n\\n## \ud574\uacb0\\n\\n\ud574\uacb0 \ubc29\ubc95\uc73c\ub85c\ub294 \uc774\ub7f0 \uac83\ub4e4\uc774 \uc788\uaca0\ub2e4.\\n\\n- timeout \ub298\ub9ac\uae30\\n- upstream\uc778 spring \uc11c\ubc84\uc758 \ucc98\ub9ac \uc131\ub2a5 \ud5a5\uc0c1\ud558\uae30 (best)\\n\\n\uadf8\ub9ac\uace0 nginx \ub85c\uadf8\ub294 \ub3c4\ucee4 \ucee8\ud14c\uc774\ub108 \uc548\uc5d0 \uc788\uc5b4\uc11c, \ucee8\ud14c\uc774\ub108\uac00 \ub0b4\ub824\uac00\uba74 \ub85c\uadf8 \ud30c\uc77c\uc774 \ub0a0\uc544\uac00 \ubcfc \uc218 \uc5c6\ub2e4.\\n\ub3c4\ucee4 \ucee8\ud14c\uc774\ub108\uc5d0\uc11c \uc313\uc740 \ub85c\uadf8\ub294 volume\uc73c\ub85c \uc5f0\uacb0\ud574 host\uc5d0\ub3c4 \uc313\uac8c \ud574\ub1a8\ub2e4. nginx \ub85c\uadf8\ub3c4 \ud45c\uc900 \ucd9c\ub825\uc73c\ub85c \ub0b4\ubcf4\ub0b4 (/dev/stdout \uc73c\ub85c \uc2ec\ubcfc\ub9ad \ub9c1\ud06c \uac78\uae30) \ub3c4\ucee4\uac00 \ub85c\uadf8\ub97c \uc218\uc9d1\ud558\uac8c \ud558\uac70\ub098 nginx \ub85c\uadf8\ub97c volume\uc73c\ub85c \uc124\uc815\ud574 \ubcf4\uad00 \ud560 \ud544\uc694\uac00 \uc788\ub2e4.\\n\\n\uc77c\ub2e8 timeout\uc744 \ub298\ub824\uc11c \uae09\ud55c \ubd88\uc744 \ub044\uace0, spring \uc11c\ubc84\uc758 \ucc98\ub9ac \uc131\ub2a5\uc744 \ub192\uc77c \ubc29\ubc95\uc744 \uc0dd\uac01 \ud574 \ubcfc \uac83\uc774\ub2e4.\\n\\n\uc801\uace0\ubcf4\ub2c8 \ubcc4\uc77c \uc544\ub2cc \ub4ef \ud558\uc9c0\ub9cc,, \uc758\ubbf8 \uc788\uc5c8\ub358 \uacfc\uc815\uc774\uc600\ub2e4.\\n\\n\uc774 \uc774\uc288\ub85c \uc778\ud574 \uc6b0\ub9ac \uc11c\ube44\uc2a4\uc758 \uc5b4\ub5a4 \ubd80\ubd84\uc5d0 timeout\uc774 \uc5bc\ub9cc\ud07c \uac78\ub824\uc788\ub294\uc9c0 \ud655\uc778 \ud560 \uc218 \uc788\uc5c8\uace0, \ub85c\uadf8\ub3c4 \uc880 \ub354 \ubcf4\ucda9\ud560 \uc218 \uc788\uc5c8\ub2e4. \ub2e4\uc74c\uc5d0 \uc774\ub7ec\ud55c \uc77c\uc774 \ubc1c\uc0dd\ud558\uba74 \uc774 \uacbd\ud5d8\uc73c\ub85c \uc778\ud574 \uc6d0\uc778 \ud30c\uc545\uc744 \uc870\uae08\uc740 \ube68\ub9ac \ud560 \uc218 \uc788\uc744\uae4c \uae30\ub300\ud574\ubcf4\uba70 \uae00\uc744 \ub9c8\uce5c\ub2e4."},{"id":"/2024/01/29/first-opensearch-contribute-docker-compose","metadata":{"permalink":"/blog/2024/01/29/first-opensearch-contribute-docker-compose","source":"@site/blog/2024-01-29-first-opensearch-contribute-docker-compose.md","title":"\uc624\ud508\uc18c\uc2a4 \uccab \uae30\uc5ec \u2728","description":"\uc5d0\ub7ec\uac00 \ubc1c\uc0dd\ud558\uc9c0 \uc54a\uc558\ub294\ub370, \ud074\ub77c\uc774\uc5b8\ud2b8\ub294 \uc11c\ubc84\uc758 \uc751\ub2f5\uc744 \ubc1b\uc9c0 \ubabb\ud588\ub2e4. \uc774\uc720\ub97c \ucc3e\uc544\ubcf4\uc790 !","date":"2024-01-29T00:00:00.000Z","formattedDate":"January 29, 2024","tags":[{"label":"docker","permalink":"/blog/tags/docker"},{"label":"opensource-contributions","permalink":"/blog/tags/opensource-contributions"}],"readingTime":1.925,"hasTruncateMarker":true,"authors":[],"frontMatter":{"title":"\uc624\ud508\uc18c\uc2a4 \uccab \uae30\uc5ec \u2728","description":"\uc5d0\ub7ec\uac00 \ubc1c\uc0dd\ud558\uc9c0 \uc54a\uc558\ub294\ub370, \ud074\ub77c\uc774\uc5b8\ud2b8\ub294 \uc11c\ubc84\uc758 \uc751\ub2f5\uc744 \ubc1b\uc9c0 \ubabb\ud588\ub2e4. \uc774\uc720\ub97c \ucc3e\uc544\ubcf4\uc790 !","tags":["docker","opensource-contributions"],"hide_table_of_contents":false},"unlisted":false,"prevItem":{"title":"[TroubleShooting] express\uac00 \uc751\ub2f5\uc744 \ubcf4\ub0b4\uc9c0 \uc54a\uc558\ub2e4. \uc65c..?","permalink":"/blog/2024/02/04/troubleshooting-express-no-response"},"nextItem":{"title":"[Java] \uc5d0\ub7ec \uba54\uc2dc\uc9c0, Stacktrace\uac00 \uc0dd\ub7b5\ub418\ub294 \ubb38\uc81c","permalink":"/blog/2024/01/02/java-no-stacktrace"}},"content":"\ube44\ub85d \uacf5\uc2dd\ubb38\uc11c\uc758 \uc624\ub958\ub97c \uace0\uce58\ub294 \uc77c\uc774\uc600\uc9c0\ub9cc,\\n\\n\ucc98\uc74c\uc73c\ub85c \uc624\ud508\uc18c\uc2a4\uc5d0 \uae30\uc5ec\ud588\uace0 \ubb34\ub824 Docker \ubb38\uc11c \uc774\ub2e4!!\\n\\n\uba38\uc9c0 \ub418\uc5c8\uc744\ub54c\ub294 \uc815\ub9d0 \ubfcc\ub4ef\ud588\uace0 \uc774\uc288\ub97c \uc0dd\uc131\ud558\uace0 PR\uc744 \ub0a0\ub9ac\ub294 \uacfc\uc815\ub3c4 \uc7ac\ubc0c\uc5c8\ub2e4.\\n\\n\x3c!-- truncate --\x3e\\n\\n## \ubb38\uc81c \ubc1c\uacac, \uc774\uc288 \uc0dd\uc131\\n\\n![](../static/img/post-img/20240205204509.png)\\n\\n\ubb38\uc11c\uc5d0 \ud2c0\ub9b0 \ubd80\ubd84\uc744 \ubc1c\uacac\ud558\uace0, \uc774\uc288\ub97c \uc0dd\uc131\ud588\ub2e4.\\n\\n\uc218\uc815\uc774 \ud544\uc694\ud55c\uac8c \ub9de\ub2e4\uba74 \ub0b4\uac00 \uc791\uc5c5\ud574\ub3c4 \ub418\ub294\uc9c0 \ubb3c\uc5b4\ubd24\ub2e4.\\n\\n![](../static/img/post-img/20240205204536.png)\\n\\nassgin \ud574\uc8fc\uc168\uace0, \ubb38\uc11c \uc704\uce58\uac00 \ubc14\ub00c\uc5c8\uc73c\ub2c8 compose-spec \ub808\ud3ec\uc9c0\ud1a0\ub9ac\uc5d0\uc11c PR\uc744 \ud574\ub2ec\ub77c\ub294 \uce5c\uc808\ud55c \uac00\uc774\ub4dc\uae4c\uc9c0 \uc8fc\uc168\ub2e4.\\n\\n## Pull Request \uc0dd\uc131\ud558\uae30\\n\\n\uba3c\uc800 compose-spec\uc744 fork\ud558\uace0, \uc18c\uc2a4\ucf54\ub4dc \uc218\uc815 \ud6c4 commit \ud588\ub2e4.\\n\\n![](../static/img/post-img/20240205204610.png)\\n\\n\ub2e4\uc2dc upstream\uc73c\ub85c\xa0\uc640\uc11c pull request\ub97c \uc0dd\uc131\ud588\ub2e4.\\n\\n![](../static/img/post-img/20240205204624.png)\\n\\n\uc774\uac74 \ub0b4\uac00 CONTRIBUTING.md\ub97c \uaf3c\uaf3c\ud788 \ubcf4\uc9c0 \uc54a\uc544 \uc0dd\uae34 \uc77c\uc774\ub2e4. \u315c\u315c\\n\\n\uadf8\ub0e5 \ucee4\ubc0b\ud560\ub54c \uacc4\uc815 \uc815\ubcf4\ub97c \ud655\uc778\ud574\ub2ec\ub77c\ub294 \ub9d0\uc778 \uc904 \uc54c\uc558\ub294\ub370,\\n\\n\ucee4\ubc0b \uba54\uc138\uc9c0\uc5d0 sign-off\ub97c \ud3ec\ud568\ud558\uc9c0 \uc54a\uc73c\uba74 DCO check\uc5d0 \uc2e4\ud328 \ud558\uba74\uc11c \uc624\ub958\uac00 \ub09c\ub2e4.\\n\\n\uce5c\uc808\ud788 \uc54c\ub824\uc8fc\uc154\uc11c \ucee4\ubc0b \uba54\uc138\uc9c0 \uc218\uc815 \ud6c4 \uac15\uc81c \ud478\uc2dc\ud588\uace0,\\n\\n\uc774\ud6c4 \ube4c\ub4dc\uc5d0 \ud55c \ubc88 \uc2e4\ud328\ud574 \ud55c\uac00\uc9c0 \uc218\uc815\uc744 \ub354 \ud558\uace0 \uba38\uc9c0\ub418\uc5c8\ub2e4.\\n\\n![](../static/img/post-img/20240205204639.png)\\n\\n![](../static/img/post-img/20240205204646.png)\\n\\n\uc774\ub807\uac8c\xa0\uc624\ud508\uc18c\uc2a4 \uae30\uc5ec\uc5d0 \uc791\uc9c0\ub9cc \uc704\ub300\ud55c \uccab \uac78\uc74c\uc744 \ub0b4\ub51b\uc5c8\ub2e4.\\n\\n\uc55e\uc73c\ub85c\ub3c4 \ubb38\uc11c \ubfd0\ub9cc \uc544\ub2c8\ub77c \ucf54\ub4dc \ub808\ubca8\uae4c\uc9c0 .. \ub354 \ub9ce\uc740 \uc624\ud508\uc18c\uc2a4\uc5d0 \uae30\uc5ec\ud560 \uc218 \uc788\uac8c \ub418\uba74 \uc88b\uaca0\ub2e4."},{"id":"/2024/01/02/java-no-stacktrace","metadata":{"permalink":"/blog/2024/01/02/java-no-stacktrace","source":"@site/blog/2024-01-02-java-no-stacktrace.md","title":"[Java] \uc5d0\ub7ec \uba54\uc2dc\uc9c0, Stacktrace\uac00 \uc0dd\ub7b5\ub418\ub294 \ubb38\uc81c","description":"NullPointerException\uc758 \uc5d0\ub7ec \uba54\uc2dc\uc9c0\uac00 \ube48 \ucc44\ub85c \ucd9c\ub825\ub41c\ub2e4","date":"2024-01-02T00:00:00.000Z","formattedDate":"January 2, 2024","tags":[{"label":"java","permalink":"/blog/tags/java"}],"readingTime":4.155,"hasTruncateMarker":true,"authors":[],"frontMatter":{"title":"[Java] \uc5d0\ub7ec \uba54\uc2dc\uc9c0, Stacktrace\uac00 \uc0dd\ub7b5\ub418\ub294 \ubb38\uc81c","description":"NullPointerException\uc758 \uc5d0\ub7ec \uba54\uc2dc\uc9c0\uac00 \ube48 \ucc44\ub85c \ucd9c\ub825\ub41c\ub2e4","tags":["java"]},"unlisted":false,"prevItem":{"title":"\uc624\ud508\uc18c\uc2a4 \uccab \uae30\uc5ec \u2728","permalink":"/blog/2024/01/29/first-opensearch-contribute-docker-compose"}},"content":"## \ubb38\uc81c \uc0c1\ud669\\n\\n\ud14c\uc2a4\ud2b8 \uc2a4\ud06c\ub9bd\ud2b8\ub97c \ubc30\ud3ec\uc2dc\uc5d0 \uc2e4\ud589\ud558\uac8c \ub418\uc5b4 \uc788\ub294\ub370, \ud574\ub2f9 \uc2a4\ud074\ub9bd\ud2b8\ub294 \uac19\uc740 api\ub97c \uc5ec\ub7ec case\ub85c \uba87\uc2ed\ubc88 \ud638\ucd9c\ud55c\ub2e4.\\n\\n\ucd5c\ucd08 \uc2e4\ud589\uc2dc\uc5d0\ub294 \uc131\uacf5\ud558\uace0,\\n\\n\ub450\ubc88\uc9f8 \uc774\ud6c4\ubd80\ud134 \uc2e4\ud328\ud55c\ub2e4.\\n\\n\x3c!-- truncate --\x3e\\n\\n\uc9c0\uae08\uae4c\uc9c0\ub294 \uc11c\ube44\uc2a4 \uc2e4\ud589\uc2dc \ud55c\ubc88\ub9cc \uc2e4\ud589\ud588\uc5b4\uc11c \ubc1c\uacac\uc744 \ubabb \ud55c \ubb38\uc81c\uc600\ub2e4.\\n\\n\uc5d0\ub7ec\uac00 \ubc1c\uc0dd\ud55c \uc9c0\uc810\uc740 \uc5ec\uae30\uc600\ub2e4.\\n\\n```java\\ncatch (Exception e) {\\n    String msg = e.getMessage(); // errMsg: null\\n    if (msg.equalsIgnoreCase(\\"XXX\\")) { // NullPointerException \ubc1c\uc0dd!\\n        //\\n    }\\n}\\n```\\n\\ntry ~ catch \ubb38\uc5d0\uc11c \uc5d0\ub7ec \uba54\uc138\uc9c0\ub97c \uac00\uc838\uc640 \uc5b4\ub5a4 \uc5d0\ub7ec \uba54\uc2dc\uc9c0\uc778\uc9c0 \ud310\ub2e8\ud558\ub824\uace0 \ud560 \ub54c, `e.getMessage()` \uacb0\uacfc\uac00\xa0**null**\xa0\uc774 \ub418\uc5b4 catch \uad6c\ubb38\uc5d0\uc11c \ub2e4\uc2dc \uc5d0\ub7ec\uac00 \ubc1c\uc0dd\ud558\ub294 \uac83\uc774\uc600\ub2e4.\\n\\n\ub85c\uc9c1\uc744 \uc0b4\ud3b4\ubcf4\ub2c8 \ud2b9\uc815 case\uc5d4 \ubb34\uc870\uac74 try\ucabd\uc5d0\uc11c NPE\uac00 \ubc1c\uc0dd\ud558\uace0, catch\ubb38\uc73c\ub85c \ub4e4\uc5b4\uc624\uac8c \ub418\uc5b4\uc788\uc5c8\ub2e4.\\n\\n\uadfc\ub370 \uc65c \ucd5c\ucd08 \uc2e4\ud589\uc2dc\uc5d4 msg\uac00 null\uc774 \uc544\ub2cc\uac00..???!!!\\n\\n\ud574\ub2f9 \ud604\uc0c1\uc744 \uc7ac\ud604\ud55c \ub4a4\uc5d0 \ucd5c\ucd08 \uc2e4\ud589 / \uc774\ud6c4 \uc2e4\ud589\uc5d0 \ub300\ud55c \uc5d0\ub7ec\ub97c \ucd9c\ub825\ud574 \ubcf4\uc558\ub2e4.\\n\\n**\ucd5c\ucd08**\\n\\n```\\nclass java.lang.NullPointerException: Cannot invoke \\"Object.toString()\\" because of \\"java.util.Map.get(Object) is null\\n// \uad49\uc7a5\ud788 \uae34 StackTrace\\n```\\n\\n**\ub450\ubc88\uc9f8~**\\n\\n```java\\nclass java.lang.NullPointerException: null\\n```\\n\\n\ub098\ub294 \ub0b4\uac00 \ub85c\uc9c1 \uc5b4\ub518\uac00\uc5d0 NPE\uac00 \ubc1c\uc0dd\ud558\ub294 \uc6d0\uc778\uc744 \ub193\uce5c\uc904 \uc54c\uace0 \uba70\uce60\uc744 \uc0bd\uc9c8\ud588\ub294\ub370, \uc2a4\ud0dd\uc624\ubc84\ud50c\ub85c\uc6b0\uc5d0\uc11c \uc774 \uae00\uc744 \ubc1c\uacac\ud588\ub2e4.\\n\\n> https://stackoverflow.com/questions/2411487/nullpointerexception-in-java-with-no-stacktrace\\n\\n\uad00\ub828\ud574\uc11c \uac04\ub7b5\ud558\uac8c\xa0\uc870\uc0ac\ud574 \ubcf4\uc558\ub2e4.\\n\\n## JVM Stacktrace \uc0dd\ub7b5\\n\\n[JDK5 \ub9b4\ub9ac\uc988 \ub178\ud2b8](https://www.oracle.com/java/technologies/javase/release-notes-introduction.html)\ub97c \ubcf4\uba74 Exception\uc774 \ubc18\ubcf5\uc801\uc73c\ub85c \ubc1c\uc0dd\ud560 \ub54c, \ucef4\ud30c\uc77c\ub7ec\ub294 StackTrace\ub97c \ucd9c\ub825\ud558\ub294\uac83\uc744 \uba48\ucd98\ub2e4\uace0 \ud55c\ub2e4.\\n\\n\uc774\uac83\uc740 JVM\uc758 JIT\ucef4\ud30c\uc77c\ub7ec\uc758 C2\ucef4\ud30c\uc77c\ub7ec\uc758 \uae30\ubcf8 \ucd5c\uc801\ud654 \uc635\uc158 \uc774\ub77c\uace0 \ud55c\ub2e4.\\n\\n[OpenJDK\uc758 \uc18c\uc2a4 \ucf54\ub4dc](https://github.com/openjdk/jdk/blob/master/src/hotspot/share/opto/graphKit.cpp#L528)\ub97c \ubcf4\uba74 \ub2e4\uc74c \ub2e4\uc12f\uac00\uc9c0 Exception\uc5d0 \ub300\ud574 \ucd5c\uc801\ud654 \uc635\uc158\uc774 \uc801\uc6a9\ub41c\ub2e4.\\n\\n:::info\\n\\n**Stacktrace \uc0dd\ub7b5 \ucd5c\uc801\ud654 \uc635\uc158 \uc801\uc6a9\ub418\ub294 Exception**\\n\\n- NullPointerException\\n- ArithmeticException\\n- ArrayIndexOutOfBoundsException\\n- ClassCastException\\n- ArrayStoreException\\n\\n:::\\n\\n![](../static/img/post-img/20240205210954.png)\\n\\n\ucd5c\uc801\ud654 \uc635\uc158\uc744 \ube44\ud65c\uc131\ud654 \ud558\ub824\uba74 \uc544\ub798 JVM \uc635\uc158\uc744\xa0\ucd94\uac00\ud574 \uc8fc\uba74 \ub41c\ub2e4.\\n\\n```\\n-XX:-OmitStackTraceInFastThrow\\n```\\n\\n\ud558\uc9c0\ub9cc \uc774\ub294 \uc131\ub2a5 \ucd5c\uc801\ud654\ub97c \uc704\ud55c \uc635\uc158\uc73c\ub85c, \ud574\ub2f9 Exception\uc774 \ubc18\ubcf5\uc801\uc73c\ub85c \uc77c\uc5b4\ub098\uc9c0 \uc54a\uac8c\ub054 \ucc98\ub9ac \ud558\ub294\uac83\uc774 \uc62c\ubc14\ub978 \ubc29\ud5a5 \uc77c \uac83\uc774\ub2e4.\\n\\nException \ucc98\ub9ac\uc5d0 \ub300\ud55c \ucd5c\uc801\ud654\ub97c \ud558\ub294 \uc774\uc720\uac00 \uad81\uae08\ud574\uc11c \ucc3e\uc544\ubcf4\uc558\ub2e4.\\n\\nJava\uc5d0\uc11c Exception\uc744 \ub358\uc9c0\ub294 \ube44\uc6a9\uc740 \uaf64 \ube44\uc2f8\ub2e4\uace0 \ud55c\ub2e4. Exception\uc774 \ubc1c\uc0dd\ud558\uba74 \ud574\ub2f9 \uc608\uc678\uac00 \uc5b4\ub514\uc11c \ubc1c\uc0dd\ud588\ub294\uc9c0 \ucd94\uc801\ud558\uba70 \uc815\ubcf4\ub97c \uc218\uc9d1\ud558\uace0 Stack Trace\ub97c \uc0dd\uc131\ud55c\ub2e4. \uc774\ub97c \uacc4\uc0b0\ud558\ub294\ub370\uc5d0\ub3c4 \uc2dc\uac04\uc774 \uaf64 \uac78\ub9b0.\\n\\n\ub610, \uc608\uc678 \uac1d\uccb4\ub97c \uc0c8\ub85c \uc0dd\uc131\ud558\uace0 \ucd08\uae30\ud654 \ud558\ub294 \uacfc\uc815\uc774 \ubc18\ubcf5\ub418\uae30 \ub54c\ubb38\uc5d0 \uc88b\uc9c0 \uc54a\ub2e4. \uadf8\ub9ac\uace0 Exception\uc774 \ubc1c\uc0dd\ud558\uba74 \uadf8\uac83\uc774 \uc5b4\ub5a4 \uc608\uc678\uc778\uc9c0 \uc801\uc808\ud55c \uc608\uc678 \ud578\ub4e4\ub7ec\ub97c \ucc3e\uc544 \ub9ac\ud134\ud574\uc918\uc57c \ud558\ub294\ub370, \uc608\uc678 \ud578\ub4e4\ub7ec\ub97c \ucc3e\ub294 \uacfc\uc815 \ub610\ud55c \uc624\ub798 \uac78\ub9b0\ub2e4.\\n\\n\ud504\ub85c\uc81d\ud2b8\ub97c \ud560 \ub54c\ub3c4, \uc801\uc808\ud55c \uc608\uc678 \uc0c1\ud669\uc5d0 Custom Exception\uc744 \ub358\uc9c0\uace4 \ud588\ub294\ub370, \uc774\uac8c \ub9de\ub294 \ubc29\ubc95\uc778\uc9c0 \uc0dd\uac01\ud574\ubcfc \ud544\uc694\uac00 \uc788\ub294 \ub4ef \ud558\ub2e4.\\n\\n\uadf8\ub9ac\uace0 \uc2a4\ud0dd \ucd94\uc801\uc774 \ud544\uc694\ud558\uc9c0 \uc54a\uc740 \uacbd\uc6b0\ub77c\uba74 override\ub97c \ud1b5\ud574 trace\ub97c \ub2f4\uc9c0 \uc54a\ub3c4\ub85d \ud558\ub294 \ubc29\ubc95\ub3c4 \uc88b\uaca0\ub2e4."}]}')}}]);